pragma solidity ^0.8.0;

library FR_Library {
    struct Properties_Struct {
        string propertyName;
        uint256 listingDate;
        uint256 totalShares;
        uint256 soldShares;
        uint256 limitPerShare;
        string debitCurrency;
        string ipfsUri;
        uint256 tokenId;
        uint256 lastUpdated;
    }
    struct Property_Price_Struct {
        // uint256 propertyPrice;
        uint256 pricePerShare;
        // uint256 propertyDiscount;
        uint256 lastUpdated;
    }
    struct Property_Status_Struct {
        string saleStatus;
        string SPVStatus;
        string NFTStatus;
        string resaleStatus;
        uint256 lastUpdated;
    }

    struct User_Struct {
        address wallet;
        uint256[] ownedPropertyIDs;
        uint256[] orderIds;
        uint256[] activeOrderIDs;
        bool isDisabled;
    }
    struct User_Holdings_Struct {
        uint256 numberOfShares;
        uint256 numberOfMintedShares;
    }

    struct Orders_Struct {
        string userId;
        uint256 propertyId;
        uint256 orderDate;
        string orderType;
        string saleType;
        string orderStatus;
        InvoiceAttributeItemStruct[] invoiceAttribute;
        uint256 lastUpdated;
    }

    struct Order_Share_Details_Struct {
        uint256 price;
        uint256 totalShares;
        uint256 executedShares;
        uint256[] matchIds;
    }

    struct Match_Order_Info_Struct {
        uint256 quantity;
        uint256 orderId;
        uint256 lastUpdated;
        uint256 prevNodeId;
        uint256 currentNodeId;
        uint256 nextNodeId;
    }

    struct Match_Info_Struct {
        uint256 price;
        uint256 totalQuantity;
        uint256 totalOrders;
        Match_Order_Info_Struct[] matchOrderInfo;
        uint256 prevNodeId;
        uint256 currentNodeId;
        uint256 nextNodeId;
    }

    struct Matched_Transfers_Struct {
        uint256 quantity;
        uint256 price;
        uint256 orderID;
        uint256 buyerID;
        uint256 sellerID;
        uint256 transferDate;
    }

    struct Order_Process_Details_Struct {
        bool isWhiteListed;
        User_Holdings_Struct userHolidings;
        uint256 limitPerShare;
        uint256 avaiablePropertyShares;
        uint256 pricePerShare;
        User_Struct userStruct;
        Property_Status_Struct propertyStatus;
        address propertyToken;
        uint256 propertyListingDate;
    }

    struct Mint_Details_Struct {
        string userId;
        bool userStatus;
        bool isWhiteListed;
        uint256 tokenId;
        User_Holdings_Struct userHoldings;
    }

    struct TopUpFiatToCryptoPaymentInfo {
        User_Struct userInfo;
        address treasury;
        bool isWhiteListed;
        // uint256 exchangeRate;
        address creditToken;
    }

    struct CryptoToCryptoPaymentInfo {
        User_Struct userInfo;
        address treasury;
        bool isWhiteListed;
        string fromCurrency;
        string toCurrency;
        // address liquidityAddress;
    }

    struct WithdrawFiatToCryptoPaymentInfo {
        string userId;
        User_Struct userInfo;
        address treasury;
        bool isWhiteListed;
        // uint256 exchangeRate;
        address debitToken;
    }

    struct ValidateBuyPrimaryOrderStruct {
        string userId;
        uint256 propertyId;
        uint256 orderPricePerShare;
        uint256 orderNumberOfShares;
        bool isPartCrypto;
        uint256 isPartCryptoAmount;
    }

    struct ValidateBuyPrimaryOrderCryptoStruct {
        string userId;
        uint256 propertyId;
        uint256 orderPricePerShare;
        uint256 orderNumberOfShares;
        bool isPartCrypto;
        uint256 isPartCryptoAmount;
    }

    struct ValidateSecondaryOrderCryptoStruct {
        string userId;
        address userAddress;
        uint256 propertyId;
        string orderType;
        uint256 pricePerShare;
        uint256 numberOfShares;
        bool isPartCrypto;
        uint256 isPartCryptoAmount;
    }


    struct InvoiceAttributeItemStruct {
        string invoiceName;
        uint256 invoicePrice;
        bool isPercentage;
        address wallet;
        string bankAccount;
    }

    struct InvoiceAttributesStruct {
        InvoiceAttributeItemStruct[] invoiceAttributeItems;
        uint256 lastUpdated;
    }

    struct addPropertyInvoiceStruct {
        string saleType;
        string tradeType;
        InvoiceAttributeItemStruct[] invoiceAttributeItems;
    }

    struct updatePropertyInvoiceStruct {
        bool isInvoiceChanged;
        string saleType;
        string tradeType;
        InvoiceAttributeItemStruct[] invoiceAttributeItems;
    }

    struct UpdateOrderDetailsStruct{
        uint256 orderId;
        uint256 quantity;
    }

    //**************************Handy Function************//
    function compareStrings(
        string memory a,
        string memory b
    ) public pure returns (bool) {
        return (keccak256(abi.encodePacked(a)) ==
            keccak256(abi.encodePacked(b)));
    }

    function isPropertyEmpty(
        Properties_Struct memory _property
    ) internal pure returns (bool) {
        Properties_Struct memory emptyStruct = Properties_Struct(
            "",
            0,
            0,
            0,
            0,
            "",
            "",
            0,
            0
        );
        if (
            emptyStruct.listingDate == _property.listingDate &&
            compareStrings(emptyStruct.ipfsUri, _property.ipfsUri) &&
            compareStrings(
                emptyStruct.debitCurrency,
                _property.debitCurrency
            ) &&
            compareStrings(emptyStruct.propertyName, _property.propertyName) &&
            emptyStruct.totalShares == _property.totalShares &&
            emptyStruct.soldShares == _property.soldShares &&
            emptyStruct.limitPerShare == _property.limitPerShare &&
            emptyStruct.lastUpdated == _property.lastUpdated
        ) {
            return true;
        }
        return false;
    }

    function min(uint256 a, uint256 b) public pure returns (uint256) {
        return a < b ? a : b;
    }

    function max(uint256 a, uint256 b) public pure returns (uint256) {
        return a > b ? a : b;
    }

    //********************ARRAY UTILS********************//

    function contains(
        uint256[] memory arr,
        uint256 element
    ) internal pure returns (bool) {
        for (uint256 i = 0; i < arr.length; i++) {
            if (arr[i] == element) {
                return true;
            }
        }
        return false;
    }

    function put(
        uint256[] memory array,
        uint256 element
    ) internal pure returns (uint256[] memory) {
        uint256 newLength = array.length + 1;
        uint256[] memory newArray = new uint256[](newLength);
        for (uint i = 0; i < array.length; i++) {
            newArray[i] = array[i];
        }
        newArray[newLength - 1] = element;
        return newArray;
    }
}
