pragma solidity ^0.8.0;

import "./FR_Library.sol";
import "./FR_IDataContract.sol";
import "./FR_IDataContractV2.sol";
import "./FR_IIDGenerator.sol";
import "./SafeMath.sol";

contract FR_Order_Contract {
    using SafeMath for uint256;

    FR_IDataContract iDataContractFR;
    FR_IDataContractV2 public iDataContractV2FR;
    FR_IIDGeneretor iIDGeneretorFR;

    event BuyPrimaryShares(
        uint256 indexed _event_id,
        uint256 indexed _call_ref_id,
        uint256 indexed _order_id
    );

    event BuyPrimarySharesCrypto(
        uint256 indexed _event_id,
        uint256 indexed _order_id
    );

    constructor(
        address _dataContractFR,
        address _dataContractV2FR,
        address _idGenertorContract
    ) {
        iDataContractFR = FR_IDataContract(_dataContractFR);
        iDataContractV2FR = FR_IDataContractV2(_dataContractV2FR);
        iIDGeneretorFR = FR_IIDGeneretor(_idGenertorContract);
    }

    function setIDataContractFR(address _dataContractFR) external {
        iDataContractFR.checkRole(
            keccak256(abi.encodePacked("ADMIN_ROLE")),
            msg.sender
        );
        iDataContractFR = FR_IDataContract(_dataContractFR);
    }

    function setIDataContractV2FR(address _dataContract_v2) public {
        iDataContractFR.checkRole(
            keccak256(abi.encodePacked("ADMIN_ROLE")),
            msg.sender
        );
        iDataContractV2FR = FR_IDataContractV2(_dataContract_v2);
    }

    function setIIDGeneretorFR(address _idGenertorContract) public {
        iDataContractFR.checkRole(
            keccak256(abi.encodePacked("ADMIN_ROLE")),
            msg.sender
        );
        iIDGeneretorFR = FR_IIDGeneretor(_idGenertorContract);
    }

    function buyPrimarySharesCrypto(
        uint256 _property_id,
        uint256 _order_number_of_shares,
        uint256 _amount
    ) external {
        string memory _user_id = iDataContractFR.usersIdByWallet(msg.sender);
        require(
            keccak256(abi.encodePacked(_user_id)) !=
                keccak256(abi.encodePacked("")),
            "INVALID USER - USER ID IS NOT EXISTS"
        );

        require(_order_number_of_shares != 0, "INVALID SHARES");

        (
            FR_Library.Order_Process_Details_Struct
                memory buyPrimaryProcessDetails,
            FR_Library.InvoiceAttributeItemStruct[]
                memory invoiceAttributeItems,
            uint256 tax_amount
        ) = validateBuyPrimarySharesCrypto(
                FR_Library.ValidateBuyPrimaryOrderCryptoStruct(
                    _user_id,
                    _property_id,
                    (_amount * 10 ** 5).div(_order_number_of_shares), // for handling share decimel in number
                    _order_number_of_shares,
                    true, //_is_part_crypto
                    _amount
                )
            );

        uint256 primaryOrderId = iIDGeneretorFR.generateNewId("PRIMARY_ORDER_ID");
        if (
            !(
                FR_Library.contains(
                    buyPrimaryProcessDetails.userStruct.ownedPropertyIDs,
                    _property_id
                )
            )
        ) {
            buyPrimaryProcessDetails.userStruct.ownedPropertyIDs = FR_Library
                .put(
                    buyPrimaryProcessDetails.userStruct.ownedPropertyIDs,
                    _property_id
                );
        }

        buyPrimaryProcessDetails.userStruct.orderIds = FR_Library.put(
            buyPrimaryProcessDetails.userStruct.orderIds,
            primaryOrderId
        );

        iDataContractFR.setPrimaryOrders(
            primaryOrderId,
            FR_Library.Orders_Struct(
                _user_id,
                _property_id,
                block.timestamp,
                "MARKET",
                "BUY",
                "FULLFILLED",
                invoiceAttributeItems,
                block.timestamp
            ),
            FR_Library.Order_Share_Details_Struct(
                (_amount * 10 ** 5).div(_order_number_of_shares), // for handling share decimel in number
                _order_number_of_shares,
                _order_number_of_shares,
                new uint256[](0)
            ),
            buyPrimaryProcessDetails.userStruct
        );

        IERC20 _debit_token = IERC20(buyPrimaryProcessDetails.propertyToken);
        iDataContractFR.debitTransfer(
            _amount.add(tax_amount),
            buyPrimaryProcessDetails.userStruct.wallet,
            _debit_token
        );

        emit BuyPrimarySharesCrypto(
            iIDGeneretorFR.generateNewId("EVENT_ID"),
            primaryOrderId
        );
    }

    function buyPrimaryShares(
        uint256 _call_ref_id,
        string memory _user_id,
        uint256 _property_id,
        uint256 _order_price_per_share,
        uint256 _order_number_of_shares,
        uint256 _order_date,
        string[] memory _order_details,
        bool _is_part_crypto,
        uint256 isPartCryptoAmount
    ) external {
        iDataContractFR.checkRole(
            keccak256(abi.encodePacked("ADMIN_ROLE")),
            msg.sender
        );

        FR_Library.Order_Process_Details_Struct
            memory buyPrimaryProcessDetails = validateBuyPrimarySharesInternal(
                FR_Library.ValidateBuyPrimaryOrderStruct(
                    _user_id,
                    _property_id,
                    _order_price_per_share,
                    _order_number_of_shares,
                    _is_part_crypto,
                    isPartCryptoAmount
                )
            );

        uint256 primaryOrderId = iIDGeneretorFR.generateNewId("PRIMARY_ORDER_ID");
        if (
            !(
                FR_Library.contains(
                    buyPrimaryProcessDetails.userStruct.ownedPropertyIDs,
                    _property_id
                )
            )
        ) {
            buyPrimaryProcessDetails.userStruct.ownedPropertyIDs = FR_Library
                .put(
                    buyPrimaryProcessDetails.userStruct.ownedPropertyIDs,
                    _property_id
                );
        }

        buyPrimaryProcessDetails.userStruct.orderIds = FR_Library.put(
            buyPrimaryProcessDetails.userStruct.orderIds,
            primaryOrderId
        );

        iDataContractFR.setPrimaryOrders(
            primaryOrderId,
            FR_Library.Orders_Struct(
                _user_id,
                _property_id,
                _order_date,
                _order_details[0],
                "BUY",
                _order_details[1],
                iDataContractV2FR
                    .getLatestInvoiceAttributeItems(
                        _property_id,
                        "PRIMARY",
                        "BUY"
                    )
                    .invoiceAttributeItems,
                block.timestamp
            ),
            FR_Library.Order_Share_Details_Struct(
                _order_price_per_share,
                _order_number_of_shares,
                _order_number_of_shares,
                new uint256[](0)
            ),
            buyPrimaryProcessDetails.userStruct
        );

        if (_is_part_crypto) {
            IERC20 _debit_token = IERC20(
                buyPrimaryProcessDetails.propertyToken
            );
            iDataContractFR.debitTransfer(
                isPartCryptoAmount,
                buyPrimaryProcessDetails.userStruct.wallet,
                _debit_token
            );
        }

        emit BuyPrimaryShares(
            iIDGeneretorFR.generateNewId("EVENT_ID"),
            _call_ref_id,
            primaryOrderId
        );
    }

    function validateBuyPrimarySharesCrypto(
        FR_Library.ValidateBuyPrimaryOrderCryptoStruct memory _validateParams
    )
        public
        view
        returns (
            FR_Library.Order_Process_Details_Struct memory,
            FR_Library.InvoiceAttributeItemStruct[] memory,
            uint256
        )
    {
        FR_Library.Order_Process_Details_Struct
            memory buyPrimaryProcessDetails = iDataContractFR
                .getOrderProcessDetails(
                    _validateParams.userId,
                    _validateParams.propertyId
                );
        require(
            buyPrimaryProcessDetails.propertyListingDate <= block.timestamp,
            "INVALID PROPERTY - COMING SOON"
        );
        require(
            buyPrimaryProcessDetails.userStruct.isDisabled == false,
            "INVALID USER - ACCESS DISABLED"
        );
        require(
            buyPrimaryProcessDetails.isWhiteListed == true,
            "INVALID USER - NOT WHILELISTED"
        );

        require(
            buyPrimaryProcessDetails.userHolidings.numberOfShares +
                _validateParams.orderNumberOfShares <=
                buyPrimaryProcessDetails.limitPerShare,
            "SHARES LIMIT EXCEEDED"
        );

        if (buyPrimaryProcessDetails.pricePerShare <= (1 * 10 ** 18)) {
            require(
                (buyPrimaryProcessDetails.pricePerShare ==
                    _validateParams.orderPricePerShare) ||
                    (buyPrimaryProcessDetails.pricePerShare.add(1 * 10 ** 18) >=
                        _validateParams.orderPricePerShare),
                "INVALID PRICE - PRICING MISMATCH"
            );
        } else {
            require(
                (buyPrimaryProcessDetails.pricePerShare ==
                    _validateParams.orderPricePerShare) ||
                    ((buyPrimaryProcessDetails.pricePerShare.add(
                        1 * 10 ** 18
                    ) >= _validateParams.orderPricePerShare) &&
                        (buyPrimaryProcessDetails.pricePerShare.sub(
                            1 * 10 ** 18
                        ) <= _validateParams.orderPricePerShare)),
                "INVALID PRICE - PRICING MISMATCH"
            );
        }

        require(
            (keccak256(
                abi.encodePacked(
                    buyPrimaryProcessDetails.propertyStatus.saleStatus
                )
            ) != keccak256(abi.encodePacked("CREATED"))) &&
                (keccak256(
                    abi.encodePacked(
                        buyPrimaryProcessDetails.propertyStatus.saleStatus
                    )
                ) != keccak256(abi.encodePacked("PAUSED"))) &&
                (keccak256(
                    abi.encodePacked(
                        buyPrimaryProcessDetails.propertyStatus.saleStatus
                    )
                ) != keccak256(abi.encodePacked("STOPPED"))) &&
                (keccak256(
                    abi.encodePacked(
                        buyPrimaryProcessDetails.propertyStatus.saleStatus
                    )
                ) != keccak256(abi.encodePacked("COMPLETED"))),
            "INVALID PROPERTY STATUS"
        );

        require(_validateParams.isPartCryptoAmount != 0, "INVALID AMOUNT");
        require(
            buyPrimaryProcessDetails.propertyToken != address(0),
            "INVALID TOKEN - PROPERTY TOKEN IS NOT EXISTS"
        );
        require(
            buyPrimaryProcessDetails.userStruct.wallet != address(0),
            "INVALID ADDRESS - USER ADDRESS NOT EXISTS"
        );

        FR_Library.InvoiceAttributeItemStruct[]
            memory invoiceTaxItems = iDataContractV2FR
                .getLatestInvoiceAttributeItems(
                    _validateParams.propertyId,
                    "PRIMARY",
                    "BUY"
                )
                .invoiceAttributeItems;

        uint256 taxAmount = 0;

        for (
            uint256 invoiceIndex = 0;
            invoiceIndex < invoiceTaxItems.length;
            invoiceIndex++
        ) {
            if (invoiceTaxItems[invoiceIndex].isPercentage) {
                taxAmount = taxAmount.add(
                    (
                        (
                            _validateParams.isPartCryptoAmount.mul(
                                invoiceTaxItems[invoiceIndex].invoicePrice
                            )
                        ).div(100)
                    ).div(10 ** 18)
                );
            } else {
                taxAmount = taxAmount.add(
                    invoiceTaxItems[invoiceIndex].invoicePrice
                );
            }
        }

        require(
            IERC20(buyPrimaryProcessDetails.propertyToken).balanceOf(
                buyPrimaryProcessDetails.userStruct.wallet
