    "contracts/MerlinFund.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import "./TokenAllocationController.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import '@uniswap/v3-periphery/contracts/interfaces/ISwapRouter.sol';

contract MerlinFund is TokenAllocationController, ERC20 {
    using SafeERC20 for IERC20;
    using SafeMath for uint256;

    uint256 public basketRequestsSum; //contains the sum of outstanding requests to understand how much will need to be swapped
    bool public swapIn;
    uint256 public swapIntervalEnd;

    mapping(address => uint256) public pendingWithdrawRequests;

    event Deposit(address sender, uint256 amountIn, uint256 amountOut);
    event Withdraw(address sender, uint256 amountIn, uint256 amountOut);
    event WithdrawRequest(address sender, uint256 amount);
    event CloseBasket(bool forSwapIn, uint256 amount,  uint256 blocktimestamp);
    event Swap(address tokenIn, address tokenOut, uint256 amountIn, uint256 swapCounter, uint256 blocktimestamp);

    constructor(
        address _adminAddress,
        address _swapManager,
        address _usdAddress,
        string memory _name, 
        string memory _symbol) ERC20(_name, _symbol){
		require(_adminAddress != address(0), "Cannot be zero address");
        uint256 decimal = ERC20(_usdAddress).decimals();
        require(decimal == 6, "Decimal token not suitable");
		adminAddress = _adminAddress;
        swapManager = _swapManager;
        usdAddress = _usdAddress;
        swapCounter = 1;
	}

    function deposit(uint256 amount) public fullPercentage nonReentrant {
        require(amount > 0, "amount must be greater than zero");
        
        uint256 tokenPrice = 1e6;
        uint256 _totalSupply = totalSupply();

        if(_totalSupply != 0 ){
            tokenPrice = getAveragePrice();
        }

        //Since the balanceOf function is used to calculate the averagePrice, in order not to influence this calculation, the function is placed after
        IERC20(usdAddress).safeTransferFrom(msg.sender, address(this), amount);

        //fee calculate
        uint256 fee = amount.mul(fee).div(10000);
        amount -= fee;

        basketOpen += amount;
        feeBasket += fee;

        uint256 issuedAmount = amount.mul(1e6).div(tokenPrice);

        _mint(msg.sender, issuedAmount);

        emit Deposit(msg.sender, amount, issuedAmount);
    }

    function withdrawRequest(uint256 amount) public fullPercentage {
        uint256 balance = balanceOf(msg.sender);

        require(balance >= amount, "balance insufficient");

        uint256 pendingRequestAmount = pendingWithdrawRequests[msg.sender];

        pendingWithdrawRequests[msg.sender] = amount;

        basketRequestsSum = basketRequestsSum - pendingRequestAmount + amount;

        emit WithdrawRequest(msg.sender, amount);
    }

    function claim() public fullPercentage nonReentrant {
        uint256 pendingRequestAmount = pendingWithdrawRequests[msg.sender];

        require(pendingRequestAmount > 0, "no prior withdrawal request made");

        uint256 tokenPrice = getAveragePrice();
        uint256 amountOut = (pendingRequestAmount * tokenPrice).div(1e6);

        //fee calculate
        uint256 fee = amountOut.mul(fee).div(10000);
        amountOut -= fee;

        feeBasket += fee;

        require(basketOpen >= amountOut, "insufficient liquidity in the fund");

        _burn(msg.sender, pendingRequestAmount);

        pendingWithdrawRequests[msg.sender] = 0;

        basketOpen -= amountOut;
        basketRequestsSum -= pendingRequestAmount;

        IERC20(usdAddress).approve(address(this), amountOut);
        IERC20(usdAddress).safeTransferFrom(address(this), msg.sender, amountOut);

        emit Withdraw(msg.sender, pendingRequestAmount, amountOut);
    }

    function getAveragePrice() public fullPercentage view returns (uint256 average)
    {
        uint256 weightedSum = 0;

        for (uint8 i = 1; i <= tokenInFundCount; i++) {

            //take token
            address tokenAddress = tokenInFund[i];
            
            ERC20 token = ERC20(tokenAddress);

            uint256 balance = token.balanceOf(address(this));
            
            uint256 tokenDecimals;
            try token.decimals() returns (uint8 decimals) {
			tokenDecimals = decimals;
            } catch {
                tokenDecimals = 18;
            }

            uint256 priceDecimals;
            //Take price
            
            address aggregatorAddress = aggregators[tokenAddress];
            AggregatorV3Interface priceFeed = AggregatorV3Interface(aggregatorAddress);

            (, int256 price, , , ) = priceFeed.latestRoundData();

            try priceFeed.decimals() returns (uint8 decimals) {
			priceDecimals = decimals;
            } catch {
                priceDecimals = 8;
            }
            
            tokenDecimals += priceDecimals;

            //for Weighted average
            uint256 uPrice = uint256(price);
            uint256 dividend = balance.mul(uint256(uPrice.mul(1e6)));
            weightedSum += dividend.div(10**tokenDecimals);
        }

        uint256 supply = totalSupply();

        uint256 ris = 1*1e6;

        if(supply == 0)
        {
            return ris;
        }
        else
        {
            //To avoid forgetting quantities, we directly check the balance of the smart contract, minus the fees
            ERC20 token = ERC20(usdAddress);
            uint256 usdBalance = token.balanceOf(address(this));

            weightedSum = weightedSum + usdBalance - feeBasket;

            weightedSum = weightedSum.mul(1e6);
            weightedSum = weightedSum.mul(1e18);
                    
            ris = weightedSum.div(supply);

            ris = ris.div(1e18);

            if(ris == 0)
            {
                return 1;
            }
            else
            {
                return ris;
            }
        }
    }

    function closeBasket() external fullPercentage noSwapProcessInProgress {
        require(block.timestamp > swapIntervalEnd, "Not enough time has passed since the end of the last fund rebalancing");

        if(basketRequestsSum > basketOpen){
            swapIn = false;

            uint256 tokenPrice = getAveragePrice();

            uint256 basketRequestsSuminUsd = (basketRequestsSum * tokenPrice).div(10**6);

            totalAmountForSwap = basketRequestsSuminUsd - basketOpen; //Only the amount needed
            require(totalAmountForSwap >= 100, "amount of request too low");

            emit CloseBasket(false, totalAmountForSwap, block.timestamp);
        }
        else
        {
            require(basketOpen >= 100, "amount of request too low");

            swapIn = true;
            totalAmountForSwap = basketOpen;
            amountForSwapIn = basketOpen;

            basketOpen = 0;

            emit CloseBasket(true, totalAmountForSwap, block.timestamp);
        }
    }

    function swap() public fullPercentage onlySwapManager nonReentrant {
        require(totalAmountForSwap != 0, "empty basket");

        address tokenAddress = tokenInFund[swapCounter];
        uint256 percentage = percentages[tokenAddress];
        address swapRouter = routers[tokenAddress];

        require(swapRouter != address(0), "SwapRouter has not been set");

        address tokenIn = usdAddress;
        address tokenOut = tokenAddress;
        uint256 amountIn = 0;

        if(swapIn == false){
            tokenIn = tokenAddress;
            tokenOut = usdAddress;

            uint256 amountInUsd = totalAmountForSwap.mul(percentage).div(10000);
            uint256 tokenDecimals = ERC20(tokenIn).decimals();

            //take price
            address aggregatorAddress = aggregators[tokenIn];
            AggregatorV3Interface priceFeed = AggregatorV3Interface(aggregatorAddress);

            (, int256 price, , , ) = priceFeed.latestRoundData();

            //decimals for conversion
            uint256 priceDecimals;
            try priceFeed.decimals() returns (uint8 decimals) {
			priceDecimals = decimals;
            } catch {
                priceDecimals = 8;
            }

            uint256 uPrice = uint256(price);
            amountIn = (10**tokenDecimals * amountInUsd)/((uPrice*1e6)/(10**priceDecimals));

            uint256 tokenBalance = ERC20(tokenIn).balanceOf(address(this));

            if(amountIn > tokenBalance){
                amountIn = tokenBalance;
            }
        }
        else{
            amountIn = totalAmountForSwap.mul(percentage).div(10000);

            uint256 tokenInBalance = IERC20(tokenIn).balanceOf(address(this));

            if(amountIn > tokenInBalance)
            {
                amountIn = tokenInBalance;
            }
        }

        address poolAddress = pools[tokenAddress];
        uint24 fee = IUniswapV3Pool(poolAddress).fee();

        IERC20(tokenIn).approve(swapRouter, amountIn);

        ISwapRouter uniswapV3Router = ISwapRouter(swapRouter);

        ISwapRouter.ExactInputSingleParams memory params = ISwapRouter.ExactInputSingleParams({
            tokenIn: tokenIn,
            tokenOut: tokenOut,
            fee: fee,
            recipient: address(this),
            deadline: block.timestamp + 300,
            amountIn: amountIn,
            amountOutMinimum: 0,
            sqrtPriceLimitX96: 0
        });

        // Perform the swap
        uint256 amountOut = uniswapV3Router.exactInputSingle(params);

        if(swapIn == false)
        {
            basketOpen += amountOut;
        }
        else
        {
            amountForSwapIn -= amountIn;
        }

        swapCounter = swapCounter + 1;

        if(swapCounter > tokenInFundCount){
            swapCounter = 1;
            //.div() function always rounds down, so at the end of the swap cycle, there may be some remaining usd. These will be available for withdrawals.
            basketOpen += amountForSwapIn;
            totalAmountForSwap = 0;
            amountForSwapIn = 0;
            swapIntervalEnd = block.timestamp + swapIntervalDuration;
        }

        emit Swap(tokenIn, tokenOut, amountIn, swapCounter - 1, block.timestamp);
    }
}"
    },
    "contracts/MerlinFundAdministrative.sol": {
      "content": "// SPDX-License-Identifier: MIT

pragma solidity ^0.8.9;

import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "./MerlinFundModifier.sol";

abstract contract MerlinFundAdministrative is Ownable, MerlinFundModifier, ReentrancyGuard {
    using SafeERC20 for IERC20;
    
    address public usdAddress;
    uint256 public swapIntervalDuration;
    uint256 public fee;
    uint256 public feeBasket;
    uint256 public basketOpen; //contains usd received from deposits and swaps, also available for withdrawals

    mapping (address => bool) public whitelistOwner;

    event NewAdminAddress(address admin);
    event NewSwapManager(address swapManager);
    event NewusdAddress(address usdAddress);
    event NewSwapIntervalDuration(uint256 swapIntervalDuration);
    event AddPotentialOwner(address potentialOwner, bool status);
    event FeeUpdated(uint256 fee);
    event FeeManagerUpdated(address feeManager);
    event NewClaimFee(address to, uint256 amountFee, uint256 blockTimeSpan);

	function setAdmin(address _adminAddress) public onlyOwner {
		require(_adminAddress != address(0), "Cannot be zero address");
		adminAddress = _adminAddress;

		emit NewAdminAddress(_adminAddress);
	}

    function setSwapManager(address _swapManager) public onlyAdmin {
		require(_swapManager != address(0), "Cannot be zero address");
		swapManager = _swapManager;

		emit NewSwapManager(swapManager);
	}

    function setusdAddress(address _usdAddress) public onlyAdmin {
        require(_usdAddress != address(0), "Cannot be zero address");
        require(_usdAddress != usdAddress, "Address already set");

        uint256 decimal = ERC20(_usdAddress).decimals();
        require(decimal == 6, "Decimal token not suitable");
        
		usdAddress = _usdAddress;

		emit NewusdAddress(_usdAddress);
    }

    function setSwapIntervalDuration(uint256 _swapIntervalDuration) public onlyAdmin {
        swapIntervalDuration = _swapIntervalDuration;

        emit NewSwapIntervalDuration(_swapIntervalDuration);
    }

    function manageWhiteListOwner(address _potentialOwner, bool status) public onlyOwner {
        require(_potentialOwner != address(0), "Cannot be zero address");

        whitelistOwner[_potentialOwner] = status;

        emit AddPotentialOwner(_potentialOwner, status);
    }

    function setFee(uint256 _fee) public onlyAdmin {
        fee = _fee;
        emit FeeUpdated(_fee);
    }

    function setFeeManager(address _feeManager) public onlyAdmin {
        require(_feeManager != address(0), "Address invalid");

        feeManager = _feeManager;
        emit FeeManagerUpdated(_feeManager);
    }

    function claimFee() public onlyFeeManager nonReentrant {
        require(feeManager != address(0), "feeManager not set");
        require(feeBasket != 0, "no fee in basket");
        
        IERC20(usdAddress).approve(address(this), feeBasket);
        IERC20(usdAddress).safeTransferFrom(address(this), swapManager, feeBasket);

        emit NewClaimFee(swapManager, feeBasket, block.timestamp);
    }

    function transferOwnership(address newOwner) override public virtual onlyOwner {
        require(newOwner != address(0), "Ownable: new owner is the zero address");

        address _owner = owner();

        require(!whitelistOwner[_owner], "It is no longer possible to transfer ownership");

        _transferOwnership(newOwner);
    }
}
"
    },
    "contracts/MerlinFundModifier.sol": {
      "content": "// SPDX-License-Identifier: MIT

pragma solidity ^0.8.9;

import "@openzeppelin/contracts/security/ReentrancyGuard.sol";

abstract contract MerlinFundModifier {

	address public adminAddress; // address of the admin
    address public swapManager;
    address public feeManager;
    uint256 public percentageFill; // sum of the percentages of each token within the fund
    uint256 public totalAmountForSwap;
    uint256 public amountForSwapIn;
	
	modifier onlyAdmin() {
		require(msg.sender == adminAddress, "Not admin");
		_;
	}

    modifier onlySwapManager() {
		require(msg.sender == swapManager, "Not swap manager");
		_;
	}

    modifier fullPercentage() {
        require(percentageFill == 10000, "Fund setting has not yet been completed");
        _;
    }

    modifier noSwapProcessInProgress() {
        require(totalAmountForSwap == 0, "swap process in progress");
        _;
    }

    modifier onlyFeeManager(){
        require(msg.sender == feeManager, "Not FeeManager");
        _;
    }
}"
    },
    "contracts/TokenAllocationController.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import '@uniswap/v3-core/contracts/interfaces/IUniswapV3Pool.sol';
import '@uniswap/v3-periphery/contracts/interfaces/ISwapRouter.sol';
import "@chainlink/contracts/src/v0.8/interfaces/AggregatorV3Interface.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "./MerlinFundAdministrative.sol";

abstract contract TokenAllocationController is MerlinFundAdministrative {
    using SafeMath for uint256;
    
    struct InfoTokenPercentage {
		address tokenAddress;
		uint256 percentage;
	}

    struct InfoTokenAdd {
		address tokenAddress;
        address aggregator;
        address pool;
        address router;
	}

    uint8 public tokenInFundCount;
    uint8 public swapCounter;
    //When the percentage associated with a token is decreased, the usd obtained from the swap must be set aside for when they will be modified 
    //in addition to the percentages of other tokens
    uint256 private basketChangePercentage;

    mapping(address => address) public aggregators; //token => aggregator
    mapping (address => address ) public routers;
    mapping(address => address) public pools; //token => pool x swap
    mapping(address => uint256) public percentages; //token => percentages
    mapping(uint8 => address) public tokenInFund; //tokens regularly registered and included in the fund, i.e., having a percentage > 0
    mapping(address => uint8) public tokenInFundPosition;

    // event NewTokenAdded(address token, address aggregator, address pool, uint256 percentage);
    event NewTokenAdded(address token, address aggregator, address pool);
    event NewAssociatedAggregator(address token, address aggregator);
    event NewAssociatedRouter(address token, address router);
    event NewAssociatedPool(address token, address pool);
    event FundTokenAdjustment(address token, bool inside);
    event NewPercentageChange(address tokenAddress, uint256 previusPrecentage, uint256 newPercentage);
    event NewDiscardToken(address tokenAddress, address managerAddress, uint256 tokenBalance, uint256 price, uint256 usdBalance);

    function addTokenInFund(InfoTokenAdd memory infoToken) external onlyAdmin{
        noAddressZero(infoToken.tokenAddress, infoToken.aggregator);
        require(aggregators[infoToken.tokenAddress] == address(0), "Token already added");

        aggregators[infoToken.tokenAddress] = infoToken.aggregator;
        routers[infoToken.tokenAddress] = infoToken.router;

        checkValidPool(infoToken.pool);
        pools[infoToken.tokenAddress] = infoToken.pool;

        emit NewTokenAdded(infoToken.tokenAddress, infoToken.aggregator, infoToken.pool);
    }

    //It can happen, even during swaps, that there are issues with the pool or dependent services, so there must be the possibility to change them

    function setAggregator(address token, address aggregator) external onlyAdmin {
        noAddressZero(token, aggregator);

        aggregators[token] = aggregator;

        emit NewAssociatedAggregator(token, aggregator);
    }

    function setRouter(address token, address router) external onlyAdmin {
        noAddressZero(token, router);

        routers[token] = router;

        emit NewAssociatedRouter(token, router);
    }

    function setPool(address token, address poolAddress) external onlyAdmin {
        require(token != address(0), "token address cannot be zero address");
        require(poolAddress != address(0), "router address cannot be zero address");

        pools[token] = poolAddress;

        emit NewAssociatedPool(token, poolAddress);
    }

    function noAddressZero(address token, address aggregator) internal pure {
        require(token != address(0), "token address cannot be zero address");
        require(aggregator != address(0), "aggregator address cannot be zero address");
    }

    function changePercentage(address tokenAddress, uint256 percentage) external onlyAdmin noSwapProcessInProgress nonReentrant {
        checkTokenInfo(tokenAddress, percentage);

        //New values added must not change the result of the sum of the percentages
        uint256 currentPercentage = percentages[tokenAddress];
        address swapRouter = routers[tokenAddress];

        if(currentPercentage > percentage)
        {
            uint256 currentBalance = ERC20(tokenAddress).balanceOf(address(this));
            uint256 newBalance = (currentBalance.div(currentPercentage)).mul(percentage);

            uint256 amountIn = currentBalance - newBalance;

            if(amountIn != 0)
            {
                IERC20(tokenAddress).approve(swapRouter, amountIn);

                address poolAddress = pools[tokenAddress];
                uint24 fee = IUniswapV3Pool(poolAddress).fee();

                ISwapRouter uniswapV3Router = ISwapRouter(swapRouter);

                ISwapRouter.ExactInputSingleParams memory params = ISwapRouter.ExactInputSingleParams({
                    tokenIn: tokenAddress,
                    tokenOut: usdAddress,
                    fee: fee,
                    recipient: address(this),
                    deadline: block.timestamp + 300,
                    amountIn: amountIn,
                    amountOutMinimum: 0,
                    sqrtPriceLimitX96: 0
                });

                // Perform the swap
                uint256 amountOut = uniswapV3Router.exactInputSingle(params);
                basketChangePercentage += amountOut;                
            }
            
            percentageFill -= (currentPercentage - percentage);
        }
        else
        {
            require(percentageFill - currentPercentage + percentage <= 10000, "token value of percentage entered invalid");
            uint256 availablePercentage = (10000 - percentageFill);
            
            //Starting value
            uint256 amountIn = 0;
            uint basketAdaptation = basketChangePercentage.mul(100);

            //If a token has been removed from the fund, we are probably making a change to the composition of the token 
            //and offsetting tokens are added that will have to use these funds to rebalance the fund
            if(basketAdaptation != 0 && percentage != 0)
            {
                uint256 basketFraction = basketAdaptation.div(availablePercentage);
                uint256 amountResult = basketFraction.mul(percentage);
                amountIn = amountResult.div(100);
            }
            
            if(amountIn > basketChangePercentage)
            {
                amountIn = basketChangePercentage;
            }

            if(amountIn != 0){
                IERC20(usdAddress).approve(swapRouter, amountIn);

                address poolAddress = pools[tokenAddress];
                uint24 fee = IUniswapV3Pool(poolAddress).fee();

                ISwapRouter uniswapV3Router = ISwapRouter(swapRouter);

                ISwapRouter.ExactInputSingleParams memory params = ISwapRouter.ExactInputSingleParams({
                    tokenIn: usdAddress,
                    tokenOut: tokenAddress,
                    fee: fee,
                    recipient: address(this),
                    deadline: block.timestamp + 300,
                    amountIn: amountIn,
                    amountOutMinimum: 0,
                    sqrtPriceLimitX96: 0
                });

                // Perform the swap
                uint256 amountOut = uniswapV3Router.exactInputSingle(params);
                basketChangePercentage -= amountIn;
            }

            percentageFill += (percentage - currentPercentage);
            
            //Changing lists if the percentage change is inherent in adding a new token to the fund
            if(currentPercentage == 0  && percentage != 0)
            {
                tokenInFundCount = tokenInFundCount + 1;

                tokenInFundPosition[tokenAddress] = tokenInFundCount;
                tokenInFund[tokenInFundCount] = tokenAddress;
            }
        }

        percentages[tokenAddress] = percentage;

        //List management
        tokenInFundManagament(tokenAddress, percentage);

        emit NewPercentageChange(tokenAddress, currentPercentage, percentage);
    }

    function checkTokenInfo(address tokenAddress, uint256 percentage) internal view {
        require(tokenAddress != address(0), "token address cannot be zero address" );
        // require(percentage != percentages[tokenAddress], "percentage value already set");
        require(aggregators[tokenAddress] != address(0), "token not previously added");
        require(percentage <= 10000, "token value of percentage entered invalid");
    }

    function tokenInFundManagament(address tokenAddress, uint256 percentage) internal {

        //Remove
        if(tokenInFundPosition[tokenAddress] != 0 && percentage == 0){
            //take current position
            uint8 position = tokenInFundPosition[tokenAddress];

            //remove from list
            tokenInFundPosition[tokenAddress] = 0;

            //reposition the last token to the position of the removed token
            address lastToken = tokenInFund[tokenInFundCount];
            tokenInFundCount = tokenInFundCount - 1;

            tokenInFundPosition[lastToken] = position;
            tokenInFund[position] = lastToken;
        }

    }

    function checkValidPool(address poolAddress) internal view {
        IUniswapV3Pool pool = IUniswapV3Pool(poolAddress);
        uint24 fee = pool.fee();
        
        require(fee != 0 , "invalid pool");
    }

    function discardToken(address tokenAddress, address managerAddress) external onlyAdmin nonReentrant {

        uint256 currentBalance = ERC20(tokenAddress).balanceOf(address(this));

        if(tokenInFundPosition[tokenAddress] == swapCounter)
        {
            swapCounter = swapCounter + 1;
        }

        //take price
        address aggregatorAddress = aggregators[tokenAddress];
        AggregatorV3Interface priceFeed = AggregatorV3Interface(aggregatorAddress);

        (, int256 price, , , ) = priceFeed.latestRoundData();

        //decimals for conversion
        uint256 priceDecimals;
        try priceFeed.decimals() returns (uint8 decimals) {
        priceDecimals = decimals;
        } catch {
            priceDecimals = 8;
        }

        ERC20 tokenERC20 = ERC20(tokenAddress);
        uint256 tokenDecimals = tokenERC20.decimals();
        
        uint256 uPrice = uint256(price);
        uint256 amountUsd = (currentBalance*uPrice*(1e6))/(10**(priceDecimals+tokenDecimals));

        IERC20(usdAddress).transferFrom(managerAddress, address(this), amountUsd);
        basketOpen += amountUsd;
        IERC20(tokenAddress).approve(address(this), currentBalance);
        IERC20(tokenAddress).transferFrom(address(this), managerAddress, currentBalance);

        emit NewDiscardToken(tokenAddress,managerAddress, currentBalance, uPrice, amountUsd);
    }
    
}"
    }
  },
