      "content": "// SPDX-License-Identifier: MIT
pragma solidity 0.8.6;

import {Ownable} from "@openzeppelin/contracts/access/Ownable.sol";
import {ERC1155} from "@openzeppelin/contracts/token/ERC1155/ERC1155.sol";
import {Strings} from "@openzeppelin/contracts/utils/Strings.sol";
import {Pausable} from "@openzeppelin/contracts/security/Pausable.sol";
import {ReentrancyGuard} from "@openzeppelin/contracts/security/ReentrancyGuard.sol";

contract SipherSpaceshipParts is ERC1155, Ownable, ReentrancyGuard, Pausable {
    event Minted(
        address indexed minter,
        uint256 batchID,
        uint256 amount,
        string salt
    );
    event MintedBatch(
        address indexed minter,
        uint256[] batchID,
        uint256[] amount,
        string salt
    );
    event Burned(
        address indexed burner,
        uint256 batchID,
        uint256 amount,
        uint256 salt
    );
    event BurnedBatch(
        address indexed burner,
        uint256[] batchID,
        uint256[] amount,
        uint256 salt
    );
    event CancelOrder(bytes signature);
    event ModifyBlacklist(address indexed user, bool isBlacklist);

    struct EIP712Domain {
        string name;
        string version;
        uint256 chainId;
        address verifyingContract;
    }
    struct Order {
        address to;
        uint256 batchID;
        uint256 amount;
        string salt;
        uint256 deadline;
    }
    struct BatchOrder {
        address to;
        uint256[] batchID;
        uint256[] amount;
        string salt;
        uint256 deadline;
    }
    mapping(address => bool) internal isBlacklist;
    address public authorizedSigner;
    address public admin;
    string public name;
    string public symbol;
    mapping(bytes => bool) public usedSig;
    bytes32 constant EIP712DOMAIN_TYPEHASH =
        keccak256(
            "EIP712Domain(string name,string version,uint256 chainId,address verifyingContract)"
        );
    bytes32 constant ORDER_TYPEHASH =
        keccak256(
            "Order(address to,uint256 batchID,uint256 amount,string salt,uint256 deadline)"
        );
    bytes32 constant BATCH_ORDER_TYPEHASH =
        keccak256(
            "BatchOrder(address to,bytes batchID,bytes amount,string salt,uint256 deadline)"
        );
    bytes32 public DOMAIN_SEPARATOR;

    constructor(
        address _admin,
        address _signer,
        string memory uri_,
        string memory _name,
        string memory _symbol,
        string memory _version
    ) ERC1155(uri_) {
        admin = _admin;
        name = _name;
        symbol = _symbol;
        authorizedSigner = _signer;
        DOMAIN_SEPARATOR = hashDomain(
            EIP712Domain({
                name: _name,
                version: _version,
                chainId: block.chainid,
                verifyingContract: address(this)
            })
        );
    }

    /**
     * @dev modifier to prevent blacklisted address to call function.
     */
    modifier notBlacklist() {
        if (isBlacklist[msg.sender]) {
            revert(
                "Sipher: You are block by admin, Please contact Sipher agent for more information"
            );
        }
        _;
    }

    /**
     * @dev set admin role.
     * Requirements:
     * - Only owner can excecute.
     */
    function setAdmin(address _admin) external onlyOwner {
        require(_admin != address(0), "Sipher: invalid admin address");
        admin = _admin;
    }

    /**
     * @dev set signer.
     * Requirements:
     * - Only owner can excecute.
     */
    function setSigner(address _signer) external onlyOwner {
        authorizedSigner = _signer;
    }

    /**
     * @dev lock user, the user after being locked by owner(), cannot do anything with their assets.
     * Requirements:
     * - owner or admin can execute.
     * - owner() cannot lock his own address.
     * - cannot repeat action on already existing data.
     */
    function setBlacklist(address user, bool isblacklist) external {
        require(
            msg.sender == admin || msg.sender == owner(),
            "Sipher: Only owner or admin can execute"
        );
        require(owner() != user, "Sipher: interact with owner address");
        require(
            isBlacklist[user] != isblacklist,
            "Sipher: address adready marked with input status"
        );
        isBlacklist[user] = isblacklist;
        emit ModifyBlacklist(user, isblacklist);
    }

    /**
     * @dev approve all asset for an operator.
     * Requirements:
     * - operator not in black list.
     */
    function setApprovalForAll(address operator, bool approved)
        public
        virtual
        override
        notBlacklist
    {
        super.setApprovalForAll(operator, approved);
    }

    /**
     * @dev set baseURI.
     * Requirements:
     * - owner or admin can execute.
     */
    function setBaseURI(string memory baseURI) external {
        require(
            msg.sender == admin || msg.sender == owner(),
            "Sipher: Only owner or admin can execute"
        );
        _setURI(baseURI);
    }


    /**
     * @dev set DOMAIN_SEPARATOR.
     * Requirements:
     * - owner or admin can execute.
     */
    function setDomainSeparator(
        string memory _name,
        string memory _version,
        string memory _symbol
    ) external {
        require(
            msg.sender == admin || msg.sender == owner(),
            "Sipher: Only owner or admin can execute"
        );
        name = _name;
        symbol = _symbol;
        DOMAIN_SEPARATOR = hashDomain(
            EIP712Domain({
                name: _name,
                version: _version,
                chainId: block.chainid,
                verifyingContract: address(this)
            })
        );
    }

    /**
     * @dev compute hash domain for signature verification.
     */
    function hashDomain(EIP712Domain memory eip712Domain)
        internal
        pure
        returns (bytes32)
    {
        return
            keccak256(
                abi.encode(
                    EIP712DOMAIN_TYPEHASH,
                    keccak256(bytes(eip712Domain.name)),
                    keccak256(bytes(eip712Domain.version)),
                    eip712Domain.chainId,
                    eip712Domain.verifyingContract
                )
            );
    }

    /**
     * @dev compute hash order for signature verification.
     */
    function hashOrder(Order memory order) internal pure returns (bytes32) {
        return
            keccak256(
                abi.encode(
                    ORDER_TYPEHASH,
                    order.to,
                    order.batchID,
                    order.amount,
                    keccak256(bytes(order.salt)),
                    order.deadline
                )
            );
    }

    /**
     * @dev compute hash batch order for signature verification.
     */
    function hashBatchOrder(BatchOrder memory batchOrder)
        internal
        pure
        returns (bytes32)
    {
        return
            keccak256(
                abi.encode(
                    BATCH_ORDER_TYPEHASH,
                    batchOrder.to,
                    keccak256(abi.encode(batchOrder.batchID)),
                    keccak256(abi.encode(batchOrder.amount)),
                    keccak256(bytes(batchOrder.salt)),
                    batchOrder.deadline
                )
            );
    }

    /**
     * @dev mint order.
     * Requirements:
     * - mint amount > 0.
     * - order is not expired.
     * - signature is not used.
     * - valid signature.
     * - when contract is not paused.
     */
    function mint(
        uint256 _deadline,
        uint256 _batchID,
        uint256 _amount,
        string memory _salt,
        bytes memory signature
    ) external notBlacklist nonReentrant {
        Order memory order = Order({
            to: msg.sender,
            batchID: _batchID,
            amount: _amount,
            salt: _salt,
            deadline: _deadline
        });
        require(order.amount > 0, "Sipher: invalid mint amount");
        require(block.timestamp <= order.deadline, "Sipher: order expired");
        require(usedSig[signature] == false, "Sipher: signature used");
        require(verify(order, signature), "Sipher: invalid order or signature");
        usedSig[signature] = true;
        _mint(order.to, order.batchID, order.amount, "");
        emit Minted(order.to, order.batchID, order.amount, _salt);
    }

    /**
     * @dev mint batch order.
     * Requirements:
     * - mint amount > 0.
     * - order is not expired.
     * - signature is not used.
     * - valid signature.
     * - when contract is not paused.
     */
    function mintBatch(
        uint256 _deadline,
        uint256[] memory _batchID,
        uint256[] memory _amount,
        string memory _salt,
        bytes memory signature
    ) external notBlacklist nonReentrant {
        BatchOrder memory batchOrder = BatchOrder({
            to: msg.sender,
            batchID: _batchID,
            amount: _amount,
            salt: _salt,
            deadline: _deadline
        });
        require(
            block.timestamp <= batchOrder.deadline,
            "Sipher: order expired"
        );
        require(
            !verifyBatchFormat(batchOrder.batchID, batchOrder.amount),
            "Sipher: invalid batch format"
        );
        require(usedSig[signature] == false, "Sipher: signature used");
        require(
            verifyBatch(batchOrder, signature),
            "Sipher: invalid batch order or signature"
        );
        usedSig[signature] = true;
        _mintBatch(batchOrder.to, batchOrder.batchID, batchOrder.amount, "");
        emit MintedBatch(
            batchOrder.to,
            batchOrder.batchID,
            batchOrder.amount,
            _salt
        );
    }
    
    /**
     * @dev verify batch order format.
     */
    function verifyBatchFormat(
        uint256[] memory batchID,
        uint256[] memory amount
    ) internal pure returns (bool) {
        bool isInvalid;
        for (uint256 i = 0; i < batchID.length; i++) {
            if (amount[i] == 0) {
                isInvalid = true;
            }
        }
        return isInvalid;
    }

    /**
     * @dev verify order signature.
     */
    function verify(Order memory order, bytes memory signature)
        internal
        view
        returns (bool)
    {
        // Note: we need to use `encodePacked` here instead of `encode`.
        bytes32 digest = keccak256(
            abi.encodePacked("\x19\x01", DOMAIN_SEPARATOR, hashOrder(order))
        );
        (bytes32 r, bytes32 s, uint8 v) = splitSignature(signature);
        return ecrecover(digest, v, r, s) == authorizedSigner;
    }

    /**
     * @dev verify batch order signature.
     */
    function verifyBatch(BatchOrder memory batchOrder, bytes memory signature)
        internal
        view
        returns (bool)
    {
        // Note: we need to use `encodePacked` here instead of `encode`.
        bytes32 digest = keccak256(
            abi.encodePacked(
                "\x19\x01",
                DOMAIN_SEPARATOR,
                hashBatchOrder(batchOrder)
            )
        );
        (bytes32 r, bytes32 s, uint8 v) = splitSignature(signature);
        return ecrecover(digest, v, r, s) == authorizedSigner;
    }

    /**
     * @dev split signature and returen r, s, v parameters.
     */
    function splitSignature(bytes memory sig)
        internal
        pure
        returns (
            bytes32 r,
            bytes32 s,
            uint8 v
        )
    {
        require(sig.length == 65, "Sipher: invalid signature length");
        assembly {
            // first 32 bytes, after the length prefix
            r := mload(add(sig, 32))
            // second 32 bytes
            s := mload(add(sig, 64))
            // final byte (first byte of the next 32 bytes)
            v := byte(0, mload(add(sig, 96)))
        }
    }

    /**
     * @dev check if user is blacklisted.
     */
    function isUserBlacklist(address user) external view returns (bool) {
        return isBlacklist[user];
    }

    /**
     * @dev transfer token.
     * Requirements:
     * - receiver not in black list
     * - can not transfer to blacklisted address.
     * - when contract is not paused.
     */
    function safeTransferFrom(
        address from,
        address to,
        uint256 id,
        uint256 amount,
        bytes memory data
    ) public virtual override notBlacklist {
        require(
            !isBlacklist[from],
            "Sipher: transfer from a blacklist address"
        );
        require(!isBlacklist[to], "Sipher: transfer to a blacklist address");
        super.safeTransferFrom(from, to, id, amount, data);
    }

    /**
     * @dev batch transfer tokens.
     * Requirements:
     * - receiver not in black list
     * - can not transfer to blacklisted address.
     * - when contract is not paused.
     */
    function safeBatchTransferFrom(
        address from,
        address to,
        uint256[] memory ids,
        uint256[] memory amounts,
        bytes memory data
    ) public virtual override notBlacklist {
        require(
            !isBlacklist[from],
            "Sipher: transfer from a blacklist address"
        );
        require(!isBlacklist[to], "Sipher: transfer to a blacklist address");
        super.safeBatchTransferFrom(from, to, ids, amounts, data);
    }

    /**
     * @dev burn tokens.
     * Requirements:
     * - receiver not in black list.
     * - when contract is not paused.
     */
    function burn(uint256 batchID, uint256 amount)
        external
        notBlacklist
        nonReentrant
    {
        _burn(msg.sender, batchID, amount);
        uint256 _salt = uint256(
            keccak256(
                abi.encodePacked(
                    block.timestamp,
                    block.difficulty,
                    msg.sender,
                    batchID,
                    amount
                )
            )
        );
        emit Burned(msg.sender, batchID, amount, _salt);
    }

    /**
     * @dev burn tokens.
     * Requirements:
     * - receiver not in black list.
     * - when contract is not paused.
     */
    function burnBatch(uint256[] memory batchID, uint256[] memory amount)
        external
        notBlacklist
        nonReentrant
    {
        _burnBatch(msg.sender, batchID, amount);
        uint256 _salt = uint256(
            keccak256(
                abi.encodePacked(
                    block.timestamp,
                    block.difficulty,
                    msg.sender,
                    batchID,
                    amount
                )
            )
        );
        emit BurnedBatch(msg.sender, batchID, amount, _salt);
    }

    /**
     * @dev for anyone to cancel an order with a valid signature.
     */
    function cancelOrder(bytes memory signature) external {
        usedSig[signature] = true;
        emit CancelOrder(signature);
    }

    /**
     * @dev get token uri.
     */
    function uri(uint256 _batchID)
        public
        view
        virtual
        override
        returns (string memory)
    {
        string memory baseURI = super.uri(_batchID);
        return
            bytes(baseURI).length > 0
                ? string(abi.encodePacked(baseURI, Strings.toString(_batchID)))
                : "";
    }

    /**
     * @dev call by owner or admin to pause the transfer
     */
    function pause() external {
        require(
            msg.sender == admin || msg.sender == owner(),
            "Sipher: Only owner or admin can execute"
        );
        _pause();
    }

    /**
     * @dev call by owner or admin to unpause the transfer.
     */
    function unpause() external {
        require(
            msg.sender == admin || msg.sender == owner(),
            "Sipher: Only owner or admin can execute"
        );
        _unpause();
    }

    /**
     * @dev override to make sure all type of transfers will fail when the contract is paused.
     */
    function _beforeTokenTransfer(
        address operator,
        address from,
        address to,
        uint256[] memory ids,
        uint256[] memory amounts,
        bytes memory data
    ) internal override(ERC1155) whenNotPaused {
        super._beforeTokenTransfer(operator, from, to, ids, amounts, data);
    }
}
