    "contracts/Converter/SuperMintConverter.sol": {
      "content": "// SPDX-License-Identifier: MIT

pragma solidity ^0.8.11;

import '@openzeppelin/contracts/access/Ownable.sol';
import '@openzeppelin/contracts/security/ReentrancyGuard.sol';
import '@openzeppelin/contracts/utils/cryptography/EIP712.sol';
import '@openzeppelin/contracts/access/AccessControl.sol';

import '@chainlink/contracts/src/v0.8/interfaces/VRFCoordinatorV2Interface.sol';
import '@chainlink/contracts/src/v0.8/VRFConsumerBaseV2.sol';

import '../Tokens/ControlledMint.ERC1155.sol';
import '../Tokens/LockedToken.ERC721.sol';

/**
 * Enables conversion transactions between a specific token ID of the PGERC1155 token instance into LockedTokenERC721 tokens using the mint function.
 * Having a PGERC1155 (contract 1) token along with a PGERC1155 (contract 2) super token will enable users to use superMint functionality that mints a super LockedTokenERC721 token.
 * Any user that has this combination of tokens can perform a mint/superMint.
 */
contract SuperMintConverter is Ownable, ReentrancyGuard, VRFConsumerBaseV2, AccessControl, EIP712 {
	event TokenBurned(
		address indexed burner,
		uint256 indexed requestId,
		uint256 indexed tokenId,
		address targetAddress,
		uint256 amountBurned,
		uint256 tokenIdERC721
	);

	event TokenBurnedMultiple(
		address indexed burner,
		uint256 indexed requestId,
		uint256[] tokenIds,
		address targetAddress,
		uint256[] amountsBurned,
		uint256[] tokenIdsERC721
	);

	event TokenBurnedSuper(
		address indexed burner,
		uint256 indexed requestId,
		uint256 indexed tokenId,
		address targetAddress,
		uint256 amountBurned,
		uint256[] superIds,
		uint256 tokenIdERC721
	);

	event RequestFulfilled(address indexed burner, uint256 indexed requestId, uint256 randomWord);

	event RequestFulfilledAdmin(address burner, uint256 indexed requestId, uint256 randomWord);

	event RandomNumberForBurn(
		address indexed burner,
		uint256 indexed requestId,
		uint256 randomWord
	);

	struct RequestStatus {
		bool fulfilled;
		bool exists;
		address burner;
		address to;
		uint256 tokenId;
		uint256 mintAmount;
		uint256 randomWord;
	}

	struct RequestStatusMultiple {
		bool fulfilled;
		bool exists;
		address burner;
		address to;
		uint256 randomWord;
		uint256[] tokenIds;
		uint256[] mintAmounts;
	}

	struct RequestStatusSuperMint {
		bool fulfilled;
		bool exists;
		address burner;
		address to;
		uint256 tokenId;
		uint256 mintAmount;
		uint256 randomWord;
		uint256[] superIds;
	}

	struct RequestStatusAdmin {
		bool fulfilled;
		bool exists;
		address burner;
		uint256 randomWord;
	}

	struct BurnSuperToken {
		uint256 chainId;
		address to;
		uint256 tokenId;
		uint256[] superIds;
	}

	// Request ids linked to their status
	mapping(uint256 => RequestStatus) public s_requests;

	// Request id to multiple token burns
	mapping(uint256 => RequestStatusMultiple) public s_requestsMultiple;

	// Request id to super mint status
	mapping(uint256 => RequestStatusSuperMint) public s_requestsSuperMint;

	// Request id to admin requests
	mapping(uint256 => RequestStatusAdmin) public s_requestsAdmin;

	// Token id burnt to NFT to mint
	mapping(uint256 => address) public tokenIdForErc721Address;

	// The operator that set contract to pause/unpaused
	bool public paused = false;

	// An account that receives the 1155 tokens after the conversion is made
	address public receiver;

	// The PGERC1155 instance of the 1155 NFT we want to convert
	PGERC1155 public pgerc1155;

	// The PGERC1155 instance of the NFT needed to make a super mint
	PGERC1155 public pgerc1155_super;

	// Chainlink VRF coordinator address specific to desired chain
	VRFCoordinatorV2Interface public i_vrfCoordinator;

	// gasLane (keyHash) of the specific chain
	bytes32 public i_keyHash;

	// Typed Data Hash for signature verification
	bytes32 typedDataHash;

	// Admin role for changing settings
	bytes32 public constant ADMIN_ROLE = keccak256('ADMIN_ROLE');

	// Admin role for signature verification
	bytes32 public constant SIGNER_ROLE = keccak256('SIGNER_ROLE');

	// Request id from ChainLink VRF
	uint256 public lastRequestId;

	// Chainlink VRF subscription Id created by the owner
	uint64 public i_subscriptionId;

	// Chainlink VRF callback function limit cost
	uint32 public i_callbackGasLimit;

	// Number of random words chainLink VRF will provide
	uint32 public NUM_WORDS = 1;

	// How many blocks the VRF service waits before writing a fulfillment to the chain
	uint16 public REQUEST_CONFIRMATIONS = 3;

	/**
	 * Instantiates the contract by assigning the pgerc1155 collection, pgerc721 collection, pgerc1155_super collection,
	 * _PGERC1155PlanetId tokenId from pgerc1155 and the receiver
	 */
	constructor(
		PGERC1155 _pgerc1155,
		PGERC1155 _pgerc1155_super,
		address _receiver,
		address vrfCoordinatorV2,
		string memory _domainName,
		string memory _signatureVersion,
		bytes32 _typedDataHash
	) VRFConsumerBaseV2(vrfCoordinatorV2) EIP712(_domainName, _signatureVersion) {
		pgerc1155 = _pgerc1155;
		pgerc1155_super = _pgerc1155_super;
		i_vrfCoordinator = VRFCoordinatorV2Interface(vrfCoordinatorV2);
		_setupRole(DEFAULT_ADMIN_ROLE, msg.sender);
		_setupRole(ADMIN_ROLE, msg.sender);
		_setupRole(SIGNER_ROLE, msg.sender);
		setReceiver(_receiver);
		typedDataHash = _typedDataHash;
	}

	/**
	 * @dev Function that initiate all the chainLink variables
	 * @param subscriptionId Sets the subscriptionId
	 * @param keyHash Sets the gasLane/keyHash
	 * @param callbackGasLimit Sets the callbackGasLimit
	 * @notice Only the contract ADMIN can call this function.
	 */
	function initChainLink(
		uint64 subscriptionId,
		bytes32 keyHash,
		uint32 callbackGasLimit
	) external onlyRole(ADMIN_ROLE) {
		i_subscriptionId = subscriptionId;
		i_keyHash = keyHash;
		i_callbackGasLimit = callbackGasLimit;
	}

	/**
	 * @notice Burn tokens, initiate a random word request and mint LockedTokenERC721 nfts.
	 * @dev This function allows burning a specified amount of tokens, initiating a random word request and minting the specified amount of LockedTokenERC721 tokens.
	 * @param _to The address to which the burned tokens will be transferred.
	 * @param _tokenId The ID of the token to be burned.
	 * @param _mintAmount The amount of tokens to be burned and minted.
	 */
	function burnTokens(address _to, uint256 _tokenId, uint256 _mintAmount) external nonReentrant {
		require(!paused, 'Super Mint Converter: Contract is paused');
		require(_mintAmount != 0, 'Super Mint Converter: mint amount must be > 0');

		uint256 requestId = i_vrfCoordinator.requestRandomWords(
			i_keyHash,
			i_subscriptionId,
			REQUEST_CONFIRMATIONS,
			i_callbackGasLimit,
			NUM_WORDS
		);

		s_requests[requestId] = RequestStatus({
			randomWord: 0,
			exists: true,
			fulfilled: false,
			burner: msg.sender,
			to: _to,
			tokenId: _tokenId,
			mintAmount: _mintAmount
		});

		lastRequestId = requestId;

		pgerc1155.safeTransferFrom(msg.sender, receiver, _tokenId, _mintAmount, '0x');
		LockedTokenERC721 pg721 = LockedTokenERC721(tokenIdForErc721Address[_tokenId]);
		uint256 currentId = pg721.totalSupply() + 1;

		pg721.mint(_to, _mintAmount);
		emit TokenBurned(msg.sender, requestId, _tokenId, _to, _mintAmount, currentId);
	}

	/**
	 * @notice Burn multiple tokens, initiate a random word request and mint multiple LockedTokenERC721 nfts.
	 * @dev This function allows burning multiple tokens with corresponding amounts, initiating a random word request and minting multiple LockedTokenERC721 tokens.
	 * @param _to The address to which the burned tokens will be transferred.
	 * @param _tokenIds The array of token IDs to be burned.
	 * @param _mintAmounts The array of amounts corresponding to the token IDs to be burned and minted.
	 */
	function burnTokensMultiple(
		address _to,
		uint256[] calldata _tokenIds,
		uint256[] calldata _mintAmounts
	) external nonReentrant {
		require(!paused, 'Super Mint Converter: Contract is paused');
		require(_tokenIds.length > 0, 'Super Mint Converter: tokenIds.length must be > 0');
		require(
			_tokenIds.length == _mintAmounts.length,
			'Super Mint Converter: Arrays need to have the same lengths'
		);

		uint256 requestId = i_vrfCoordinator.requestRandomWords(
			i_keyHash,
			i_subscriptionId,
			REQUEST_CONFIRMATIONS,
			i_callbackGasLimit,
			NUM_WORDS
		);

		s_requestsMultiple[requestId] = RequestStatusMultiple({
			randomWord: 0,
			exists: true,
			fulfilled: false,
			burner: msg.sender,
			to: _to,
			tokenIds: _tokenIds,
			mintAmounts: _mintAmounts
		});

		lastRequestId = requestId;
		uint256[] memory tokenIds721Minted = new uint256[](_tokenIds.length);

		for (uint256 i = 0; i < _tokenIds.length; i++) {
			require(_mintAmounts[i] > 0, 'Super Mint Converter: mint amount must be > 0');
			pgerc1155.safeTransferFrom(msg.sender, receiver, _tokenIds[i], _mintAmounts[i], '0x');

			LockedTokenERC721 pg721 = LockedTokenERC721(tokenIdForErc721Address[_tokenIds[i]]);
			uint256 currentId = pg721.totalSupply() + 1;
			tokenIds721Minted[i] = currentId;
			pg721.mint(_to, _mintAmounts[i]);
		}

		emit TokenBurnedMultiple(
			msg.sender,
			requestId,
			_tokenIds,
			_to,
			_mintAmounts,
			tokenIds721Minted
		);
	}

	/**
	 * @notice Burn a Super Token, initiate a random word request and mint a LockedTokenERC721 nft.
	 * @dev This function allows burning a Super Token, verifying the provided signature, initiating a random word request and minting a LockedTokenERC721 nft.
	 * @param _burnSuperToken The BurnSuperToken calldata struct containing the necessary information for burning the Super Token.
	 * @param _signature The signature to be verified.
	 */
	function burnSuperToken(
		BurnSuperToken calldata _burnSuperToken,
		bytes calldata _signature
	) external nonReentrant {
		require(!paused, 'Super Mint Converter: Contract is paused');
		require(
			hasRole(SIGNER_ROLE, _verify(_burnSuperToken, _signature)),
			'Super Mint Converter: Invalid signature'
		);

		uint256 requestId = i_vrfCoordinator.requestRandomWords(
			i_keyHash,
			i_subscriptionId,
			REQUEST_CONFIRMATIONS,
			i_callbackGasLimit,
			NUM_WORDS
		);

		s_requestsSuperMint[requestId] = RequestStatusSuperMint({
			randomWord: 0,
			exists: true,
			fulfilled: false,
			burner: msg.sender,
			to: _burnSuperToken.to,
			tokenId: _burnSuperToken.tokenId,
			mintAmount: 1,
			superIds: _burnSuperToken.superIds
		});

		lastRequestId = requestId;

		pgerc1155.safeTransferFrom(msg.sender, receiver, _burnSuperToken.tokenId, 1, '0x');

		for (uint256 i = 0; i < _burnSuperToken.superIds.length; i++) {
			pgerc1155_super.safeTransferFrom(
				msg.sender,
				receiver,
				_burnSuperToken.superIds[i],
				1,
				'0x'
			);
		}

		LockedTokenERC721 pg721 = LockedTokenERC721(
			tokenIdForErc721Address[_burnSuperToken.tokenId]
		);
		uint256 currentId = pg721.totalSupply() + 1;
		pg721.mint(_burnSuperToken.to, 1);

		emit TokenBurnedSuper(
			msg.sender,
			requestId,
			_burnSuperToken.tokenId,
			_burnSuperToken.to,
			1,
			_burnSuperToken.superIds,
			currentId
		);
	}

	/**
	 * @notice initiate a random word request only by an admin. Helper function in case ChainLinkVRF user request fail.
	 * @param _burner The address to which the random number will be associated with transferred.
	 */
	function askRandomNumber(address _burner) external onlyRole(ADMIN_ROLE) {
		uint256 requestId = i_vrfCoordinator.requestRandomWords(
			i_keyHash,
			i_subscriptionId,
			REQUEST_CONFIRMATIONS,
			i_callbackGasLimit,
			NUM_WORDS
		);

		s_requestsAdmin[requestId] = RequestStatusAdmin({
			randomWord: 0,
			exists: true,
			fulfilled: false,
			burner: _burner
		});
	}

	/**
	 * @notice Fallback function called by chainlink after askRandomNumber function is called;
	 * @param _requestId Id we received when we called the askRandom function
	 * @param _randomWords Array with random numbers generated by ChainLink
	 */
	function fulfillRandomWords(
		uint256 _requestId,
		uint256[] memory _randomWords
	) internal override {
		if (s_requests[_requestId].exists && !s_requests[_requestId].fulfilled) {
			s_requests[_requestId].fulfilled = true;
			s_requests[_requestId].randomWord = _randomWords[0];

			emit RequestFulfilled(
				s_requests[_requestId].burner,
				_requestId,
				s_requests[_requestId].randomWord
			);
		} else if (
			s_requestsMultiple[_requestId].exists && !s_requestsMultiple[_requestId].fulfilled
		) {
			s_requestsMultiple[_requestId].fulfilled = true;
			s_requestsMultiple[_requestId].randomWord = _randomWords[0];

			emit RequestFulfilled(
				s_requestsMultiple[_requestId].burner,
				_requestId,
				s_requestsMultiple[_requestId].randomWord
			);
		} else if (
			s_requestsSuperMint[_requestId].exists && !s_requestsSuperMint[_requestId].fulfilled
		) {
			s_requestsSuperMint[_requestId].fulfilled = true;
			s_requestsSuperMint[_requestId].randomWord = _randomWords[0];

			emit RequestFulfilled(
				s_requestsSuperMint[_requestId].burner,
				_requestId,
				s_requestsSuperMint[_requestId].randomWord
			);
		} else if (s_requestsAdmin[_requestId].exists && !s_requestsAdmin[_requestId].fulfilled) {
			s_requestsAdmin[_requestId].fulfilled = true;
			s_requestsAdmin[_requestId].randomWord = _randomWords[0];

			emit RequestFulfilledAdmin(
				s_requestsAdmin[_requestId].burner,
				_requestId,
				s_requestsAdmin[_requestId].randomWord
			);
		} else {
			revert('request not found');
		}
	}

	/**
	 * @notice Verify the signature for a BurnSuperToken request
	 * @dev This internal function verifies the signature for a given BurnSuperToken struct and returns the address of the signer.
	 * @param _burnSuperToken The BurnSuperToken calldata struct to be verified.
	 * @param _signature The signature to be verified.
	 * @return The address of the signer.
	 */
	function _verify(
		BurnSuperToken calldata _burnSuperToken,
		bytes calldata _signature
	) internal view returns (address) {
		bytes32 digest = _hashTypedData(_burnSuperToken);
		return ECDSA.recover(digest, _signature);
	}

	/**
	 * @notice Compute the hash of a BurnSuperToken struct as per the typed data schema
	 * @dev This internal function computes the hash of the provided BurnSuperToken struct using the typed data schema.
	 * @param _burnSuperToken The BurnSuperToken calldata struct to be hashed.
	 * @return The computed hash of the BurnSuperToken struct.
	 */
	function _hashTypedData(
		BurnSuperToken calldata _burnSuperToken
	) internal view returns (bytes32) {
		return
			_hashTypedDataV4(
				keccak256(
					abi.encode(
						typedDataHash,
						block.chainid,
						_burnSuperToken.to,
						_burnSuperToken.tokenId,
						keccak256(abi.encodePacked(_burnSuperToken.superIds))
					)
				)
			);
	}

	/**
	 * Changes the receiver that gets the 1155 NFTs after conversions
	 *
	 * Access: only the contract ADMIN account
	 *
	 * @param _receiver new receiver
	 */
	function setReceiver(address _receiver) public onlyRole(ADMIN_ROLE) {
		require(_receiver != address(0), 'Super Mint Converter: Receiver cannot be zero address');
		receiver = _receiver;
	}

	/**
	 * Changes the pgerc1155 contract
	 *
	 * Access: only the contract ADMIN account.
	 *
	 * @param _pgerc1155 new pgerc1155 contract
	 */
	function setPgerc1155(PGERC1155 _pgerc1155) public onlyRole(ADMIN_ROLE) {
		pgerc1155 = _pgerc1155;
	}

	/**
	 * Changes the pgerc1155_super contract
	 *
	 * Access: only the contract ADMIN account
	 *
	 * @param _pgerc1155_super new pgerc1155_super contract
	 */
	function setPgerc1155_super(PGERC1155 _pgerc1155_super) public onlyRole(ADMIN_ROLE) {
		pgerc1155_super = _pgerc1155_super;
	}

	/**
	 * Changes the state of the conversions (paused or not paused)
	 *
	 * Access: only the contract ADMIN account.
	 *
	 * @param state new value for pause (true/false)
	 */
	function setPaused(bool state) public onlyRole(ADMIN_ROLE) {
		paused = state;
	}

	/**
	 * Changes the chainlink subscription id
	 *
	 * Access: only the contract ADMIN account.
	 *
	 * @param subscriptionId new value
	 */
	function setSubscriptionId(uint64 subscriptionId) external onlyRole(ADMIN_ROLE) {
		i_subscriptionId = subscriptionId;
	}

	/**
	 * Changes the chainlink keyhash
	 *
	 * Access: only the contract ADMIN account.
	 *
	 * @param keyHash new value
	 */
	function setKeyHash(bytes32 keyHash) external onlyRole(ADMIN_ROLE) {
		i_keyHash = keyHash;
	}

	/**
	 * Changes the chainlink callback gas limit
	 *
	 * Access: only the contract ADMIN account.
	 *
	 * @param callbackGasLimit new value
	 */
	function setCallbackGasLimit(uint32 callbackGasLimit) external onlyRole(ADMIN_ROLE) {
		i_callbackGasLimit = callbackGasLimit;
	}

	/**
	 * Changes the chainlink request confirmations
	 *
	 * Access: only the contract ADMIN account.
	 *
	 * @param requestConfirmations new value
	 */
	function setRequestConfirmations(uint16 requestConfirmations) external onlyRole(ADMIN_ROLE) {
		REQUEST_CONFIRMATIONS = requestConfirmations;
	}

	/**
	 * maps specific PGERC1155' tokenIds to different LockedTokenERC721 addresses.
	 *
	 * Access: only the contract ADMIN account.
	 *
	 * @param tokenIds array with tokenIds
	 * @param addresses array of addresses that will be associated to the tokenIds
	 */
	function setTokenIdForErc721AddressBatch(
		uint256[] calldata tokenIds,
		address[] calldata addresses
	) external onlyRole(ADMIN_ROLE) {
		require(
			tokenIds.length == addresses.length,
			'Super Mint Converter: arrays must have the same lengths'
		);
		for (uint256 i = 0; i < tokenIds.length; i++) {
			tokenIdForErc721Address[tokenIds[i]] = addresses[i];
		}
	}
}
