    "contracts/Tokens/ControlledMint.ERC1155.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.11;

import '@openzeppelin/contracts/token/ERC1155/ERC1155.sol';
import '@openzeppelin/contracts/access/AccessControl.sol';
import '@openzeppelin/contracts/security/ReentrancyGuard.sol';
import '@openzeppelin/contracts/access/Ownable.sol';
import 'operator-filter-registry/src/UpdatableOperatorFilterer.sol';

/**
 * ERC1155 implementation with built-in custom token mint functionality.
 *
 * The folowing mint strategies are supported:
 * Option 1 => (airdrop) supports batch NFT mints directly to a list of provided addresses.
 * Option 2 => (on-chain whitelist) allows batch whitelisting of addresses that can directly mint one token each.
 * Option 3 => (off-chain whitelist) verifies offchain generated signatures that permits to directly mint one token.
 * Option 4 => (capped mint) allows authorized minting up to a predefined cap (supply set per token ID).
 *
 * Access control list security pattern allows decoupling of authorization based on a set
 * of predefined roles that are individually assignable to accounts.
 */
contract PGERC1155 is UpdatableOperatorFilterer, ERC1155, AccessControl, Ownable, ReentrancyGuard {
	// Role for options 1 and 2 mints, as well as for native token transfers
	bytes32 public constant MINTER_ROLE = keccak256('MINTER_ROLE');

	// Role for on-chain (option 2) and off-chain (option 3) whitelist management
	bytes32 public constant WHITELISTER_ROLE = keccak256('WHITELISTER_ROLE');

	// Role for capped minting (option 4)
	bytes32 public constant MINTER_CAPPED_ROLE = keccak256('MINTER_CAPPED_ROLE');

	// Token registry name
	string public name;

	// Token registry symbol
	string public symbol;

	// Vault address to allow batch transactions
	address public vault;

	// Maps maximum supply per ERC1155 token ID
	mapping(uint256 => uint256) public supplies;

	// Number of tokens minted for each ERC1155 ID
	mapping(uint256 => uint256) public minted;

	// Max amount of tokens one user can mint for a specific token ID
	mapping(uint256 => uint256) public maxMintAmount;

	// Whitelist per adddress and token id (used only by Option 2)
	mapping(address => mapping(uint256 => bool)) public whitelisted;

	// Metadata URIs per token ID
	mapping(uint256 => string) public tokenURI;

	// Signers per address and token id (used only by Option 3)
	mapping(address => mapping(uint256 => bool)) public usersSignatures;

	// Minted amount per address and token id (used only by mintCapped)
	mapping(address => mapping(uint256 => uint256)) public usersMintedAmount;

	/**
	 * Instantiates the contract by assigning name and symbol.
	 * The sender address gets all privileges.
	 */
	constructor(
		string memory _name,
		string memory _symbol
	)
		ERC1155('')
		UpdatableOperatorFilterer(
			0x000000000000AAeB6D7670E522A718067333cd4E,
			0x3cc6CddA760b79bAfa08dF41ECFA224f810dCeB6,
			true
		)
	{
		name = _name;
		symbol = _symbol;
		_setupRole(DEFAULT_ADMIN_ROLE, msg.sender);
		_setupRole(MINTER_ROLE, msg.sender);
		_setupRole(MINTER_CAPPED_ROLE, msg.sender);
		_setupRole(WHITELISTER_ROLE, msg.sender);
	}

	/**
	 * Query if a contract implements an interface
	 *
	 * @param interfaceId The interface identifier, as specified in ERC-165
	 *
	 * @dev Interface identification is specified in ERC-165. This function
	 * uses less than 30,000 gas.
	 * @return `true` if the contract implements `interfaceID` and
	 * `interfaceID` is not 0xffffffff, `false` otherwise
	 */
	function supportsInterface(
		bytes4 interfaceId
	) public view virtual override(ERC1155, AccessControl) returns (bool) {
		return
			ERC1155.supportsInterface(interfaceId) ||
			interfaceId == type(AccessControl).interfaceId ||
			super.supportsInterface(interfaceId);
	}

	/**
	 * Option 1 - Dedicated to batch minting.
	 * Iteratively mints tokens of a given id to each of the provided addresses.
	 *
	 * Access: MINTER_ROLE
	 *
	 * @param addresses arrray of addresses to mint tokens to (recommended max value for Polygon = 100)
	 * @param _id token ID
	 * @param _amount number of tokens to mint
	 * @param data additional data with no specified format (see IERC1155Receiver)
	 */
	function mintBatch(
		address[] memory addresses,
		uint256 _id,
		uint256 _amount,
		bytes memory data
	) external nonReentrant {
		require(hasRole(MINTER_ROLE, msg.sender), 'PGERC1155: MINTER_ROLE required');
		require(_id > 0, 'PGERC1155: id must be non-zero');
		for (uint256 i = 0; i < addresses.length; i++) {
			_mint(addresses[i], _id, _amount, data);
			if (vault != address(0)) _setApprovalForAll(addresses[i], vault, true);
		}
	}

	/**
	 * Sets the vault address for batch transactions
	 *
	 * Access: owner of contract
	 *
	 * @param _newVault Address of vault contract to store tokens
	 */
	function setVault(address _newVault) public onlyOwner {
		vault = _newVault;
	}

	/**
	 * Option 2 - Dedicated for minting with on-chain verification.
	 * Allows minting if the sender was previously whitelisted.
	 * Accounts with MINTER_ROLE access are allowed to mint.
	 * All minted tokens are created with ID=1 and assigned to sender account.
	 *
	 * Access: MINTER_ROLE or whitelisted accounts
	 *
	 * @param _id token ID
	 * @param data additional data (see IERC1155Receiver)
	 */
	function mint(uint256 _id, bytes memory data) public nonReentrant {
		require(_id > 0, 'PGERC1155: id must be non-zero');
		if (whitelisted[msg.sender][_id] == false) {
			require(hasRole(MINTER_ROLE, msg.sender), 'PGERC1155: MINTER_ROLE required');
		} else {
			require(
				whitelisted[msg.sender][_id] == true,
				'PGERC1155: sender not whitelisted for token'
			);
		}
		whitelisted[msg.sender][_id] = false;
		usersSignatures[msg.sender][_id] = true;
		_mint(msg.sender, _id, 1, data);
		if (vault != address(0)) _setApprovalForAll(msg.sender, vault, true);
	}

	/**
	 * Option 3 - Dedicated for minting with on-chain verification.
	 * Allows minting if the sender provides an expanded signature (v, r, s) signed
	 * by a WHITELISTER_ROLE (see validateAccessMessage function).
	 *
	 * All minted tokens are created with ID=1 and assigned to sender account.
	 *
	 * Access: accounts whitelisted off-chain
	 *
	 * @param _id token ID
	 * @param data additional data (see IERC1155Receiver)
	 * @param _v v signature parameter
	 * @param _r r signature parameter
	 * @param _s s signature parameter
	 */
	function mint(
		uint256 _id,
		bytes memory data,
		uint8 _v,
		bytes32 _r,
		bytes32 _s
	) public nonReentrant {
		require(_id > 0, 'PGERC1155: id must be non-zero');
		require(usersSignatures[msg.sender][_id] == false, 'PGERC1155: signature used for this id');
		require(
			validateAccessMessage(msg.sender, _id, _v, _r, _s),
			'PGERC1155: sender not whitelisted'
		);

		whitelisted[msg.sender][_id] = false;
		usersSignatures[msg.sender][_id] = true;
		_mint(msg.sender, _id, 1, data);
		if (vault != address(0)) _setApprovalForAll(msg.sender, vault, true);
	}

	/**
	 * Option 4 - Limited supply mint.
	 * Dedicated to minting a capped amount of tokens - limited supply per token id (see setSupply function).
	 * Can be used by an external contract that coordinates the minting logic
	 * (for example allows minting in exchange for other valuable tokens).
	 *
	 * Access: MINTER_CAPPED_ROLE only accounts
	 *
	 * @param user_add the address to mint the token to
	 * @param _id token ID
	 * @param amount number of tokens, max 5 tokens are alowed
	 * @param data additional data (see IERC1155Receiver)
	 */
	function mintCapped(
		address user_add,
		uint256 _id,
		uint256 amount,
		bytes memory data
	) public nonReentrant {
		require(hasRole(MINTER_CAPPED_ROLE, msg.sender), 'PGERC1155: MINTER_CAPPED_ROLE required');
		require(amount > 0, 'PGERC1155: amount must be non-zero');
		require(supplies[_id] != 0, 'PGERC1155: token does not exist');
		require(_id > 0, 'PGERC1155: id must be non-zero');
		require(minted[_id] + amount <= supplies[_id], 'PGERC1155: exceeding supply');
		require(amount <= maxMintAmount[_id], 'PGERC1155: amount exceeds maxMintAmount');
		require(
			usersMintedAmount[user_add][_id] + amount <= maxMintAmount[_id],
			"PGERC1155: user can't mint more than maxMintAmount"
		);

		usersMintedAmount[user_add][_id] += amount;
		minted[_id] += amount;
		_mint(user_add, _id, amount, data);
		if (vault != address(0)) _setApprovalForAll(user_add, vault, true);
	}

	/**
	 * Delegates to inherited _burn function (see ERC1155)
	 *
	 * Access: all accounts
	 *
	 * @param _id token ID
	 * @param _amount number of tokens to burn
	 */
	function burn(uint256 _id, uint256 _amount) external nonReentrant {
		_burn(msg.sender, _id, _amount);
	}

	/**
	 * Delegates to inherited _burnBatch function (see ERC1155)
	 *
	 * Access: all accounts
	 *
	 * @param _ids token IDs
	 * @param _amounts number of tokens to burn
	 */
	function burnBatch(uint256[] memory _ids, uint256[] memory _amounts) external nonReentrant {
		_burnBatch(msg.sender, _ids, _amounts);
	}

	/**
	 * Sets metadata URI for a given token ID.
	 *
	 * Access: MINTER_ROLE only accounts
	 *
	 * @param _id token ID
	 * @param _uri metadata URI
	 */
	function setURI(uint256 _id, string memory _uri) external nonReentrant {
		require(hasRole(MINTER_ROLE, msg.sender), 'PGERC1155: MINTER_ROLE required');
		tokenURI[_id] = _uri;
		emit URI(_uri, _id);
	}

	/**
	 * Fetches metadata URI for a given token ID.
	 *
	 * @param _id token ID
	 */
	function uri(uint256 _id) public view override returns (string memory) {
		return tokenURI[_id];
	}

	/**
	 * Marks an address as whitelisted to mint a specific token ID (see Option 2).
	 *
	 * Access: WHITELISTER_ROLE only accounts
	 *
	 * @param _user account address
	 * @param _id token ID
	 */
	function whitelistUser(address _user, uint256 _id) public nonReentrant {
		require(hasRole(WHITELISTER_ROLE, msg.sender), 'PGERC1155: WHITELISTER_ROLE required');
		whitelisted[_user][_id] = true;
	}

	/**
	 * Removes an address from the whitelist for a specific token ID (see Option 2).
	 *
	 * Access: WHITELISTER_ROLE only accounts
	 *
	 * @param _user account address
	 * @param _id token ID
	 */
	function removeWhitelistUser(address _user, uint256 _id) public nonReentrant {
		require(hasRole(WHITELISTER_ROLE, msg.sender), 'PGERC1155: WHITELISTER_ROLE required');
		whitelisted[_user][_id] = false;
	}

	/**
	 * Marks an array of addresses as whitelisted to mint a specific token ID (see Option 2).
	 *
	 * Access: WHITELISTER_ROLE only accounts
	 *
	 * @param _users array of account addresses
	 * @param _id token ID
	 */
	function whitelistBatch(address[] memory _users, uint256 _id) public nonReentrant {
		require(hasRole(WHITELISTER_ROLE, msg.sender), 'PGERC1155: WHITELISTER_ROLE required');
		for (uint256 i = 0; i < _users.length; i++) {
			whitelisted[_users[i]][_id] = true;
		}
	}

	/**
	 * Removes an array of address from the whitelist for a specific token ID (see Option 2).
	 *
	 * Access: WHITELISTER_ROLE only accounts
	 *
	 * @param _users array of account addresses
	 * @param _id token ID
	 */
	function removeWhitelistBatch(address[] memory _users, uint256 _id) public nonReentrant {
		require(hasRole(WHITELISTER_ROLE, msg.sender), 'PGERC1155: WHITELISTER_ROLE required');
		for (uint256 i = 0; i < _users.length; i++) {
			whitelisted[_users[i]][_id] = false;
		}
	}

	/**
	 * Limits the supply of tokens with a specific ID to a given number.
	 * Already existing supply for a given token cannot be reduced!
	 *
	 * Access: MINTER_ROLE only accounts
	 *
	 * @param _id token ID
	 * @param supply new supply
	 */
	function setSupply(uint256 _id, uint256 supply) public nonReentrant {
		require(hasRole(MINTER_ROLE, msg.sender), 'PGERC1155: MINTER_ROLE required');
		require(
			supplies[_id] < supply,
			"PGERC1155: can't reduce supply because there might be minted tokens"
		);
		supplies[_id] = supply;
	}

	/**
	 * Limits the amont of tokens with a specific ID one user can mint.
	 *
	 * Access: MINTER_ROLE only accounts
	 *
	 * @param _id token ID
	 * @param newMaxAmount new Max Amount
	 */
	function setMaxMintAmount(uint256 _id, uint256 newMaxAmount) public nonReentrant {
		require(hasRole(MINTER_CAPPED_ROLE, msg.sender), 'PGERC1155: MINTER_CAPPED_ROLE required');
		require(
			newMaxAmount < supplies[_id],
			"PGERC1155: can't set maxAmount to be greater than the entire supply"
		);

		maxMintAmount[_id] = newMaxAmount;
	}

	/**
	 * Returns true if the sender provides an expanded signature (v, r, s) signed by a WHITELISTER_ROLE.
	 *
	 * Access: all accounts
	 *
	 * @param _add address that is part of the verified hash
	 * @param _id token ID that is part of the verified hash
	 * @param _v v signature parameter
	 * @param _r r signature parameter
	 * @param _s s signature parameter
	 */
	function validateAccessMessage(
		address _add,
		uint256 _id,
		uint8 _v,
		bytes32 _r,
		bytes32 _s
	) public view returns (bool) {
		bytes32 hash = keccak256(abi.encode(address(this), _add, _id));

		address signer = ecrecover(
			keccak256(abi.encodePacked('\x19Ethereum Signed Message:\n32', hash)),
			_v,
			_r,
			_s
		);
		return hasRole(WHITELISTER_ROLE, signer);
	}

	/**
	 * Returns the max supply for a given token ID.
	 *
	 * @param _id tokenID
	 */
	function getSupplies(uint256 _id) public view returns (uint256) {
		return supplies[_id];
	}

	/**
	 * Returns a users whitelisted status for a token ID.
	 *
	 * @param _add users address
	 * @param _id tokenID
	 */
	function getWhitelistedUser(address _add, uint256 _id) public view returns (bool) {
		return whitelisted[_add][_id];
	}

	/**
	 * @dev See {IERC1155-setApprovalForAll}.
	 *      In this example the added modifier ensures that the operator is allowed by the OperatorFilterRegistry.
	 */
	function setApprovalForAll(
		address operator,
		bool approved
	) public override onlyAllowedOperatorApproval(operator) {
		super.setApprovalForAll(operator, approved);
	}

	/**
	 * @dev See {IERC1155-safeTransferFrom}.
	 *      In this example the added modifier ensures that the operator is allowed by the OperatorFilterRegistry.
	 */
	function safeTransferFrom(
		address from,
		address to,
		uint256 tokenId,
		uint256 amount,
		bytes memory data
	) public override onlyAllowedOperator(from) {
		super.safeTransferFrom(from, to, tokenId, amount, data);
	}

	/**
	 * @dev See {IERC1155-safeBatchTransferFrom}.
	 *      In this example the added modifier ensures that the operator is allowed by the OperatorFilterRegistry.
	 */
	function safeBatchTransferFrom(
		address from,
		address to,
		uint256[] memory ids,
		uint256[] memory amounts,
		bytes memory data
	) public virtual override onlyAllowedOperator(from) {
		super.safeBatchTransferFrom(from, to, ids, amounts, data);
	}

	/**
	 * @dev Returns the address of the current owner.
	 */
	function owner() public view override(Ownable, UpdatableOperatorFilterer) returns (address) {
		return Ownable.owner();
	}
}
