    "contracts/Tokens/LockedToken.ERC721.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.17;

import '@openzeppelin/contracts-upgradeable/token/ERC721/extensions/ERC721EnumerableUpgradeable.sol';
import '@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol';
import '@openzeppelin/contracts-upgradeable/security/ReentrancyGuardUpgradeable.sol';
import '@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol';
import 'operator-filter-registry/src/upgradeable/UpdatableOperatorFiltererUpgradeable.sol';

/**
 * ERC721 implementation with built-in custom token mint functionality
 *
 * The following mint strategies are supported:
 * normal mint => allows users to mint NFTs if they have the rights
 * super mint => allows user to mint super NFTs if they have the rights
 *
 * Access control list security pattern allows decoupling of authorization based on a set
 * of predefined roles that are inividually assignable to accounts.
 */

contract LockedTokenERC721 is
	UpdatableOperatorFiltererUpgradeable,
	ERC721EnumerableUpgradeable,
	AccessControlUpgradeable,
	OwnableUpgradeable,
	ReentrancyGuardUpgradeable
{
	using StringsUpgradeable for uint256;

	// Emitted when user calls force unlock on a token id
	event ForceUnlocked(uint256 _tokenId, uint256 _timestamp, address _owner);

	// Emitted when a token is locked
	event TokenLocked(uint256 _tokenId, bool _state, address _owner);

	// Role required to use normal mint
	bytes32 public constant MINTER_ROLE = keccak256('MINTER_ROLE');

	// Role required to use super mint
	bytes32 public constant SUPER_MINTER_ROLE = keccak256('SUPER_MINTER_ROLE');

	// Role required to lock contract/tokens
	bytes32 public constant LOCK_ROLE = keccak256('LOCK_ROLE');

	// Prefix for tokens metadata URI
	string public baseURI;

	// Suffix for tokens metadata URIs
	string public baseExtension;

	// The paused status for minting NFTs
	bool public paused;

	// Bool to stop trading for all NFTS
	bool public isLockedForAll;

	// Maps tokens to a superId and transform them in a super NFT
	mapping(uint256 => uint256) public superTokenIds;

	// Counts the number of tokens minted for a specific superID
	mapping(uint256 => uint256) public superIdTokenCounter;

	// Determines which superId position a specific TokenId has
	mapping(uint256 => uint256) public tokenIdSuperPosition;

	// Token id to the timestamp their token will be unlocked through forceUnlock function
	mapping(uint256 => uint256) public forceUnlockCountdown;

	// Token ids locked status
	mapping(uint256 => bool) public lockedStatus;

	/// @custom:oz-upgrades-unsafe-allow constructor
	constructor() {
		_disableInitializers();
	}

	/**
	 * Instantiates the contract by assigning name, symbol and the initBaseURI
	 * The sender address gets all privileges.
	 */
	function initialize(
		string memory _name,
		string memory _symbol,
		string memory _initBaseURI
	) public reinitializer(2) {
		__UpdatableOperatorFiltererUpgradeable_init(
			0x000000000000AAeB6D7670E522A718067333cd4E,
			0x3cc6CddA760b79bAfa08dF41ECFA224f810dCeB6,
			true
		);
		__Ownable_init();
		__ReentrancyGuard_init();
		__ERC721_init(_name, _symbol);
		baseExtension = '.json';
		baseURI = _initBaseURI;
		_setupRole(DEFAULT_ADMIN_ROLE, msg.sender);
		_setupRole(MINTER_ROLE, msg.sender);
		_setupRole(SUPER_MINTER_ROLE, msg.sender);
		_setupRole(LOCK_ROLE, msg.sender);
	}

	/**
	 * Mints NFTs to an address
	 * Allows minting if the sender of the transaction has MINTER ROLE.
	 *
	 * Tokens are minted incrementally
	 *
	 * Access: accounts that have MINTER_ROLE
	 *
	 * @param _to address where tokens will be sent
	 * @param _mintAmount amount of tokens that are minted
	 */
	function mint(address _to, uint256 _mintAmount) public nonReentrant {
		require(hasRole(MINTER_ROLE, msg.sender), 'LockedTokenERC721: MINTER_ROLE required');

		require(!paused, 'LockedTokenERC721: contract on pause');

		uint256 supply = totalSupply();
		for (uint256 i = 1; i <= _mintAmount; i++) {
			_safeMint(_to, supply + i);
		}
	}

	/**
	 * Mints super NFTs to an address
	 * Allows minting if the sender of the transaction has SUPER_MINTER_ROLE
	 *
	 * Tokens are minted incrementally
	 *
	 * Access: accounts that have SUPER_MINTER_ROLE
	 *
	 * @param _to address where tokens will be sent
	 * @param _mintAmount amount of tokens that are minted
	 * @param _superId id of the super NFT - dictates the type/property of the super NFT
	 */
	function superMint(address _to, uint256 _mintAmount, uint256 _superId) public nonReentrant {
		require(
			hasRole(SUPER_MINTER_ROLE, msg.sender),
			'LockedTokenERC721: SUPER_MINTER_ROLE required'
		);
		require(!paused, 'LockedTokenERC721: contract on pause');

		uint256 supply = totalSupply();
		for (uint256 i = 1; i <= _mintAmount; i++) {
			superTokenIds[supply + i] = _superId;
			superIdTokenCounter[_superId] = superIdTokenCounter[_superId] + 1;
			tokenIdSuperPosition[supply + i] = superIdTokenCounter[_superId];

			_safeMint(_to, supply + i);
		}
	}

	/**
	 * Returns the token ids of an address
	 *
	 * @param _owner address to get ids for
	 */
	function walletOfOwner(address _owner) public view returns (uint256[] memory) {
		uint256 ownerTokenCount = balanceOf(_owner);
		uint256[] memory tokenIds = new uint256[](ownerTokenCount);
		for (uint256 i; i < ownerTokenCount; i++) {
			tokenIds[i] = tokenOfOwnerByIndex(_owner, i);
		}
		return tokenIds;
	}

	/**
	 * Returns the complete metadata URI for a token id
	 */
	function tokenURI(uint256 tokenId) public view virtual override returns (string memory) {
		require(_exists(tokenId), 'ERC721Metadata: URI query for nonexistent token');

		return
			bytes(baseURI).length > 0
				? string(abi.encodePacked(baseURI, tokenId.toString(), baseExtension))
				: '';
	}

	/**
	 * Changes the base URI for token metadata
	 *
	 * Access: only accounts with DEFAULT_ADMIN_ROLE
	 *
	 * @param _newBaseURI new value
	 */
	function setBaseURI(string memory _newBaseURI) public onlyRole(DEFAULT_ADMIN_ROLE) {
		baseURI = _newBaseURI;
	}

	/**
	 * Changes the base extension for token metadata
	 *
	 * Access: only accounts with DEFAULT_ADMIN_ROLE
	 *
	 * @param _newBaseExtension new value
	 */
	function setBaseExtension(string memory _newBaseExtension) public onlyRole(DEFAULT_ADMIN_ROLE) {
		baseExtension = _newBaseExtension;
	}

	/**
	 * Set contract to pause minting
	 *
	 * Access: only accounts with DEFAULT_ADMIN_ROLE
	 *
	 * @param _state operator set the contract on pause
	 */
	function setPause(bool _state) public onlyRole(DEFAULT_ADMIN_ROLE) {
		paused = _state;
	}

	/**
	 * Allows the owner of a token to unlock their token id after a day
	 * This is to be used if there are problems with off-chain verification
	 * or if users prefer to not use off-chain verification to unlock their tokens
	 *
	 * @param _tokenId to force unlock within a day
	 */
	function forceUnlock(uint256 _tokenId) public {
		require(lockedStatus[_tokenId], 'LockedTokenERC721 Token not locked');
		require(ownerOf(_tokenId) == msg.sender, 'LockedTokenERC721: Owner not caller');
		forceUnlockCountdown[_tokenId] = block.timestamp + 1 days;
		emit ForceUnlocked(_tokenId, forceUnlockCountdown[_tokenId], msg.sender);
	}

	/**
	 * Allows a user to lock their own token
	 *
	 * Access: only token holder
	 *
	 * @param _tokenId to be locked
	 */
	function lockToken(uint256 _tokenId) public {
		require(
			!lockedStatus[_tokenId] || forceUnlockCountdown[_tokenId] != 0,
			'LockedTokenERC721 Token is already locked'
		);
		require(ownerOf(_tokenId) == msg.sender, 'LockedTokenERC721: Owner not caller');
		lockedStatus[_tokenId] = true;
		forceUnlockCountdown[_tokenId] = 0;
		emit TokenLocked(_tokenId, true, msg.sender);
	}

	/**
	 * Sets the locked status of a token id to true or false
	 *
	 * Access: only addresses with LOCK_ROLE
	 *
	 * @param _tokenId to be locked or unlocked
	 * @param _state to set the token to
	 */
	function setLockedToken(uint256 _tokenId, bool _state) public onlyRole(LOCK_ROLE) {
		lockedStatus[_tokenId] = _state;
		forceUnlockCountdown[_tokenId] = 0;
		emit TokenLocked(_tokenId, _state, ownerOf(_tokenId));
	}

	/*
	 * @dev Sets the lock state for all users
	 *
	 * @param _lockedForAll Whether all users are locked out
	 * @notice Only the `LOCK_ROLE` role can call this function
	 */
	function setLockedForAll(bool _lockedForAll) public onlyRole(LOCK_ROLE) {
		isLockedForAll = _lockedForAll;
	}

	/**
	 * Requires the contract to not be locked to send tokens
	 * Requires a token id to be unlocked to send to another address
	 *
	 * Access: internal
	 *
	 * @param from address of sender
	 * @param to address of receiver
	 * @param tokenId to be transferred
	 */
	function _beforeTokenTransfer(
		address from,
		address to,
		uint256 tokenId,
		uint256 batchSize
	) internal override {
		super._beforeTokenTransfer(from, to, tokenId, batchSize);
		require(isLockedForAll == false, 'LockedTokenERC721: Contract is locked for all');

		if (forceUnlockCountdown[tokenId] > 0 && forceUnlockCountdown[tokenId] < block.timestamp) {
			lockedStatus[tokenId] = false;
			forceUnlockCountdown[tokenId] = 0;
		}

		require(
			lockedStatus[tokenId] == false,
			'LockedTokenERC721: Token is still locked by at least one contract'
		);
	}

	/**
	 * @dev See {IERC165-supportsInterface}.
	 */
	function supportsInterface(
		bytes4 interfaceId
	)
		public
		view
		virtual
		override(ERC721EnumerableUpgradeable, AccessControlUpgradeable)
		returns (bool)
	{
		return
			ERC721Upgradeable.supportsInterface(interfaceId) ||
			interfaceId == type(AccessControlUpgradeable).interfaceId ||
			super.supportsInterface(interfaceId);
	}

	/**
	 * @dev Returns the address of the current owner.
	 */
	function owner()
		public
		view
		virtual
		override(UpdatableOperatorFiltererUpgradeable, OwnableUpgradeable)
		returns (address)
	{
		return super.owner();
	}
}
"
    },
    "operator-filter-registry/src/IOperatorFilterRegistry.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.13;

interface IOperatorFilterRegistry {
    /**
     * @notice Returns true if operator is not filtered for a given token, either by address or codeHash. Also returns
     *         true if supplied registrant address is not registered.
     */
    function isOperatorAllowed(address registrant, address operator) external view returns (bool);

    /**
     * @notice Registers an address with the registry. May be called by address itself or by EIP-173 owner.
     */
    function register(address registrant) external;

    /**
     * @notice Registers an address with the registry and "subscribes" to another address's filtered operators and codeHashes.
     */
    function registerAndSubscribe(address registrant, address subscription) external;

    /**
     * @notice Registers an address with the registry and copies the filtered operators and codeHashes from another
     *         address without subscribing.
     */
    function registerAndCopyEntries(address registrant, address registrantToCopy) external;

    /**
     * @notice Unregisters an address with the registry and removes its subscription. May be called by address itself or by EIP-173 owner.
     *         Note that this does not remove any filtered addresses or codeHashes.
     *         Also note that any subscriptions to this registrant will still be active and follow the existing filtered addresses and codehashes.
     */
    function unregister(address addr) external;

    /**
     * @notice Update an operator address for a registered address - when filtered is true, the operator is filtered.
     */
    function updateOperator(address registrant, address operator, bool filtered) external;

    /**
     * @notice Update multiple operators for a registered address - when filtered is true, the operators will be filtered. Reverts on duplicates.
     */
    function updateOperators(address registrant, address[] calldata operators, bool filtered) external;

    /**
     * @notice Update a codeHash for a registered address - when filtered is true, the codeHash is filtered.
     */
    function updateCodeHash(address registrant, bytes32 codehash, bool filtered) external;

    /**
     * @notice Update multiple codeHashes for a registered address - when filtered is true, the codeHashes will be filtered. Reverts on duplicates.
     */
    function updateCodeHashes(address registrant, bytes32[] calldata codeHashes, bool filtered) external;

    /**
     * @notice Subscribe an address to another registrant's filtered operators and codeHashes. Will remove previous
     *         subscription if present.
     *         Note that accounts with subscriptions may go on to subscribe to other accounts - in this case,
     *         subscriptions will not be forwarded. Instead the former subscription's existing entries will still be
     *         used.
     */
    function subscribe(address registrant, address registrantToSubscribe) external;

    /**
     * @notice Unsubscribe an address from its current subscribed registrant, and optionally copy its filtered operators and codeHashes.
     */
    function unsubscribe(address registrant, bool copyExistingEntries) external;

    /**
     * @notice Get the subscription address of a given registrant, if any.
     */
    function subscriptionOf(address addr) external returns (address registrant);

    /**
     * @notice Get the set of addresses subscribed to a given registrant.
     *         Note that order is not guaranteed as updates are made.
     */
    function subscribers(address registrant) external returns (address[] memory);

    /**
     * @notice Get the subscriber at a given index in the set of addresses subscribed to a given registrant.
     *         Note that order is not guaranteed as updates are made.
     */
    function subscriberAt(address registrant, uint256 index) external returns (address);

    /**
     * @notice Copy filtered operators and codeHashes from a different registrantToCopy to addr.
     */
    function copyEntriesOf(address registrant, address registrantToCopy) external;

    /**
     * @notice Returns true if operator is filtered by a given address or its subscription.
     */
    function isOperatorFiltered(address registrant, address operator) external returns (bool);

    /**
     * @notice Returns true if the hash of an address's code is filtered by a given address or its subscription.
     */
    function isCodeHashOfFiltered(address registrant, address operatorWithCode) external returns (bool);

    /**
     * @notice Returns true if a codeHash is filtered by a given address or its subscription.
     */
    function isCodeHashFiltered(address registrant, bytes32 codeHash) external returns (bool);

    /**
     * @notice Returns a list of filtered operators for a given address or its subscription.
     */
    function filteredOperators(address addr) external returns (address[] memory);

    /**
     * @notice Returns the set of filtered codeHashes for a given address or its subscription.
     *         Note that order is not guaranteed as updates are made.
     */
    function filteredCodeHashes(address addr) external returns (bytes32[] memory);

    /**
     * @notice Returns the filtered operator at the given index of the set of filtered operators for a given address or
     *         its subscription.
     *         Note that order is not guaranteed as updates are made.
     */
    function filteredOperatorAt(address registrant, uint256 index) external returns (address);

    /**
     * @notice Returns the filtered codeHash at the given index of the list of filtered codeHashes for a given address or
     *         its subscription.
     *         Note that order is not guaranteed as updates are made.
     */
    function filteredCodeHashAt(address registrant, uint256 index) external returns (bytes32);

    /**
     * @notice Returns true if an address has registered
     */
    function isRegistered(address addr) external returns (bool);

    /**
     * @dev Convenience method to compute the code hash of an arbitrary contract
     */
    function codeHashOf(address addr) external returns (bytes32);
}
"
