      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import "./libs/ECDSA.sol";
import "./libs/draft-EIP712.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";

/*
  @dev
  Based on MinimalForwarder: https://github.com/OpenZeppelin/openzeppelin-contracts/blob/master/contracts/metatx/MinimalForwarder.sol
*/
contract TransactionForwarder is EIP712, AccessControl {
    using ECDSA for bytes32;

    struct ForwardRequest {
        address from;
        address to;
        uint256 value;
        bytes data;
    }

    bytes32 private constant _TYPEHASH = keccak256("ForwardRequest(address from,address to,uint256 value,bytes data)");
    mapping(address => bool) private _trustedRelayers;

    constructor() EIP712("TransactionForwarder", "0.0.1") {
        _setupRole(DEFAULT_ADMIN_ROLE, msg.sender);
    }

    function isTrustedRelayer(address relayer) public view virtual returns (bool) {
        return _trustedRelayers[relayer];
    }

    function addTrustedRelayer(address relayer) external onlyRole(DEFAULT_ADMIN_ROLE) {
        _trustedRelayers[relayer] = true;
    }

    function removeTrustedRelayer(address relayer) external onlyRole(DEFAULT_ADMIN_ROLE) {
        delete(_trustedRelayers[relayer]);
    }

    function verify(ForwardRequest calldata req, bytes calldata signature) public view returns (bool) {
        address signer = _hashTypedDataV4(
            keccak256(abi.encode(_TYPEHASH, req.from, req.to, req.value, keccak256(req.data)))
        ).recover(signature);
        return signer == req.from;
    }

    function execute(ForwardRequest calldata req, bytes calldata signature) public returns (bool, bytes memory) {
        require(isTrustedRelayer(msg.sender), "Caller is not a trusted relayer");
        require(req.value == 0, "value has to be 0");
        require(verify(req, signature), "TransactionForwarder: signature does not match request");
        return forward(req);
    }

    function executeCombined(
        ForwardRequest calldata firstReq,
        bytes calldata firstSignature,
        ForwardRequest calldata secondReq,
        bytes calldata secondSignature
    ) public returns (bool, bytes memory, bool, bytes memory)  {
        require(isTrustedRelayer(msg.sender), "Caller is not a trusted relayer");
        require(firstReq.value == 0, "First req value has to be 0");
        require(secondReq.value == 0, "second req value has to be 0");

        require(firstReq.from == secondReq.from, "Both requests need to be from the same person");

        require(verify(firstReq, firstSignature), "TransactionForwarder: first signature does not match first request");
        require(verify(secondReq, secondSignature), "TransactionForwarder: second signature does not match second request");

        (bool firstSuccess, bytes memory firstReturnData) = forward(firstReq);
        require(firstSuccess, "First transaction failed");

        (bool secondSuccess, bytes memory secondReturnData) = forward(secondReq);
        require(secondSuccess, "Second transaction failed");

        return (firstSuccess, firstReturnData, secondSuccess, secondReturnData);
    }

    function forward(ForwardRequest calldata req) private returns (bool, bytes memory) {
        (bool success, bytes memory returndata) = req.to.call{value : req.value}(
            abi.encodePacked(req.data, req.from)
        );
        return (success, returndata);
    }
}
