      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/token/ERC721/utils/ERC721Holder.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "./FractionalToken.sol";

contract TokenVault is FractionalToken, ERC721Holder, Ownable, ReentrancyGuard {
    /// @notice The ERC721 token address of the fractional NFT.
    address public collection;

    /// @notice The ERC721 token ID of the fractional NFT.
    uint256 public tokenId;

    /// @notice The address who initially deposited the NFT.
    address public curator;

    /// @notice The address of virtual account.
    address public virtualAccount;

    /// @notice Emitted when an NFT is transferred to the token vault NFT contract.
    /// @param sender The address that sent the NFT.
    event DepositedERC721(address indexed sender);

    /// @notice Emitted when a user successfully fractionalizes an NFT and receives the total supply of the newly created ERC20 token.
    /// @param collection The address of the newly fractionalized NFT.
    /// @param tokenId The contract address of the newly created ERC20 token.
    event Fractionalized(address indexed collection, uint256 indexed tokenId);
    /// @notice Emitted when a user minted ERC20 tokens manually after transferring ERC721
    /// @param supply The total supply amount of vault ERC20 tokens
    event Minted(uint256 indexed supply);

    /// @notice Emitted when a user successfully purchase some amount of NFT fractions.
    /// @param buyer The buyer of the fractions.
    /// @param amount The amount of bought fractions.
    event Purchased(address buyer, uint256 amount);

    /// @notice Emitted when a user finalize sale.
    event Burned(address sender, uint256 amount);

    /// @notice Emitted when the curator change curator account address.
    event UpdateCurator(address indexed curator);

    /// @notice Emitted when the curator successfully withdraw ERC20 supply from the vault in case the sale has not happened.
    event WithdrawERC20(address indexed token, address indexed receiver);
    /// @notice Emitted when the curator successfully withdraw ERC721 token from the vault in case the sale has not happened.
    event WithdrawERC721(address indexed collection, uint256 indexed tokenId, address indexed receiver);

    /// @notice Emitted when the owner set virtual account
    event SetVirtualAccount(address indexed virtualAccount);

    /// Set to the zero address
    error SetToZeroAddress();
    /// Sender should be curator
    error SenderIsNotCurator(address sender);
    /// Sender should be curator
    error SenderIsNotVirtualAccount(address sender);
    /// Virtual account should be set up
    error VirtualAccountIsNotSet();
    /// Exceeds the total supply
    error ExceedsTotalSupply(uint256 purchaseAmount);

    /// @dev Only curator can make a call
    modifier onlyCurator {
        if (_msgSender() != curator)
            revert SenderIsNotCurator({sender: _msgSender()});
        _;
    }

    /// @dev Only virtualAccount can male a call
    modifier onlyVirtualAccount {
        if (_msgSender() != virtualAccount)
            revert SenderIsNotVirtualAccount({sender: _msgSender()});
        _;
    }

    constructor(address _curator, string memory _name, string memory _symbol, uint8 _decimals) FractionalToken(_name, _symbol, _decimals) {
        if (_curator == address(0))
            revert SetToZeroAddress();
        curator = _curator;
    }

    /// @notice Create a fractionalized NFT: create a new ERC20 token, as specified;
    ///         and transfer the total supply of the token to the curator.
    /// @param _from The address of the curator/NFT owner.
    /// @param _collection The address of the NFT that is to be fractionalized.
    /// @param _tokenId The token ID of the NFT that is to be fractionalized.
    /// @param _supply The count of fractions of the fractionalized NFT - the total supply amount of vault ERC20 tokens.
    /// @dev Note the NFT must be approved for transfer by the owner of NFT token ID.
    function fractionalize(
        address _from,
        address _collection,
        uint256 _tokenId,
        uint256 _supply
    ) public onlyOwner {
        collection = _collection;
        tokenId = _tokenId;

        IERC721(collection).safeTransferFrom(_from, address(this), _tokenId);
        _mint(address(this), _supply);

        emit Fractionalized(collection, tokenId);
    }

    /// @notice Mint ERC20 tokens after receiving ERC721
    /// @param _supply The count of fractions of the fractionalized NFT - the total supply amount of vault ERC20 tokens.
    function mint(uint256 _supply) public onlyOwner {
        _mint(address(this), _supply);

        emit Minted(_supply);
    }

    /// @dev Set virtual account.
    function setVirtualAccount(address _virtualAccount) external onlyOwner {
        if (_virtualAccount == address(0))
            revert SetToZeroAddress();
        virtualAccount = _virtualAccount;

        emit SetVirtualAccount(_virtualAccount);
    }

    /// @notice Allow curator to update the curator address.
    /// @param _curator new curator address.
    function updateCurator(address _curator) external onlyCurator {
        if (_curator == address(0))
            revert SetToZeroAddress();
        curator = _curator;
        emit UpdateCurator(_curator);
    }

    /// @notice Airdrop vault fractions.
    /// @param _amount The quantity of vault fractions.
    function airdrop(uint256 _amount) external onlyVirtualAccount {
        _purchase(curator, _amount);
    }

    /// @notice Withdraw ERC20 token from this contract into _receiver wallet.
    function withdrawERC20(address _token, address _receiver) external onlyCurator {
        SafeERC20.safeTransfer(IERC20(_token), _receiver, IERC20(_token).balanceOf(address(this)));
        emit WithdrawERC20(_token, _receiver);
    }

    /// @notice Withdraw an ERC721 token from this contract into _receiver wallet in case the primary sale has not happened or emergency.
    function withdrawERC721(address _collection, uint256 _tokenId, address _receiver) external onlyCurator {
        IERC721(_collection).safeTransferFrom(address(this), _receiver, _tokenId);
        emit WithdrawERC721(_collection, _tokenId, _receiver);
    }

    receive() external payable {
        revert();
    }

    fallback() external payable {
        revert();
    }

    /// @dev Validate available amount and transfer purchased tokens
    function _purchase(address _account, uint256 _amount) internal {
        uint256 _supply = balanceOf(address(this));
        if (_amount > _supply)
            revert ExceedsTotalSupply({purchaseAmount: _amount});
        _transfer(address(this), _account, _amount);

        emit Purchased(_msgSender(), _amount);
    }

    /// @dev Required to use safeTransferFrom() from OpenZeppelin's ERC721 contract (when transferring NFTs to this contract).
    function onERC721Received(address, address, uint256, bytes memory) public override returns (bytes4) {
        emit DepositedERC721(msg.sender);
        return
            bytes4(
                keccak256("onERC721Received(address,address,uint256,bytes)")
            );
    }
}
"
