pragma solidity ^0.8.9;

// Importing required libraries and contracts from OpenZeppelin and Chainlink







// SlotMachine contract definition
contract SlotMachine is ReentrancyGuard, Pausable, Ownable, VRFConsumerBaseV2 {

    // Events to log important actions
    event RequestSent(uint256 requestId, address player);
    event RequestFulfilled(uint256 requestId, address player, uint256[3] randomWords);
    event PlayerWin(uint256 requestId, address player, uint256 amount);
    event BonusWin(uint256 requestId, address player, uint256 amount);
    event EtherReceived(address sender, uint256 value);

    // Struct to track the status of random number requests
    struct RequestStatus {
        address player;
        bool fulfilled;
        bool exists;
        uint256[] randomWords;
    }

    // Using SafeMath library for safe arithmetic operations
    using SafeMath for uint256;

    mapping(uint256 => RequestStatus) public s_requests;

    // Chainlink config
    bytes32 private s_keyHash;
    uint64 private s_subscriptionId;
    uint16 private requestConfirmations;
    uint32 private callbackGasLimit;

    mapping(address => uint256) public credits;
    mapping(address => uint256[3]) public slotResults;
    mapping(address => uint256) public SPIN_IN_PROGRESS;

    uint256 public constant HOUSE_EDGE_PERCENT = 5;
    uint256 public constant CREDITS_COST = 10000000000000000;
    uint256 public constant SPIN_COST = 10; 
    uint256 public max_multiplier;
    uint256 public totalCredits;

    uint8[3] public bonusSequence;
    mapping(bytes32 => uint256) public winningSequences;

    VRFCoordinatorV2Interface COORDINATOR;

    constructor(
        address vrfCoordinator,
        bytes32 keyHash,
        uint64 subscriptionId,
        uint16 confirmations,
        uint32 gasLimit
    )
        VRFConsumerBaseV2(vrfCoordinator)
    {
        COORDINATOR = VRFCoordinatorV2Interface(vrfCoordinator);
        s_keyHash = keyHash;
        s_subscriptionId = subscriptionId;
        requestConfirmations = confirmations;
        callbackGasLimit = gasLimit;
    }

    // Function to buy credits using matic
    function buyCredits(address player) public payable whenNotPaused {
        uint256 purchasedCredits = msg.value.div(CREDITS_COST);
        credits[player] = credits[player].add(purchasedCredits);
        totalCredits = totalCredits.add(purchasedCredits);
    }

    // Function to withdraw credits and receive matic in return
    function withdrawCredits(uint256 amount) public nonReentrant {
        require(credits[msg.sender] >= amount, "Insufficient credits");
    
        uint256 withdrawalAmountInEther = amount.mul(CREDITS_COST);

        credits[msg.sender] = credits[msg.sender].sub(amount);

        totalCredits = totalCredits.sub(amount);
    
        payable(msg.sender).transfer(withdrawalAmountInEther);
    }

    // Function to spin the slot machine
    function spinSlots() public nonReentrant whenNotPaused returns (uint256 requestId) {
        require(credits[msg.sender] >= SPIN_COST, "Not enough credits for a spin");
        require(
            address(this).balance >= (SPIN_COST.mul(CREDITS_COST).mul(max_multiplier)).add(totalCredits.mul(CREDITS_COST)).sub(SPIN_COST.mul(CREDITS_COST)), 
            "Contract does not have enough funds to cover potential winnings and credit withdrawals");
        require(SPIN_IN_PROGRESS[msg.sender] == 0, "Already rolled");

        requestId = COORDINATOR.requestRandomWords(
            s_keyHash,
            s_subscriptionId,
            requestConfirmations,
            callbackGasLimit,
            3
        );

        s_requests[requestId] = RequestStatus({
            player: msg.sender,
            randomWords: new uint256[](0),
            exists: true,
            fulfilled: false
        });

        credits[msg.sender] = credits[msg.sender].sub(SPIN_COST);
        totalCredits = totalCredits.sub(SPIN_COST);

        SPIN_IN_PROGRESS[msg.sender] = 42;

        emit RequestSent(requestId, msg.sender);
        return requestId;
    }

    // Callback function to handle the random numbers received from Chainlink VRF
    function fulfillRandomWords(
        uint256 requestId,
        uint256[] memory randomWords
    ) internal override {
        require(s_requests[requestId].exists, "request not found");
        s_requests[requestId].fulfilled = true;
        s_requests[requestId].randomWords = randomWords;

        for (uint256 i = 0; i < 3; i++) {
          slotResults[s_requests[requestId].player][i] = (randomWords[i] % 11) + 1;
        }

        emit RequestFulfilled(requestId, s_requests[requestId].player, slotResults[s_requests[requestId].player]);

        if (
            slotResults[s_requests[requestId].player][0] == bonusSequence[0] &&
            slotResults[s_requests[requestId].player][1] == bonusSequence[1] &&
            slotResults[s_requests[requestId].player][2] == bonusSequence[2]
        ) {
            uint256 bonusCredits = getBonusAmount().div(CREDITS_COST);

            uint256 houseEdge = bonusCredits.mul(HOUSE_EDGE_PERCENT).div(100);
            
            uint256 adjustedPrize = bonusCredits.sub(houseEdge);

            totalCredits = totalCredits.add(adjustedPrize);

            credits[s_requests[requestId].player] = credits[s_requests[requestId].player].add(adjustedPrize);

            emit BonusWin(requestId, s_requests[requestId].player, bonusCredits);

            SPIN_IN_PROGRESS[s_requests[requestId].player] = 0;

            return;
        }

        uint256 winnings = getPrizeForSequence(slotResults[s_requests[requestId].player]);

        if (winnings > 0) {
            uint256 totalPrize = SPIN_COST.mul(winnings);
        
            uint256 houseEdge = totalPrize.mul(HOUSE_EDGE_PERCENT).div(100);
            
            uint256 adjustedPrize = totalPrize.sub(houseEdge);

            totalCredits = totalCredits.add(adjustedPrize);
        
            credits[s_requests[requestId].player] = credits[s_requests[requestId].player].add(adjustedPrize);

            emit PlayerWin(requestId, s_requests[requestId].player, totalPrize);
        }

        SPIN_IN_PROGRESS[s_requests[requestId].player] = 0;
    }

    // Function to get the prize amount for a given sequence
    function getPrizeForSequence(uint256[3] memory sequence) public view returns (uint256) {
        bytes32 keyHash = keccak256(abi.encodePacked(sequence));
        return winningSequences[keyHash];
    }

    // Function to add new winning sequences and their associated prizes
    function addWinningSequences(uint8[][] memory keys, uint256[] memory values) public onlyOwner {
        require(keys.length == values.length, "Keys and values arrays must have the same length");

        for (uint256 i = 0; i < keys.length; i++) {
            bytes32 keyHash = keccak256(abi.encodePacked(keys[i]));
            winningSequences[keyHash] = values[i];

            if (values[i] > max_multiplier) {
                max_multiplier = values[i];
            }
        }
    }

    function setBonusSequence(uint8[3] memory sequence) public onlyOwner {
        bonusSequence = sequence;
    }


    function pause() public onlyOwner {
        _pause();
    }

    function unpause() public onlyOwner {
        _unpause();
    }

    function ownerWithdraw(uint256 amount) public onlyOwner {
        uint256 availableForWithdrawal = getBonusAmount();
    
        require(amount <= availableForWithdrawal, "Requested amount exceeds available funds after reserving for solvency");
    
        payable(owner()).transfer(amount);
    }

    function getBonusAmount() public view returns (uint256) {
        uint256 creditsReserve = totalCredits.mul(CREDITS_COST);
    
        uint256 prizeReserve = SPIN_COST.mul(CREDITS_COST).mul(max_multiplier);
    
        uint256 totalReserve = creditsReserve.add(prizeReserve);
    
        return address(this).balance.sub(totalReserve);
    }

    receive() external payable {
        emit EtherReceived(msg.sender, msg.value);
    }
}
