import {
    ERC1155Upgradeable,
    IERC1155Upgradeable
} from "openzeppelin-contracts-upgradeable/token/ERC1155/ERC1155Upgradeable.sol";
import {
    ERC2981Upgradeable,
    IERC2981Upgradeable
} from "openzeppelin-contracts-upgradeable/token/common/ERC2981Upgradeable.sol";
import {IERC165Upgradeable} from "openzeppelin-contracts-upgradeable/interfaces/IERC165Upgradeable.sol";
import {OwnableUpgradeable} from 'openzeppelin-contracts-upgradeable/access/OwnableUpgradeable.sol';

contract DTTD1155Airdrop is ERC1155Upgradeable, OwnableUpgradeable, ERC2981Upgradeable {
    struct Token {
        string uri; // uri for metadata
        uint256 maxSupply;
        uint256 totalSupply; // current supply
    }

    string public name;
    string public symbol;

    uint256 public numberOfTokens;
    mapping(uint256 => Token) public tokens;
    mapping(bytes32 => bool) public tidUsed;
    mapping(bytes32 => bool) public claimed;

    IERC2981Upgradeable public royaltyProvider;

    mapping(address => bool) public preapprovedOperators;
    mapping(address => bool) public changedPreapprovedOperatorApproval;

    constructor() {
        _disableInitializers();
    }

    function initialize(
        string memory _name,
        string memory _symbol,
        string memory _uri,
        address _owner,
        address _preapprovedOperator,
        uint256 , // unused reserve1
        uint256  // unused reserve2
    ) initializer
      external 
    {
        name = _name;
        symbol = _symbol;

        __ERC1155_init(_uri);
        __Ownable_init();
        preapprovedOperators[_preapprovedOperator] = true;

        transferOwnership(_owner);
    }

    function supportsInterface(
        bytes4 _interfaceId
    ) public view override(ERC1155Upgradeable, ERC2981Upgradeable) returns (bool) {
        return super.supportsInterface(_interfaceId);
    }

    // *********************** //
    // *** MODIFIER CHECKS *** //
    // *********************** //

    modifier onlyUnusedTid(bytes32 _tid) {
        require (!tidUsed[_tid], "Already minted: tid");
        tidUsed[_tid] = true;
        _;
    }

    modifier onlyMatchingLengths(uint256 _l1, uint256 _l2) {
        require(_l1 == _l2, "Invalid length");
        _;
    }

    modifier onlyValidToken(uint256 _id) {
        require(_id < numberOfTokens, "Invalid ID");
        _;
    }

    // ******************* //
    // *** TOKEN SETUP *** //
    // ******************* //

    function setupToken(uint256 _tokenMaxSupply, string memory _uri) public onlyOwner {
        require(_tokenMaxSupply > 0, "Missing max supply");
        require(bytes(_uri).length > 0, "Missing URI");

        Token memory token = Token({
            maxSupply: _tokenMaxSupply,
            totalSupply: 0,
            uri: _uri
        });

        tokens[numberOfTokens] = token;
        ++numberOfTokens;
    }

    function setTokenURI(uint256 _id, string memory _uri) external onlyOwner onlyValidToken(_id) {
        require(bytes(_uri).length > 0, "Missing URI");

        Token storage token = tokens[_id];
        token.uri = _uri;
        emit URI(_uri, _id);
    }

    // ********************* //
    // *** TOKEN AIRDROP *** //
    // ********************* //

    function airdropToken(
        bytes32 _tid,
        bytes32 _pid,
        address _to,
        uint256 _id
    ) external onlyOwner onlyUnusedTid(_tid) onlyValidToken(_id) {
        bytes32 claimHash = keccak256(abi.encode(_pid, _id));
        require(claimed[claimHash] == false, "Already claimed: pid-id");

        Token memory token = tokens[_id];
        uint256 tokenMaxSupply = token.maxSupply;
        uint256 tokenTotalSupply = token.totalSupply;
        require (tokenTotalSupply < tokenMaxSupply, "Insufficient remaining supply");

        claimed[claimHash] = true;

        _mint(_to, _id, 1, "");
    }

    function airdropTokens(
        bytes32 _tid,
        address _to,
        uint256[] memory _ids,
        uint256[] memory _amounts
    ) external onlyOwner onlyUnusedTid(_tid) onlyMatchingLengths(_ids.length, _amounts.length) {
        // loop through every token to check token validity and supply
        for (uint256 i = 0; i < _ids.length; i++) {
            uint256 tokenId = _ids[i];
            uint256 amount = _amounts[i];

            require(tokenId < numberOfTokens, "Invalid ID");
            require(amount > 0, "Zero amount");

            Token memory token = tokens[tokenId];
            require (token.totalSupply + amount <= token.maxSupply, "Insufficient remaining supply");
        }
        _mintBatch(_to, _ids, _amounts, "");
    }

    // ******************************* //
    // *** TOKEN SETUP AND AIRDROP *** //
    // ******************************* //

    function setupAndAirdropTokens(
        bytes32 _tid,
        uint256 _tokenMaxSupply,
        string memory _uri,
        address[] memory _to,
        uint256[] memory _amounts
    ) external onlyOwner onlyUnusedTid(_tid) onlyMatchingLengths(_to.length, _amounts.length) {
        uint256 newTokenId = numberOfTokens;
        setupToken(_tokenMaxSupply, _uri);

        uint256 numberOfRecipients = _to.length;
        for (uint256 i = 0; i < numberOfRecipients; i++) {
            address to = _to[i];
            uint256 amount = _amounts[i];

            require(amount > 0, "Zero amount");

            Token memory token = tokens[newTokenId];
            require (token.totalSupply + amount <= _tokenMaxSupply, "Insufficient remaining supply");
            _mint(to, newTokenId, amount, "");
        }
    }

    // ********************* //
    // *** TOKEN GETTERS *** //
    // ********************* //

    function uri(uint256 _id) public view override returns (string memory) {
        Token memory token = tokens[_id];
        return token.uri;
    }

    function maxSupply(uint256 _id) public view returns (uint256) {
        Token memory token = tokens[_id];
        return token.maxSupply;
    }

    function totalSupply(uint256 _id) public view returns (uint256) {
        Token memory token = tokens[_id];
        return token.totalSupply;
    }

    // Override for adjusting token total supply
    function _beforeTokenTransfer(
        address _operator,
        address _from,
        address _to,
        uint256[] memory _ids,
        uint256[] memory _amounts,
        bytes memory _data
    ) internal virtual override {
        super._beforeTokenTransfer(_operator, _from, _to, _ids, _amounts, _data);
        if (_from == address(0)) {
            for (uint256 i = 0; i < _ids.length; ++i) {
                Token storage token = tokens[_ids[i]];
                token.totalSupply += _amounts[i];
            }
        }
    }

    // ******************** //
    // *** PRE-APPROVAL *** //
    // ******************** //

    function addPreapprovedOperator(address _operator) external onlyOwner {
        preapprovedOperators[_operator] = true;
    }

    function removePreapprovedOperator(address _operator) external onlyOwner {
        preapprovedOperators[_operator] = false;
    }

    function setApprovalForAll(address _operator, bool _approved) public virtual override {
        bool isPreapprovedOperator = preapprovedOperators[_operator];
        if (isPreapprovedOperator) {
            // if the operator is pre-approved, then mark it as having manually changed
            bool didChangePreapprovedOperatorApproval = changedPreapprovedOperatorApproval[msg.sender];
            if (!didChangePreapprovedOperatorApproval) changedPreapprovedOperatorApproval[msg.sender] = true;
        }
        return super.setApprovalForAll(_operator, _approved);
    }

    function isApprovedForAll(address _owner, address _operator) public view virtual override returns (bool) {
        bool isPreapprovedOperator = preapprovedOperators[_operator];
        if (!isPreapprovedOperator) {
            // if the operator is not pre-approved, then return super result
            return super.isApprovedForAll(_owner, _operator);
        }
        // otherwise, return true if the owner has not manually changed their approval
        bool didChangePreapprovedOperatorApproval = changedPreapprovedOperatorApproval[_owner];
        return (!didChangePreapprovedOperatorApproval) || super.isApprovedForAll(_owner, _operator);
    }

    // ************************** //
    // *** ROYALTIES EIP-2981 *** //
    // ************************** //

    function royaltyInfo(uint256 _id, uint256 _salePrice)
        public
        view
        override
        returns (address, uint256)
    {
        if (address(royaltyProvider) != address(0)) {
            return royaltyProvider.royaltyInfo(_id, _salePrice);
        }
        return super.royaltyInfo(_id, _salePrice);
    }

    function setRoyaltyProvider(IERC2981Upgradeable _royaltyProvider) external onlyOwner {
        royaltyProvider = _royaltyProvider;
    }

    function setDefaultRoyalty(address _royaltyReceiver, uint96 _royaltyBasisPoints) external onlyOwner {
        _setDefaultRoyalty(_royaltyReceiver, _royaltyBasisPoints);
    }

    // ************************ //
    // *** REFRESH METADATA *** //
    // ************************ //

    function emitURIEvent(uint256 _id) public onlyOwner onlyValidToken(_id) {
        emit URI(tokens[_id].uri, _id);
    }

    function emitURIEvent(uint256 _start, uint256 _end) public onlyOwner onlyValidToken(_end) {
        require(_start < _end, "Invalid IDs");
        for (uint256 i = _start; i <= _end; i++) {
            emit URI(tokens[i].uri, i);
        }
    }

}
