    "contracts/v2/PoolManagerV2.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.10;

import "../v1/utils/Ownable.sol";
import "../v1/interfaces/IERC20.sol";
import "../v1/libraries/TransferHelper.sol";
import "./interfaces/IPoolManagerV2.sol";
import "./interfaces/IGeneralPoolV2.sol";
import "./interfaces/IFixedCryptoPoolV2.sol";
import "@openzeppelin/contracts/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts/proxy/Clones.sol";
import "@openzeppelin/contracts/token/ERC1155/extensions/ERC1155Supply.sol";

contract PoolManagerV2 is
    IPoolManagerV2,
    Ownable,
    ERC1155Supply,
    Initializable
{
    address public immutable override collateralToken;
    address public override generalPoolTemplate;
    address public override fixedCryptoPoolTemplate;

    uint16 public override tradeFeeRate; // 1 means 1/10000
    uint8 public override tradeFactor; // less than 100

    mapping(address => bool) public override isBuilder;
    mapping(address => bool) public override isKeeper;

    mapping(bytes32 => address) private _pools;
    mapping(address => uint8) private _poolType; // 1: GeneralPool; 2: FixedCryptoPool

    /// @dev The ID of the next token that will be minted. Skips 0
    uint176 private _nextId = 1;

    modifier onlyBuilder() {
        require(isBuilder[msg.sender], "only builder");
        _;
    }

    modifier onlyKeeper() {
        require(isKeeper[msg.sender], "only keeper");
        _;
    }

    constructor(
        address collateralToken_,
        address generalPoolTemlate_,
        address fixedCryptoPoolTemplate_,
        uint16 tradeFeeRate_,
        uint8 tradeFactor_
    ) ERC1155("") {
        collateralToken = collateralToken_;
        generalPoolTemplate = generalPoolTemlate_;
        fixedCryptoPoolTemplate = fixedCryptoPoolTemplate_;
        tradeFeeRate = tradeFeeRate_;
        tradeFactor = tradeFactor_;
    }

    function getPool(
        bytes32 poolId
    ) public view override returns (address pool, uint8 poolType) {
        pool = _pools[poolId];
        poolType = _poolType[pool];
    }

    function updateGeneralPoolTemplate(
        address newTemplate
    ) external override onlyOwner {
        require(newTemplate != address(0), "zero address");
        generalPoolTemplate = newTemplate;
    }

    function updateFixedCryptoPoolTemplate(
        address newTemplate
    ) external override onlyOwner {
        require(newTemplate != address(0), "zero address");
        fixedCryptoPoolTemplate = newTemplate;
    }

    function updateTradeFeeRate(uint16 newFeeRate) external override onlyOwner {
        require(newFeeRate < 10000, "over 10000");
        tradeFeeRate = newFeeRate;
    }

    function updateTradeFactor(uint8 newFactor) external override onlyOwner {
        require(newFactor < 100, "over 100");
        tradeFactor = newFactor;
    }

    function setBuilder(
        address builder,
        bool state
    ) external override onlyOwner {
        require(builder != address(0), "zero address");
        isBuilder[builder] = state;
    }

    function setKeeper(
        address keeper,
        bool state
    ) external override onlyOwner {
        require(keeper != address(0), "zero address");
        isKeeper[keeper] = state;
    }

    function withdraw(
        address token,
        address to,
        uint256 amount
    ) external override onlyOwner {
        require(
            IERC20(token).balanceOf(address(this)) >= amount,
            "over balance"
        );
        TransferHelper.safeTransfer(token, to, amount);
    }

    function createAndInitializeGeneralPool(
        uint256 collateralAmount,
        uint256 reserve0,
        uint256 tradeStartTime,
        uint256 tradeEndTime,
        uint256 claimTime,
        string memory question,
        string[] memory options,
        string memory tag
    ) external override onlyBuilder returns (address pool) {
        require(options.length == 2, "wrong options length");

        bytes32 poolId = keccak256(
            abi.encodePacked(
                tradeStartTime,
                tradeEndTime,
                claimTime,
                question,
                options[0],
                options[1]
            )
        );
        require(_pools[poolId] == address(0), "pool exist");

        uint256 option0TokenId = _nextId;
        _nextId += 2;

        pool = Clones.clone(generalPoolTemplate);
        IGeneralPoolV2(pool).initialize(
            collateralToken,
            tradeFeeRate,
            tradeFactor,
            tradeStartTime,
            tradeEndTime,
            claimTime,
            option0TokenId,
            question,
            options
        );

        _pools[poolId] = pool;
        _poolType[pool] = 1;

        emit GeneralPoolCreated(
            poolId,
            pool,
            option0TokenId,
            tradeStartTime,
            tradeEndTime,
            claimTime,
            question,
            options,
            tag
        );

        require(
            IERC20((collateralToken)).balanceOf(address(this)) >=
                collateralAmount,
            "not enough balance"
        );
        TransferHelper.safeTransfer(collateralToken, pool, collateralAmount);
        IGeneralPoolV2(pool).addLiquidity(reserve0);
    }

    function updateClaimTimeForGeneral(
        bytes32 poolId,
        uint256 newClaimTime
    ) external override onlyBuilder {
        (address pool, uint8 poolType) = getPool(poolId);
        require(pool != address(0) && poolType == 1, "invalid pool");
        IGeneralPoolV2(pool).updateClaimTime(newClaimTime);
    }

    function submitResultForGeneral(
        bytes32 poolId,
        uint8 option,
        string calldata description
    ) external override onlyBuilder {
        (address pool, uint8 poolType) = getPool(poolId);
        require(pool != address(0) && poolType == 1, "invalid pool");
        IGeneralPoolV2(pool).submitResult(option, description);
    }

    function removeLiquidityForGeneral(bytes32 poolId) external override onlyBuilder {
        (address pool, uint8 poolType) = getPool(poolId);
        require(pool != address(0) && poolType == 1, "invalid pool");
        IGeneralPoolV2(pool).removeLiquidity();
    } 

    function createAndInitializeFixedCryptoPool(
        address priceOracle,
        uint256 initReserve,
        uint256 tradeStartTime,
        uint32 roundGap,
        uint32 tradeDuration,
        uint32 priceDuration,
        uint32 totalRounds,
        string memory tag
    ) external override onlyBuilder returns (address pool) {
        bytes32 poolId = keccak256(
            abi.encodePacked(
                priceOracle,
                initReserve,
                roundGap,
                tradeDuration,
                priceDuration
            )
        );
        require(_pools[poolId] == address(0), "pool exist");

        pool = Clones.clone(fixedCryptoPoolTemplate);
        IFixedCryptoPoolV2(pool).initialize(
            collateralToken,
            priceOracle,
            initReserve,
            roundGap,
            tradeDuration,
            priceDuration,
            totalRounds,
            tradeFeeRate,
            tradeFactor
        );

        _pools[poolId] = pool;
        _poolType[pool] = 2;

        emit FixedCryptoPoolCreated(
            poolId,
            pool,
            priceOracle,
            initReserve,
            roundGap,
            tradeDuration,
            priceDuration,
            totalRounds,
            tag
        );

        TransferHelper.safeTransfer(collateralToken, pool, initReserve);
        uint256 option0TokenId = _nextId;
        _nextId += 2;
        IFixedCryptoPoolV2(pool).startNewRound(tradeStartTime, option0TokenId);
    }

    function updateTotalRounds(bytes32 poolId, uint32 newTotalRounds) external override onlyBuilder {
        (address pool, uint8 poolType) = getPool(poolId);
        require(pool != address(0) && poolType == 2, "invalid pool");
        IFixedCryptoPoolV2(pool).updateTotalRounds(newTotalRounds);
    }

    function startNewRound(bytes32 poolId, uint256 tradeStartTime) external override onlyBuilder {
        (address pool, uint8 poolType) = getPool(poolId);
        require(pool != address(0) && poolType == 2, "invalid pool");
        IFixedCryptoPoolV2 fixedPool = IFixedCryptoPoolV2(pool);

        TransferHelper.safeTransfer(collateralToken, pool, fixedPool.initReserve());
        uint256 option0TokenId = _nextId;
        _nextId += 2;
        fixedPool.startNewRound(tradeStartTime, option0TokenId);
    }

    function setStartPrice(bytes32 poolId) external override onlyKeeper {
        (address pool, uint8 poolType) = getPool(poolId);
        require(pool != address(0) && poolType == 2, "invalid pool");
        IFixedCryptoPoolV2(pool).setStartPrice();
    }

    function endCurrentRound(bytes32 poolId) external override onlyKeeper {
        (address pool, uint8 poolType) = getPool(poolId);
        require(pool != address(0) && poolType == 2, "invalid pool");
        IFixedCryptoPoolV2(pool).endCurrentRound();
    }

    function endCurrentAndStartNewRound(bytes32 poolId) external override onlyKeeper {
        (address pool, uint8 poolType) = getPool(poolId);
        require(pool != address(0) && poolType == 2, "invalid pool");
        IFixedCryptoPoolV2 fixedPool = IFixedCryptoPoolV2(pool);
        fixedPool.endCurrentRound();

        // check if can start new round
        uint256 balanceOfThis = IERC20(collateralToken).balanceOf(address(this));
        uint256 initReserve = fixedPool.initReserve();
        uint256 currentRound = fixedPool.currentRound();
        uint256 totalRounds = fixedPool.totalRounds();
        uint32 roundGap = fixedPool.roundGap();
        ( , , , uint256 roundEndTime, , , , ) = fixedPool.getRoundData(currentRound);
        uint256 newRoundTradeStartTime = roundEndTime + roundGap;

        if (balanceOfThis >= initReserve && currentRound < totalRounds && newRoundTradeStartTime >= block.timestamp) {
            TransferHelper.safeTransfer(collateralToken, pool, initReserve);
            uint256 option0TokenId = _nextId;
            _nextId += 2;
            fixedPool.startNewRound(newRoundTradeStartTime, option0TokenId);
        }
    }

    function pause(bytes32 poolId) external override onlyBuilder {
        require(_pools[poolId] != address(0), "pool not found");
        IBasePoolV2(_pools[poolId]).pause();
        emit PoolPaused(poolId);
    }

    function unpause(bytes32 poolId) external override onlyBuilder {
        require(_pools[poolId] != address(0), "pool not found");
        IBasePoolV2(_pools[poolId]).unpause();
        emit PoolUnpaused(poolId);
    }

    function mint(
        address to,
        uint256 tokenId,
        uint256 shares
    ) external override {
        require(_poolType[msg.sender] > 0, "forbidden");
        _mint(to, tokenId, shares, "");
    }

    function burn(
        uint256 tokenId,
        uint256 shares
    ) external override {
        require(_poolType[msg.sender] > 0, "forbidden");
        _burn(msg.sender, tokenId, shares);
    }
}"
    }
  },
  "settings": {
    "optimizer": {
      "enabled": true,
      "runs": 10
    },
    "outputSelection": {
      "*": {
        "*": [
          "evm.bytecode",
          "evm.deployedBytecode",
          "devdoc",
          "userdoc",
          "metadata",
          "abi"
        ]
      }
    },
    "libraries": {}
  }
}}
