        IUniRouterV2 routerV2 = IUniRouterV2(quoterV2); //UniRouterV2
        address tokenOut = DaughterContract(getDaughterContract(tokenId)).getTokenAddress();
        address[] memory t0 = new address[](2);
        address[] memory t1 = new address[](2);
        t0[0] = tokenIn; 
        t0[1] = tokenUsdRef; 
        t1[0] = tokenUsdRef;
        t1[1] = tokenOut; 

        return maticValue[tokenId] * routerV2.getAmountsOut(routerV2.getAmountsOut(1e18, t0)[1], t1)[1]; //Matic-> USD-> Token (PAXG?) 2deci
