        public
        view
        returns (string memory)
    {
        require(_exists(tokenId), "nonexistent token");
        return svgArt[tokenId];
    }

    function transferTokenValue (uint256 tokenId)
        public
    {
        require(_isApprovedOrOwner(_msgSender(), tokenId), "ERC721: transfer caller is not owner nor approved");
        totalValue = totalValue - tokenValue[tokenId];
        tokenValue[tokenId] = 0;
        payable(msg.sender).transfer(tokenValue[tokenId]);
    }

    function getTokenValue (uint256 tokenId)
        public 
        view 
        returns (uint256) 
    {
        return tokenValue[tokenId];
    }

    function getDaughterContract (uint256 tokenId)
        public 
        view 
        returns (address) 
    {
        return daughterContract[tokenId];
    }

    function getDeadline (uint256 tokenId)
        public 
        view 
        returns (uint256) 
    {
        return deadline[tokenId];
    }

    function getTokenGold (uint256 tokenId)
        public 
        view 
        returns (uint256) 
    {
        return IERC20(DaughterContract(getDaughterContract(tokenId)).getTokenAddress()).balanceOf(getDaughterContract(tokenId));
    }

    function addressToString(address _addr) 
        internal 
        pure 
        returns(string memory) 
    {
        bytes32 value = bytes32(uint256(uint160(_addr)));
        bytes memory alphabet = "0123456789abcdef";

        bytes memory str = new bytes(42);
        str[0] = "0";
        str[1] = "x";
        for (uint i = 0; i < 20; i++) {
            str[2+i*2] = alphabet[uint(uint8(value[i + 12] >> 4))];
            str[3+i*2] = alphabet[uint(uint8(value[i + 12] & 0x0f))];
        }
        return string(str);
    }

