        parts[0] = "<?xml version='1.0' encoding='utf-8'?><svg viewBox='0 0 500 500' lang='en' xmlns='http://www.w3.org/2000/svg' xmlns:xlink='http://www.w3.org/1999/xlink' xmlns:bx='https://boxy-svg.com'><defs><style bx:fonts='Aclonica' bx:pinned='true'>@import url(https://fonts.googleapis.com/css2?family=Aclonica%3Aital%2Cwght%400%2C400&amp;display=swap);</style></defs><path d='M4.921 4.921h491.142v490.157H4.921z' style='fill:#453d3d;pointer-events:none'/><text style='fill:#eaf78b;font-family:Aclonica;font-size:58px;font-weight:700;white-space:pre' x='180' y='103.51'>Gold</text><text style='fill:#8247e5;font-family:Aclonica;font-size:14px;font-weight:700;white-space:pre' x='191.885' y='461.149'>iNFT | jOi-ai.com</text><text style='fill:#e6e3e3;font-family:Aclonica;font-size:10px;font-style:italic;text-anchor:middle;white-space:pre' x='253.937' y='369.647' dy='1.2em'>";
        parts[1] = string(getMessage(tokenId));
