        return string(abi.encodePacked(parts[0], parts[1], parts[2], parts[3], parts[4], parts[5], parts[6], parts[7], parts[8]));
    }
    
    function getSvg2(uint tokenId) 
        private 
        view 
        returns (string memory) 
    {
        string[8] memory parts;
        parts[0] = addressToString(getDaughterContract(tokenId));
        parts[1] = "</text><text style='fill:#eaf78b;font-family:Aclonica;font-size:12px;font-weight:700;white-space:pre' x='297.903' y='118.461'>";
        charged[tokenId] == true ? parts[2] = uint2str(getTokenGold(tokenId)*DaughterContract(getDaughterContract(tokenId)).getTokenFactor()/1e19) : parts[2] = uint2str(getGoldenValue(tokenId)*DaughterContract(getDaughterContract(tokenId)).getTokenFactor()/1e19);
        parts[3] = " mg</text><text style='fill:#453d3d;font-family:Aclonica;font-size:6px;font-weight:700;text-anchor:end;white-space:pre' x='470.868' y='420'> Polygon | Contract:";
        parts[4] = addressToString(address(this));
        parts[5] = "</text><image width='231.15' height='231.15' style='' x='144.518' y='133.836' href='";
        parts[6] = string(svgArt[tokenId]);
        parts[7] = "'/></svg>";
        return string(abi.encodePacked(parts[0], parts[1], parts[2], parts[3], parts[4], parts[5], parts[6], parts[7]));
    }

    function setBasePath_TokenIn (string memory path, address _tokenIn, address _quoterV2, address _tokenUsdRef)
        public
        onlyOwner
    {
        basePath = path;
        tokenIn = _tokenIn;
        quoterV2 = _quoterV2;
        tokenUsdRef = _tokenUsdRef;
    }

    function setBaseURI(uint256 tokenId, bool dataURI, bool gateway, bool imageDataURI) 
        public  
    {
        require(_isApprovedOrOwner(_msgSender(), tokenId), "ERC721: transfer caller is not owner nor approved");
        require(_exists(tokenId), "nonexistent token");
        _setBaseURI(tokenId, dataURI, gateway, imageDataURI);
    }

    function _setBaseURI(uint256 _tokenId, bool _dataURI, bool _gateway, bool _imageDataURI) 
        internal  
    {
        _dataURI == true ? baseURI[_tokenId] = 'data:application/json;base64,' : baseURI[_tokenId] = '';
        _gateway == true ? baseURI[_tokenId] = basePath : baseURI[_tokenId] = baseURI[_tokenId];
        _imageDataURI == true ? imageURI[_tokenId] = 'data:image/svg+xml;base64,' : imageURI[_tokenId] = '';
    }

    function tokenURI(uint256 tokenId) 
        override(ERC721) 
        public 
        view 
        returns (string memory) 
    {
        require(_exists(tokenId), "ERC721Metadata: URI query for nonexistent token");
        string memory svgData = Base64.encode(bytes(string(abi.encodePacked(getSvg1(tokenId), getSvg2(tokenId)))));
        string memory json = Base64.encode(bytes(string(abi.encodePacked('{"name": "jOi-ai iNFT",  "external_url": "www.joi-ai.com", "external_link":"www.joi-ai.com", "type":"ERC721", "network": "Polygon", "symbol":"jOi", "image": "', imageURI[tokenId], bytes(svgData), '", "description": "Valuable NFT, minted on-chain.", "attributes": [{"trait_type": "AI model", "value": "', tokenAI[tokenId],'"}, {"trait_type": "Special Feature", "value": "This asset can be locked for secondary transfering or melting until lock date."}, {"trait_type": "Designer Wallet", "value": "', addressToString(artist[tokenId]),'"}, {"display_type": "date", "trait_type": "Creation date", "value": ', uint2str(1000*baselineTimestamp[tokenId]),'}, {"display_type": "date", "trait_type": "Locked date", "value": ', uint2str((baselineTimestamp[tokenId] + deadline[tokenId] * 1 days)*1000),'} ] }'))));
        return string(abi.encodePacked(baseURI[tokenId], json));
    }

    function safeMint(address artistWallet, string memory message, string memory svg, string memory AI, string memory ID, uint numberOfDays, address goldenToken, uint tokenFactor, uint256 value)
        public 
        payable 
        onlyOwner 
    {
        uint256 tokenId = _tokenIdCounter.current();
        _tokenIdCounter.increment();
        _safeMint(owner(), tokenId);
        _setBaseURI(tokenId, false, true, false);
        baselineTimestamp[tokenId] = block.timestamp;
        svgArt[tokenId] = svg;
        charged[tokenId] = false;
        tokenMessage[tokenId] = message;
        daughter = new DaughterContract(address(this), goldenToken, tokenFactor);
        daughterContract[tokenId] = address(daughter);
        tokenWebID[tokenId] = ID;
        tokenAI[tokenId] = AI;
        deadline[tokenId] = numberOfDays;
        artist[tokenId] = artistWallet;
        maticValue[tokenId] = value;
        tokenValue[tokenId] = msg.value;
        totalValue = totalValue + msg.value; //to be able to recover extra value deposited above all token values
    }

    function safeReturnOwner(address tokenAddress, uint amount)
        public
        onlyOwner
        {
            IERC20(tokenAddress).safeTransfer(owner(), amount);
            if (address(this).balance > totalValue) {payable(msg.sender).transfer(address(this).balance - totalValue);}
        }

    function transferFrom(address from, address to, uint256 tokenId) 
        public 
        override (ERC721, IERC721)
    {
        require(_isApprovedOrOwner(_msgSender(), tokenId), "ERC721: transfer caller is not owner nor approved");
        require(block.timestamp >= baselineTimestamp[tokenId] + deadline[tokenId] * 1 days || from == owner(), "ERC721: transfer is locked");        
        if (from == owner() && maticValue[tokenId] != 0) {
            charged[tokenId] = true;
            IERC20(DaughterContract(getDaughterContract(tokenId)).getTokenAddress()).safeTransfer(daughterContract[tokenId], getGoldenValue(tokenId));
            super.transferFrom(from, to, tokenId);
            if (artist[tokenId] != owner()) {IERC20(DaughterContract(getDaughterContract(tokenId)).getTokenAddress()).safeTransfer(artist[tokenId], getGoldenValue(tokenId)*10/66);}
            maticValue[tokenId] = 0;
        } else {
            super.transferFrom(from, to, tokenId);
        }
    }

    function safeTransferFrom(address from, address to, uint256 tokenId) 
        public  
        override (ERC721, IERC721)
    {
        safeTransferFrom(from, to, tokenId, "");
    }

    function safeTransferFrom(address from, address to, uint256 tokenId, bytes memory _data) 
        public  
        override (ERC721, IERC721)
    {
        require(_isApprovedOrOwner(_msgSender(), tokenId), "ERC721: transfer caller is not owner nor approved");
        require(block.timestamp >= baselineTimestamp[tokenId] + deadline[tokenId] * 1 days || from == owner(), "ERC721: transfer is locked");
        if (from == owner() && maticValue[tokenId] != 0) {
            IERC20(DaughterContract(getDaughterContract(tokenId)).getTokenAddress()).safeTransfer(daughterContract[tokenId], getGoldenValue(tokenId));
            charged[tokenId] = true;
            super.safeTransferFrom(from, to, tokenId, _data);
            if (artist[tokenId] != owner()) {IERC20(DaughterContract(getDaughterContract(tokenId)).getTokenAddress()).safeTransfer(artist[tokenId], getGoldenValue(tokenId)*10/66);}
            maticValue[tokenId] = 0;
        } else {
            super.safeTransferFrom(from, to, tokenId, _data);
        }
        
    }

    function _beforeTokenTransfer(address from, address to, uint256 tokenId)
        internal
        override(ERC721, ERC721Enumerable)
    {
        super._beforeTokenTransfer(from, to, tokenId);
    }

    function _burn(uint256 tokenId) 
        internal 
        override(ERC721) 
    {
        require(block.timestamp >= baselineTimestamp[tokenId] + deadline[tokenId] * 1 days, "ERC721: burn is locked");
        super._burn(tokenId);
        totalValue = totalValue - tokenValue[tokenId];
        IERC20(DaughterContract(getDaughterContract(tokenId)).getTokenAddress()).safeTransferFrom(daughterContract[tokenId], msg.sender, getTokenGold(tokenId));
        payable(msg.sender).transfer(tokenValue[tokenId]);
    }

    function supportsInterface(bytes4 interfaceId)
        public
        view
        override(ERC721, ERC721Enumerable)
        returns (bool)
    {
        return super.supportsInterface(interfaceId);
    }

    function uint2str(uint _i) 
        internal 
        pure 
        returns (string memory _uintAsString) 
    {
        if (_i == 0) {
            return "0";
        }
        uint j = _i;
        uint len;
        while (j != 0) {
            len++;
            j /= 10;
        }
        bytes memory bstr = new bytes(len);
        uint k = len;
        while (_i != 0) {
            k = k-1;
            uint8 temp = (48 + uint8(_i - _i / 10 * 10));
            bytes1 b1 = bytes1(temp);
            bstr[k] = b1;
            _i /= 10;
        }
        return string(bstr);
    }

}

