    uint256 private factor;

    constructor(address _mom, address tokenAddress, uint256 adjFactor)
        onlyOwner
        {
            token = IERC20(tokenAddress);
            factor = adjFactor;
            token.approve(_mom, 99999999999999999999999);
        }
    
    function getTokenAddress()
        public 
