// File: contracts\ERC721_001_Template.sol


pragma solidity ^0.8.9;
contract ERC721_001_Template is AccessControl, ERC721Royalty, Ownable, Pausable {
    using Counters for Counters.Counter;
    using SafeMath for uint256;

    bytes32 public constant MINTER = keccak256("MINTER");

    // Address where funds are collected
    address payable public collectFunds;

    // How many token units a buyer gets per wei
    uint256 public RATE;

    // Amount of wei raised
    uint256 public weiRaised;

    // Total supply of token
    uint256 public mintedToken;

    // Cap of maximal token
    uint256 public LIMITED_MINT;

    // Crowdsale stat and end
    uint32 public CROWDSALE_START;
    uint32 public CROWDSALE_END;
    bool public PUBLIC_MINT;

    // Indicate whether the token can still be issued by the issuer or not anymore.
    bool internal _isIssuable;

    // holds the conttract uri
    string private _uri;

    // Total base URI
    string public baseURI;
    bool public onlyBaseURI;

    Counters.Counter private _tokenIds;

    constructor(
        string memory name_,
        string memory symbol_,
        string memory uri_,
        address owner_
    ) ERC721(name_, symbol_) {
        _uri = uri_;
        _isIssuable = true;
        onlyBaseURI = false;
        _transferOwnership(owner_);
        _setupRole(DEFAULT_ADMIN_ROLE, owner_);
        _setupRole(MINTER, owner_);
    }

    /***************************************** Modifiers ********************************************/
    /**
     * @dev Modifier to verify if token is issuable.
     */
    modifier isIssuableToken() {
        require(_isIssuable, "Minting is locked");
        _;
    }

    /**
     * @dev See {IERC165-supportsInterface}.
     */
    function supportsInterface(
        bytes4 interfaceId
    )
        public
        view
        virtual
        override(AccessControl, ERC721Royalty)
        returns (bool)
    {
        return
            AccessControl.supportsInterface(interfaceId) ||
            interfaceId == type(IERC2981).interfaceId ||
            ERC721.supportsInterface(interfaceId);
    }

    /**************************************** Token URI ****************************************/
    /**
     * @dev This implementation returns the URI for the token, which stores additinal information.
     *
     */
    function contractURI() public view returns (string memory) {
        return _uri;
    }

    /**
     * @dev Sets a new URI for the token
     */
    function setURI(
        string memory newuri
    ) external onlyRole(DEFAULT_ADMIN_ROLE) {
        _uri = newuri;
    }

    /**
     * @dev Sets a new URI for the token
     */
    function setBaseURI(
        string memory newbaseUri
    ) external onlyRole(DEFAULT_ADMIN_ROLE) {
        baseURI = newbaseUri;
    }

    /**
     * @dev See {IERC721Metadata-tokenURI}.
     */
    function tokenURI(
        uint256 tokenId
    ) public view virtual override returns (string memory) {
        _requireMinted(tokenId);

        // If only base URI should be returned.
        if (onlyBaseURI) {
            return baseURI;
        }
        return super.tokenURI(tokenId);
    }

    /************************************************************************************************/

    /**************************************** Token Issuance ****************************************/
    /**
     * @dev Know if new tokens can be issued in the future.
     * @return bool 'true' if tokens can still be issued by the issuer, 'false' if they can't anymore.
     */
    function isIssuable() external view returns (bool) {
        return _isIssuable;
    }

    /************************************************************************************************/

    function setCrowdsaleData(
        uint256 _rate,
        uint256 _limit,
        uint32 _start,
        uint32 _end,
        bool _publicMint
    ) external onlyRole(DEFAULT_ADMIN_ROLE) isIssuableToken {
        RATE = _rate;
        LIMITED_MINT = _limit;
        CROWDSALE_START = _start;
        CROWDSALE_END = _end;
        PUBLIC_MINT = _publicMint;
    }

    /**
     * @notice Allows public funding for the token
     * @dev The `buyTokens` function allows to to crowdsale the NFT
     **/
    function buyTokens() external payable isIssuableToken {
        require(PUBLIC_MINT, "Public mint not activated");

        uint256 weiAmount = msg.value;
        _preValidatePurchase(_msgSender(), weiAmount);
        uint256 quantity = _getTokenAmount(weiAmount);

        require(
            LIMITED_MINT == 0 || mintedToken + quantity <= LIMITED_MINT,
            "Max amount exceeded"
        );
        require(quantity > 0, "Not enough ETH sent");
        require(crowdSaleOpen(), "Crowdsale not open");

        weiRaised = weiRaised.add(weiAmount);
        mintedToken = mintedToken.add(quantity);

        for (uint256 i = 0; i < quantity; i++) {
            _tokenIds.increment();
            uint256 newItemId = _tokenIds.current();
            _mint(_msgSender(), newItemId);
        }
        _forwardFunds(quantity);
        _remainingFundsSendBack(_msgSender(), quantity, weiAmount);
    }

    /**
     * @notice Allows public funding for the token
     * @dev The `crowdSaleOpen` function tells, if tokens can be bought
     *
     **/
    function crowdSaleOpen() public view returns (bool open) {
        return
            (CROWDSALE_START == 0 && CROWDSALE_END == 0) ||
            ((block.timestamp >= CROWDSALE_START) &&
                (block.timestamp <= CROWDSALE_END));
    }

    function batchMint(
        address account,
        uint256 amount
    ) public isIssuableToken onlyRole(MINTER) {
        require(
            LIMITED_MINT == 0 || mintedToken + amount <= LIMITED_MINT,
            "Max amount exceeded"
        );
        mintedToken = mintedToken.add(amount);

        for (uint256 i = 0; i < amount; i++) {
            _tokenIds.increment();
            uint256 newItemId = _tokenIds.current();
            _mint(account, newItemId);
        }
    }

    /************************************************************************************************/

    /**************************************** Token Pause ****************************************/

    function pause() external onlyRole(DEFAULT_ADMIN_ROLE) {
        _pause();
    }

    function unpause() external onlyRole(DEFAULT_ADMIN_ROLE) {
        _unpause();
    }

    /**
     * @dev See {ERC721-_beforeTokenTransfer}.
     *
     * Requirements:
     *
     * - the contract must not be paused.
     */
    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 firstTokenId
    ) internal virtual override(ERC721) {
        require(!paused(), "ERC721Pausable: token transfer while paused");
        super._beforeTokenTransfer(from, to, firstTokenId);
    }

    /**************************************** ERC2981 Support ****************************************/

    function configureToken(
        address payable _wallet,
        uint96 _royalty,
        bool _onlyBaseURI
    ) external onlyRole(DEFAULT_ADMIN_ROLE) {
        collectFunds = _wallet;
        _setDefaultRoyalty(_wallet, _royalty);
        onlyBaseURI = _onlyBaseURI;
    }

    function defaultRoyaltyInfo() public view returns (address, uint256) {
        return royaltyInfo(0, _feeDenominator());
    }

    function setRoyaltyById(
        uint256 tokenId,
        uint96 feeNumerator
    ) external onlyRole(DEFAULT_ADMIN_ROLE) {
        _setTokenRoyalty(tokenId, collectFunds, feeNumerator);
    }

    /************************************************************************************************/

    /**************************************** Token behaviours **************************************/

    /**
        @dev Returns the total tokens minted so far.
        1 is always subtracted from the Counter since it tracks the next available tokenId.
     */
    function totalSupply() public view returns (uint256) {
        return mintedToken;
    }

    /**
     * @dev Definitely renounce the possibility to issue new tokens.
     * Once set to false, '_isIssuable' can never be set to 'true' again.
     */
    function renounceIssuance() external onlyRole(DEFAULT_ADMIN_ROLE) {
        _isIssuable = false;
    }

    /************************************************************************************************/

    // // -----------------------------------------
    // // Internal interface (extensible)
    // // -----------------------------------------

    /**
     * @dev Base URI for computing {tokenURI}. If set, the resulting URI for each
     * token will be the concatenation of the `baseURI` and the `tokenId`. Empty
     * by default, can be overridden in child contracts.
     */
    function _baseURI() internal view override returns (string memory) {
        return baseURI;
    }

    /**
     * @dev Validation of an incoming purchase. Use require statements to revert state when conditions are not met. Use super to concatenate validations.
     * @param _beneficiary Address performing the token purchase
     * @param _weiAmount Value in wei involved in the purchase
     */
    function _preValidatePurchase(
        address _beneficiary,
        uint256 _weiAmount
    ) internal pure {
        require(_beneficiary != address(0));
        require(_weiAmount != 0);
    }

    /**
     * @dev Override to extend the way in which ether is converted to tokens.
     * @param _weiAmount Value in wei to be converted into tokens
     * @return Number of tokens that can be purchased with the specified _weiAmount
     */
    function _getTokenAmount(
        uint256 _weiAmount
    ) internal view returns (uint256) {
        return _weiAmount.div(RATE);
    }

    /**
     * @dev Determines how ETH is stored/forwarded on purchases.
     */
    function _forwardFunds(uint256 _quantity) internal {
        uint256 _weiAmountUsed = _quantity.mul(RATE);
        (bool sent, bytes memory data) = collectFunds.call{
            value: _weiAmountUsed
        }("");
        require(sent, "Failed to send Ether");
    }

    function _remainingFundsSendBack(
        address _sender,
        uint256 _quantity,
        uint256 _weiAmountSent
    ) internal {
        uint256 _weiAmountUsed = _quantity.mul(RATE);
        uint256 _remaining = _weiAmountSent.sub(_weiAmountUsed);
        (bool sent, bytes memory data) = _sender.call{value: _remaining}("");
        require(sent, "Failed to send Ether");
    }
}
