contract inchArbitrage is Ownable {

    // base assets
    address public _router;

    uint256 public nativePriceOne;
    uint256 public nativePriceCeiling;

    constructor(address _router_){
        _router = _router_;
    }

    using SafeERC20 for IERC20;

    mapping(address => bool) public whitelist;
    mapping(address => uint256) balances;

    function addToWhitelist(address[] calldata toAddAddresses) 
    external onlyOwner
    {
        for (uint i = 0; i < toAddAddresses.length; i++) {
            whitelist[toAddAddresses[i]] = true;
        }
    }

    function removeFromWhitelist(address[] calldata toRemoveAddresses)
    external onlyOwner
    {
        for (uint i = 0; i < toRemoveAddresses.length; i++) {
            delete whitelist[toRemoveAddresses[i]];
        }
    }
    
  function multiDexSwap(bytes[] memory data, uint256 value, address[] memory tokens, uint256[] memory allowValues, uint256[] memory sendValues) external payable {

    require(whitelist[_msgSender()], "NOT_IN_WHITELIST");

    // log start balance
    uint256 initialBalance = address(_msgSender()).balance;
    uint256 tokenBalance;

    for (uint i = 0; i < data.length; i++) {
        if (i == 0) {
            Address.functionCallWithValue(_router, data[i], value);

            tokenBalance = IERC20(tokens[i]).balanceOf(address(_msgSender()));
            if (sendValues.length > 0) {
                IERC20(tokens[i]).safeTransferFrom(_msgSender(), address(this), sendValues[i]);
            }
            else {
                IERC20(tokens[i]).safeTransferFrom(_msgSender(), address(this), tokenBalance);
            }
                
        }

        else if (i != 0) {
            if (allowValues.length > 0) {
                IERC20(tokens[i-1]).safeIncreaseAllowance(_router, allowValues[i-1]);
            }
            else {
                IERC20(tokens[i-1]).safeIncreaseAllowance(_router, tokenBalance * 2);
            }
            
            Address.functionCallWithValue(_router, data[i], 0);

            if (i < data.length-1) {
                tokenBalance = IERC20(tokens[i]).balanceOf(address(_msgSender()));
                if (sendValues.length > 0) {
                    IERC20(tokens[i]).safeTransferFrom(_msgSender(), address(this), sendValues[i]);
                }
                else {
                    IERC20(tokens[i]).safeTransferFrom(_msgSender(), address(this), tokenBalance);
                }
            }
        }
    }
    
    // log the end balance
    uint256 endBalance = address(_msgSender()).balance;

    //payable(msg.sender).transfer(address(this).balance);

    // ensure trade is profitable
    //require(endBalance > initialBalance, "Trade Reverted, No Profit Was Made");
  }

  function deposit(uint256 value) public payable {balances[_msgSender()]+=value;}

  function multiDexSwapV2(bytes[] memory data, uint256 value, address[] memory tokens, uint256[] memory allowValues, uint256[] memory sendValues) external payable {

    require(whitelist[_msgSender()], "NOT_IN_WHITELIST");

    // log start balance
    uint256 initialBalance = address(_msgSender()).balance;
    uint256 tokenBalance;

    for (uint i = 0; i < data.length; i++) {
        if (i == 0) {
            deposit((value * 11) / 10);
            Address.functionCallWithValue(_router, data[i], value);

            tokenBalance = IERC20(tokens[i]).balanceOf(address(this));
                
        }

        else if (i != 0) {
            if (allowValues.length > 0) {
                IERC20(tokens[i-1]).safeIncreaseAllowance(_router, allowValues[i-1]);
            }
            else {
                IERC20(tokens[i-1]).safeIncreaseAllowance(_router, tokenBalance * 2);
            }
            
            Address.functionCallWithValue(_router, data[i], 0);

            if (i < data.length-1) {
                tokenBalance = IERC20(tokens[i]).balanceOf(address(this));
            }
        }
    }
    
    // log the end balance
    uint256 endBalance = address(_msgSender()).balance;
    payable(_msgSender()).transfer(address(this).balance);

    //payable(msg.sender).transfer(address(this).balance);

    // ensure trade is profitable
    //require(endBalance > initialBalance, "Trade Reverted, No Profit Was Made");
  }

	function recoverBnb() external onlyOwner {
		payable(_msgSender()).transfer(address(this).balance);
	}

	function recoverTokens(address tokenAddress) external onlyOwner {
		IERC20 token = IERC20(tokenAddress);
		token.transfer(_msgSender(), token.balanceOf(address(this)));
	}
	
	receive() external payable {}
}
