    "src/validatorModules/OneTimePaymentValidator.sol": {
      "content": "pragma solidity ^0.8.21;

import "../interfaces/IERC20WithAuth.sol";
import "../interfaces/IRegistry.sol";
import {SafeERC20} from "openzeppelin/token/ERC20/utils/SafeERC20.sol";

/**
 * @title OneTimePaymentValidator
 * @notice Validates a one-time payment for an agreement
 * @dev This contract interacts with a token contract and a registry contract
 */
contract OneTimePaymentValidator {
    error Overpayment();

    /// @notice ERC20 token used for payments
    IERC20WithAuth public immutable paymentToken;

    /// @notice The registry of all agreements
    IRegistry public immutable registry;

    /// @notice The payment time for each agreement
    mapping(bytes32 => uint256) public paymentTime;

    /// @notice The total amount paid for each agreement
    mapping(bytes32 => uint256) public totalPaid;

    /// @notice Constructs a new OneTimePaymentValidator
    /// @param _paymentToken The address of the ERC20 token used for payments
    /// @param _registry The address of the registry of agreements
    constructor(address _paymentToken, address _registry) {
        paymentToken = IERC20WithAuth(_paymentToken);
        registry = IRegistry(_registry);
    }

    /// @notice Pay the one-time fee for an agreement
    /// @param agreementHash The unique identifier for the agreement
    /// @param deadline The time before which the signed authorization is valid (unix time)
    /// @param v v of the signed authorization signature
    /// @param r r of the signed authorization signature
    /// @param s s of the signed authorization signature
    function pay(bytes32 agreementHash, uint256 deadline, uint8 v, bytes32 r, bytes32 s) public {
        // Fetch the Agreement from the Registry
        (address validatorModule,, address promisor, uint256 _paymentAmount, uint256 start,) =
            registry.getPaymentData(agreementHash);
        require(validatorModule == address(this), "Invalid validator module");
        require(block.timestamp <= start + 2 weeks, "Payment period expired");

        // Update the paymentTime and totalPaid
        _updatePaymentTime(agreementHash);
        uint256 _totalPaid = totalPaid[agreementHash];
        if (_totalPaid + _paymentAmount > _paymentAmount) {
            revert Overpayment();
        }

        // _totalPaid should be == 0 at this point
        totalPaid[agreementHash] = _totalPaid + _paymentAmount;

        // Execute the transfer with a signed authorization
        uint256 allowance = paymentToken.allowance(msg.sender, address(this));
        if (allowance < _paymentAmount) {
            paymentToken.permit(msg.sender, address(this), _paymentAmount, deadline, v, r, s);
        }

        SafeERC20.safeTransferFrom(paymentToken, msg.sender, promisor, _paymentAmount);
    }

    function batchPay(bytes32[] memory agreementHashes, uint256 deadline, uint8 v, bytes32 r, bytes32 s) public {
        address[] memory receivers = new address[](agreementHashes.length);
        uint256[] memory paymentAmounts = new uint[](agreementHashes.length);
        uint256 _totalToPay;
        for (uint256 i; i < agreementHashes.length; ++i) {
            bytes32 agreementHash = agreementHashes[i];

            (address validatorModule,, address promisor, uint256 _paymentAmount, uint256 start,) =
                registry.getPaymentData(agreementHash);
            require(validatorModule == address(this), "Invalid validator module");
            require(block.timestamp <= start + 2 weeks, "Payment period expired");

            // Update the paymentTime and totalPaid
            _updatePaymentTime(agreementHash);
            uint256 _totalPaid = totalPaid[agreementHash];
            if (_totalPaid + _paymentAmount > _paymentAmount) {
                revert Overpayment();
            }

            // _totalPaid should be == 0 at this point
            totalPaid[agreementHash] = _totalPaid + _paymentAmount;
            _totalToPay += _paymentAmount;
            receivers[i] = promisor;
            paymentAmounts[i] = _paymentAmount;
        }

        // Execute the transfer with a signed authorization
        uint256 allowance = paymentToken.allowance(msg.sender, address(this));
        if (allowance < _totalToPay) {
            paymentToken.permit(msg.sender, address(this), _totalToPay, deadline, v, r, s);
        }

        // account for tax
        // for example, if 1% fee and amount to pay was 10,000, then _diff = 100
        // the ratio to divide each payment by is   amount * (_totalToPay - _diff) / _totalToPay
        for (uint256 i; i < agreementHashes.length;) {
            SafeERC20.safeTransferFrom(paymentToken, msg.sender, receivers[i], paymentAmounts[i]);
            unchecked {
                ++i;
            }
        }
    }

    /// @notice Update the payment time for an agreement
    /// @param agreementHash The unique identifier for the agreement
    function _updatePaymentTime(bytes32 agreementHash) internal {
        paymentTime[agreementHash] = block.timestamp;
    }

    /// @notice Validate whether the correct amount has been paid for an agreement
    /// @param agreementHash The unique identifier for the agreement
    /// @return bool Whether the correct amount has been paid
    function validate(bytes32 agreementHash) public view returns (bool) {
        (address validatorModule,,, uint256 _paymentAmount,,) = registry.getPaymentData(agreementHash);
        require(validatorModule == address(this), "Invalid validator module");
        return totalPaid[agreementHash] >= _paymentAmount;
    }

    /// @notice Get the amount owed for an agreement
    /// @param agreementHash The unique identifier for the agreement
    /// @return uint256 The amount owed
    function amountOwed(bytes32 agreementHash) public view returns (uint256) {
        (address validatorModule,,, uint256 _paymentAmount,,) = registry.getPaymentData(agreementHash);
        require(validatorModule == address(this), "Invalid validator module");

        uint256 actualPaid = totalPaid[agreementHash];

        if (actualPaid >= _paymentAmount) {
            return 0;
        } else {
            return _paymentAmount - actualPaid;
        }
    }
}
