    "src/interfaces/IRegistry.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.10;

uint256 constant PROMISOR_SIGNATURE_REQUIRED_TO_VOID_AGREEMENT_MASK = 0x1;
uint256 constant OFFEROR_SIGNATURE_REQUIRED_TO_VOID_AGREEMENT_MASK = 0x2;
uint256 constant OFFEROR_SIGNATURE_REQUIRED_TO_UPDATE_OFFEROR_MASK = 0x4;
uint256 constant PROMISOR_SIGNATURE_REQUIRED_TO_UPDATE_OFFEROR_MASK = 0x8;
uint256 constant OFFEROR_SIGNATURE_REQUIRED_TO_UPDATE_PROMISOR_MASK = 0x10;
uint256 constant PROMISOR_SIGNATURE_REQUIRED_TO_UPDATE_PROMISOR_MASK = 0x20;
uint256 constant NOTARY_HAS_POWER_TO_UPDATE_PROMISOR_MASK = 0x40;
uint256 constant NOTARY_HAS_POWER_TO_UPDATE_OFFEROR_MASK = 0x80;
uint256 constant NOTARY_HAS_POWER_TO_VOID_AGREEMENT_MASK = 0x100;

/// @title IRegistry
/// @notice An interface for interacting with the agreement registry
interface IRegistry {
    /// @notice Agreement struct to define a standard agreement in the contract

    /// @param id The ID of the partner
    /// @param offerer The address of the offerer
    /// @param promisor The address of the promisor
    /// @param flags - a bitmap of flags
    /// @param terms An array containing the addresses representing the terms of the agreement
    /// @param assetAddress The address of the asset linked with this agreement
    /// @param tokenId The ID of the token associated with the agreement
    /// @param validatorModule The address of the validator module for the agreement
    /// @param expiration The timestamp of the expiration time of the agreement
    /// @param paymentAmount The amount that needs to be paid according to the agreement
    /// @param dynamicData A string containing CID
    struct Agreement {
        // string contractName;
        // TODO: Should this point to an existing array of terms
        uint128 id; //template id for enterprise partners
        address offerer;
        uint128 flags;
        address promisor;
        uint32[] terms;
        address assetAddress;
        uint256 tokenId;
        uint128 expiration;
        address validatorModule;
        uint256 paymentAmount;
        string dynamicData;
        // uint256 nonce;
        uint256 chainId;
        address executorContract;
    }

    struct AgreementTerms {
        string name;
        string description;
    }

    /// @notice Get the payment data for a specific agreement
    /// @param agreementHash The unique identifier for the agreement
    /// @return validatorModule The address of the validator module in the agreement
    /// @return offerer The address of the offerer in the agreement
    /// @return promisor The address of the promisor in the agreement
    /// @return paymentAmount The amount to be paid in the agreement
    /// @return start The start time of the agreement
    /// @return expiration The expiration time of the agreement
    function getPaymentData(bytes32 agreementHash)
        external
        view
        returns (
            address validatorModule,
            address offerer,
            address promisor,
            uint256 paymentAmount,
            uint256 start,
            uint256 expiration
        );

    function singleCreateAgreementsBulk(
        Agreement[] memory _agreements,
        bytes32[] memory _agreementHashes,
        bytes memory notarySignature,
        uint256[] memory nonces,
        bytes[] memory offererSignatures,
        bytes memory promisorSignature,
        address promisor,
        uint256 singleNonce
    ) external;

    function nullAgreement(
        bytes32 agreementHash,
        bytes memory notarySignature,
        bytes memory offererSignature,
        bytes memory promisorSignature,
        uint256 nonce
    ) external;

    function createAgreement(
        Agreement memory agreement,
        bytes32 _agreementHash,
        bytes memory notarySignature,
        uint256 nonce,
        bytes memory offererSignature,
        bytes memory promisorSignature
    ) external;
}
"
