import "./IDao.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "./KATBEP20.sol";

pragma solidity 0.8.13;

contract DevPayment {
    address public admin;
    address public projectManager;
    address public contractor;
    IDao public daoAddress;
    BEP20Token public usdtAddress;
    BEP20Token public katAddress;
    uint256 public totalPayment;
    uint256 public totalMilestones;
    uint256 public completedMilestones;




    struct Milestone {
        uint256 amount;
        bool completed;
    }

    //uint256 _payableAmount = 0;
    mapping(uint256 => Milestone) public milestones;

    event MilestoneSet(uint256 indexed milestoneIndex, uint256 amount);
    event MilestoneCompleted(uint256 indexed milestoneIndex);
    event PaymentReleased(uint256 indexed milestoneIndex, uint256 amount);
    event ContractorWithdrawal(address indexed contractor, uint256 amount);

    constructor(
        address _admin,
        address _projectManager,
        address _contractor,
        IDao _daoAddress,
        BEP20Token _usdtAddress,
        BEP20Token _katAddress
    ) {
        // Check that none of the provided addresses are zero addresses.
        require(_admin != address(0x00), "Admin address cannot be zero");
        require(
            _projectManager != address(0x00),
            "Project Manager address cannot be zero"
        );
        require(
            _contractor != address(0x00),
            "Contractor address cannot be zero"
        );
        require(
            address(_daoAddress) != address(0x00),
            "DAO address cannot be zero"
        );
        require(
            address(_usdtAddress) != address(0x00),
            "USDT address cannot be zero"
        );
        require(
            address(_katAddress) != address(0x00),
            "KAT address cannot be zero"
        );

        // Check that the provided addresses for DAO, USDT, and KAT are valid contract addresses.

        require(
            checkValidAddress(address(_daoAddress)),
            "DAO address is not a valid contract address"
        );
        require(
            checkValidAddress(address(_usdtAddress)),
            "USDT address is not a valid contract address"
        );
        require(
            checkValidAddress(address(_katAddress)),
            "KAT address is not a valid contract address"
        );

        admin = _admin;
        projectManager = _projectManager;
        contractor = _contractor;
        daoAddress = _daoAddress;
        usdtAddress = _usdtAddress;
        katAddress = _katAddress;
    }

    function checkValidAddress(
        address checkedAddress
    ) private view returns (bool) {
        uint256 codeSize;
        assembly {
            codeSize := extcodesize(checkedAddress)
        }
        return codeSize > 0;
    }

    modifier onlyAdmin() {
        require(msg.sender == admin, "Only admin can call this function");
        _;
    }

    modifier onlyProjectManager() {
        require(
            msg.sender == projectManager,
            "Only project manager can call this function"
        );
        _;
    }

    modifier onlyContractor() {
        require(
            msg.sender == contractor,
            "Only contractor can call this function"
        );
        _;
    }

    function setMilestone(
        uint256 _milestoneIndex,
        uint256 _amount
    ) public onlyProjectManager {
        require(_milestoneIndex < totalMilestones, "Invalid milestone index");
        require(
            !milestones[_milestoneIndex].completed,
            "Milestone has already been completed"
        );

        // Tính tổng amount của các milestone hiện tại
        uint256 totalMilestoneAmount;
        uint256 totalMilestones_ = totalMilestones;
        for (uint256 i; i < totalMilestones_; ) {
            totalMilestoneAmount += milestones[i].amount;
            unchecked {
                ++i;
            }
        }

        // Kiểm tra tổng milestone amount có vượt quá total amount không
        if (_milestoneIndex < totalMilestones - 1) {
            require(
                totalMilestoneAmount + _amount <= totalPayment,
                "Total milestone amount exceeds total payment"
            );
        } else {
            require(
                totalMilestoneAmount + _amount == totalPayment,
                "Total milestone amount should be equal to total payment"
            );
        }

        milestones[_milestoneIndex] = Milestone(_amount, false);
        emit MilestoneSet(_milestoneIndex, _amount);
    }

    function completeMilestone(
        uint256 _milestoneIndex,
        uint256 _USDTAmount,
        uint256 _EquivalentKATPerUSDT
    ) public onlyAdmin() {
        require(_milestoneIndex < totalMilestones, "Invalid milestone index");
        require(
            !milestones[_milestoneIndex].completed,
            "Milestone has already been completed"
        );

        milestones[_milestoneIndex].completed = true;
        completedMilestones++;
        emit MilestoneCompleted(_milestoneIndex);

        uint8 katDecimals = BEP20Token(katAddress).decimals();
        uint8 usdtDecimals = BEP20Token(usdtAddress).decimals();

        uint256 katBalanceOnUSDTWei =
            ((BEP20Token(katAddress).balanceOf(address(daoAddress)) *
                _USDTAmount) /
            _EquivalentKATPerUSDT)
            *(10**usdtDecimals)/(10**katDecimals);

        uint256 usdtWeiBalance = BEP20Token(usdtAddress).balanceOf(address(daoAddress));
        uint256 totalBalanceOnUSDTWei = usdtWeiBalance + katBalanceOnUSDTWei;

        uint256 milestoneAmount = milestones[_milestoneIndex].amount;

        uint256 katAmount = ((milestoneAmount *
            katBalanceOnUSDTWei *
            _EquivalentKATPerUSDT) /
            _USDTAmount /
            totalBalanceOnUSDTWei) 
             * (10 ** katDecimals) / (10**usdtDecimals);

        uint256 usdtAmount = (milestoneAmount * usdtWeiBalance) /
            totalBalanceOnUSDTWei;

        require(
            usdtAddress.balanceOf(address(daoAddress)) >= usdtAmount,
            "Not enough USDT Amount"
        );
        require(
            katAddress.balanceOf(address(daoAddress)) >= katAmount,
            "Not enough KAT Amount"
        );
        // // Transfer the converted KAT amount to the contractor

        IDao(daoAddress).executePermitted(
            address(katAddress),
            abi.encodeWithSignature(
                "transfer(address,uint256)",
                contractor,
                katAmount
            ),
            0
        );
        // // Transfer the USDT amount to the contractor
        IDao(daoAddress).executePermitted(
            address(usdtAddress),
            abi.encodeWithSignature(
                "transfer(address,uint256)",
                contractor,
                usdtAmount
            ),
            0
        );
    }

    function setTotalPayment(uint256 _totalPayment) public onlyProjectManager {
        require(_totalPayment > 0, "Total payment must be greater than 0");
        require(totalPayment == 0, "Total payment has already been set");

        totalPayment = _totalPayment;
    }

    function setTotalMilestones(
        uint256 _totalMilestones
    ) public onlyProjectManager {
        require(
            _totalMilestones > 0,
            "Total milestones must be greater than 0"
        );
        require(totalMilestones == 0, "Total milestones have already been set");

        totalMilestones = _totalMilestones;
    }
}
