pragma solidity ^0.8.15;


abstract contract CSPriceQuery is CSConfigurable {
    bytes32 public constant MATIC_USD_ORACLE = keccak256("MATIC_USD_ORACLE");
    bytes32 public constant ETH_USD_ORACLE = keccak256("ETH_USD_ORACLE");
    bytes32 public constant EUR_USD_ORACLE = keccak256("EUR_USD_ORACLE");

    function getPrice(bytes32 oracle, uint80 round) internal view returns (uint256, uint80) {
        address oracleAddress = config.getAddress(oracle);
        require(oracleAddress != address(0), "price oracle not set");
        
        uint80 roundID;
        int256 price;
        uint timeStamp;

        if(round != 0) {
            (
                roundID,
                price, /*uint startedAt*/ /*uint timeStamp*/ /*uint80 answeredInRound*/
                ,
                timeStamp,

            ) = AggregatorV3Interface(oracleAddress).getRoundData(round);
            require(price > 0, "invalid price");
            // if data is less than 60 minutes old use that
            // otherwise use latest data
            if(timeStamp > block.timestamp - 60 * 60) {
                return (uint256(price), roundID);
            }
        }
        (
            roundID,            
            price, /*uint startedAt*/ /*uint timeStamp*/ /*uint80 answeredInRound*/
            ,
            ,

        ) = AggregatorV3Interface(oracleAddress).latestRoundData();
        require(price > 0, "invalid price");
        return (uint256(price), roundID);
    }

    // price of MATIC in EUR with 8 decimals
    function MATIC_EUR(uint80[2] memory rounds) public view returns (uint256, uint80[2] memory) {
        (uint256 maticUSD, uint80 maticRound) = getPrice(MATIC_USD_ORACLE, rounds[0]);
        (uint256 eurUSD, uint80 eurRound) = getPrice(EUR_USD_ORACLE, rounds[1]);
        return ((100000000 * maticUSD) / eurUSD, [maticRound, eurRound]);
    }

    // price of ETH in EUR with 8 decimals
    function ETH_EUR(uint80[2] memory rounds) public view returns (uint256, uint80[2] memory) {
        (uint256 ethUSD, uint80 ethRound) = getPrice(ETH_USD_ORACLE, rounds[0]);
        (uint256 eurUSD, uint80 eurRound) = getPrice(EUR_USD_ORACLE, rounds[1]);
        return ((100000000 * ethUSD) / eurUSD, [ethRound, eurRound]);
    }
}


