abstract contract CS4BaseShop is CSPriceQuery, TokenRescue {
    bytes32 public constant SHOP_CONTROL_ROLE = keccak256("SHOP_CONTROL_ROLE");
    bytes32 public constant SHOP_BENEFICIARY = keccak256("SHOP_BENEFICIARY");
    bytes32 public constant WETH_TOKEN = keccak256("WETH_TOKEN");

    event ShopOpened(uint256 openTimestamp);
    event ShopClosed();

    uint256 public priceEurCent;
    uint256 public openTimestamp;

    constructor(address _config, uint256 _priceEurCent)
        CSConfigurable(_config)
    {
        priceEurCent = _priceEurCent;
    }

    function openShop(uint256 _timestamp) public onlyRole(SHOP_CONTROL_ROLE) {
        openTimestamp = _timestamp;
        emit ShopOpened(openTimestamp);
    }

    function closeShop() public onlyRole(SHOP_CONTROL_ROLE) {
        openTimestamp = 0;
        emit ShopClosed();
    }

    function isOpen() public view returns (bool) {
        return openTimestamp > 0 && block.timestamp >= openTimestamp;
    }

    modifier requireOpen() {
        require(
            isOpen() == true,
            "This call only works when the shop is open."
        );
        _;
    }

    function currentPriceMatic(uint80[2] memory forceRounds) public view returns (uint256, uint80[2] memory) {
        (uint256 maticEur, uint80[2] memory rounds) = MATIC_EUR(forceRounds);
        return ((100000000 * 10000000000000000 * priceEurCent) / maticEur, rounds);
    }

    function currentPriceEth(uint80[2] memory forceRounds) public view returns (uint256, uint80[2] memory) {
        (uint256 ethEur, uint80[2] memory rounds) = ETH_EUR(forceRounds);
        return ((100000000 * 10000000000000000 * priceEurCent) / ethEur, rounds);
    }

    function handlePaymentEUR(bool weth, uint256 quantity, uint80[2] memory forceRounds) internal {
        require(quantity > 0);
        if (weth) {
            address wethToken = config.getAddress(WETH_TOKEN);
            require(wethToken != address(0), "weth not configured");
            require(msg.value == 0, "value not allowed for weth");
            (uint256 currentPrice,) = currentPriceEth(forceRounds);
            uint256 totalPrice = currentPrice * quantity;
            require(
                IERC20(wethToken).transferFrom(
                    msg.sender,
                    config.getAddress(SHOP_BENEFICIARY),
                    totalPrice
                )
            );
        } else {
            (uint256 currentPrice,) = currentPriceMatic(forceRounds);
            uint256 totalPrice = currentPrice * quantity;
            require(msg.value >= totalPrice, "Not enough value sent");
            payable(config.getAddress(SHOP_BENEFICIARY)).transfer(totalPrice);
            payable(msg.sender).transfer(msg.value - totalPrice);
        }
    }
}


