interface IShipwrightNFT is IERC1155 {
    function getUserSalt(address user) external view returns (bytes32);
    function getUserSaltBurn(address user) external view returns (bytes32);
    function mint(address to, uint256 id, bytes32 newSalt) external returns (uint256);
    function burn(address from, uint256 id, bytes32 newSalt) external returns (bool);
}

contract ShipwrightMinter is Ownable, ReentrancyGuard {
    using ECDSA for bytes32;
    using Strings for uint256;
    using Address for address payable;
    using SafeERC20 for IERC20;

    address public feeToken;

    bool public mintActive;

    address private sysAddress;

    mapping (address => bytes32) private previousActionSalt;

    string private baseURI_;

    IShipwrightNFT shipwright;

    event MintStatusChanges(bool status);
    event FeeChanged(uint256 newFee);
    event Minted(uint256 tokenId, address owner);
    event Burned(uint256 tokenId, address owner);

    constructor(address sys, address nft) {
        require(sys != address(0), "Shipwright: Sys != address(0)");
        sysAddress = sys;
        mintActive = true;
        shipwright = IShipwrightNFT(nft);
    }

    function getUserSalt(address user) external view returns (bytes32) {
        return shipwright.getUserSalt(user);
    }

    function getUserSaltBurn(address user) external view returns (bytes32) {
        return shipwright.getUserSaltBurn(user);
    }

    function mint(uint8 _culture, uint256 _fee, bytes memory sig) external payable nonReentrant {
        require(mintActive, "Shipwright: Mint is currently paused");
        bytes32 oldSalt = shipwright.getUserSalt(_msgSender());
        require(verify(_msgSender(), true, _culture, _fee, oldSalt, sig), "Shipwright: Auth failed");
        if (_fee > 0) {
            if (feeToken == address(0)) {
                require(msg.value == _fee, "Shipwright: Not enough payed");
            } else {
                IERC20(feeToken).safeTransferFrom(_msgSender(), address(this), _fee);
            }
        }
        uint256 tokenId = shipwright.mint(_msgSender(), uint256(_culture) ,_genSalt(_msgSender(), true, _fee, _culture));
        emit Minted(tokenId, _msgSender());
    }

    function burn(uint8 _culture, uint256 tokenId, bytes memory sig) external {
        bytes32 oldSalt = shipwright.getUserSaltBurn(_msgSender());
        require(verify(_msgSender(), false, _culture, 0, oldSalt, sig), "Shipwright: Auth failed");
        uint256 id = uint256(_culture);
        bool burned = shipwright.burn(_msgSender(), id, _genSalt(_msgSender(), false, 0, _culture));
        assert(burned);
        emit Burned(tokenId, _msgSender());
    }

    /// @dev onlyOwner

    function withdrawToken(address token) external onlyOwner {
        if (token == address(0)) {
            payable(owner()).sendValue(address(this).balance);
        } else {
            IERC20(token).safeTransfer(owner(), IERC20(token).balanceOf(address(this)));
        }
    }

    function changeMintStatus(bool value) external onlyOwner {
        mintActive = value;
        emit MintStatusChanges(value);
    }

    function changeSysAddress(address sys) external onlyOwner {
        require(sys != address(0), "Shipwright: sys != address(0)");
        sysAddress = sys;
    }

    function setFeeToken(address token) external onlyOwner {
        feeToken = token;
    }

    /// @dev Internal functions 

    function verify(address user, bool toMint, uint8 culture, uint256 fee, bytes32 salt, bytes memory data) internal view returns (bool) {
        bytes32 messageHash = keccak256(abi.encodePacked(user, toMint, fee, culture, salt));
        bytes32 ethHash = ECDSA.toEthSignedMessageHash(messageHash);
        (address rec, ) = ECDSA.tryRecover(ethHash, data);
        return rec == sysAddress;
    }

    function getSig(address user, bool toMint, uint256 fee, uint8 culture, bytes32 salt) public pure returns (bytes32) {
        return keccak256(abi.encodePacked(user, toMint, fee, culture, salt));
    }

    function _genSalt(address user, bool toMint, uint256 fee, uint8 culture) public view returns (bytes32) {
        return keccak256(abi.encodePacked(user, toMint, fee, culture, block.coinbase, block.difficulty, block.number));
    }

}
