// File contracts/roles/AccessOperatable.sol



pragma solidity ^0.8.0;

abstract contract AccessOperatable is AccessControl {

    bytes32 public constant OPERATOR = keccak256("OPERATOR");

    event Paused(address account);
    event Unpaused(address account);

    bool public _paused;

    constructor() {
        _setRoleAdmin(OPERATOR, DEFAULT_ADMIN_ROLE);
        _setupRole(OPERATOR, msg.sender);
        _paused = false;
    }

    function addOperator(address account) public onlyOperator() {
        _setupRole(OPERATOR, account);
    }

    modifier onlyOperator() {
        require(hasRole(OPERATOR, msg.sender), "Must be operator");
        _;
    }

    modifier whenNotPaused() {
        require(!_paused, "Pausable: paused");
        _;
    }

    modifier whenPaused() {
        require(_paused, "Pausable: not paused");
        _;
    }

    function pause() public onlyOperator() whenNotPaused() {
        _paused = true;
        emit Paused(msg.sender);
    }

    function unpause() public onlyOperator() whenPaused() {
        _paused = false;
        emit Unpaused(msg.sender);
    }
}


// File contracts/CryptoMaidsEvent.sol



pragma solidity ^0.8.0;









interface IERC721Mintable {
    function exists(uint256 _tokenId) external view returns (bool);
    function mint(address _to, uint256 _tokenId) external;
    function totalSupply() external view returns (uint256);
}

contract CryptoMaidsEvent is ERC721Permit("CryptoMaids:Event") ,Ownable, ERC721URIStorage, ERC721Enumerable, AccessOperatable, IERC721Mintable{
    using SafeMath for uint256;
    using Counters for Counters.Counter;

    string public _defaultURI;
    uint256 private _supply;

    event UpdateDefaultURI(string defaultURI);

    constructor() ERC721("CryptoMaids:Event", "CMTE") {
        setDefaultURI("https://api.cryptomaids.tokyo/metadata/event/");
    }
    
    function exists(uint256 tokenId) override public view returns (bool) {
        return super._exists(tokenId);
    }

    function mint(address to, uint256 tokenId) virtual override public onlyOperator() {
        _supply++;
        super._mint(to, tokenId);
    }

    function burn(uint256 tokenId) virtual public onlyOperator() {
        _supply--;
        super._burn(tokenId);
    }

    function totalSupply() public view virtual override(IERC721Mintable, ERC721Enumerable) returns (uint256) {
        return _supply;
    }

    function bulkMint(address[] memory _tos, uint256[] memory _tokenIds) public onlyOperator() {
        require(_tos.length == _tokenIds.length);
        uint8 i;
        for (i = 0; i < _tos.length; i++) {
          mint(_tos[i], _tokenIds[i]);
        }
    }

    function supportsInterface(bytes4 interfaceId) public view virtual override(ERC721, AccessControl, ERC721Enumerable) returns (bool) {
        return super.supportsInterface(interfaceId);
    }

    function setDefaultURI(string memory defaultURI_) public onlyOperator() {
        _defaultURI = defaultURI_;
        emit UpdateDefaultURI(_defaultURI);
    }

    function _baseURI() internal view override returns (string memory) {
        return _defaultURI;
    }

    function _burn(uint256 tokenId) internal virtual override(ERC721, ERC721URIStorage) {
        super._burn(tokenId);
    }

    function tokenURI(uint256 tokenId) public view virtual override(ERC721, ERC721URIStorage) returns (string memory){
        return super.tokenURI(tokenId);
    }

    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 tokenId
    ) internal virtual override(ERC721, ERC721Enumerable) {
        super._beforeTokenTransfer(from, to, tokenId);
    }

}
