// File: contracts/QrolliId.sol

pragma solidity 0.6.12;
pragma experimental ABIEncoderV2;







/**
 * @title QRolli-ID NFT
 * QRolli-ID NFT - ERC721 contract that has minting functionality for users
 */

contract QrolliId is ERC721Upgradeable, OwnableUpgradeable {
    using SafeMath for uint256;

    struct UserInfo {
        bytes32 id;
        string name;
        string banner;
        string icon;
        string social;
        uint256 following;
        uint256 followers;
        uint256 createdAt;
        uint256 updatedAt;
    }

    mapping(uint256 => UserInfo) public users;
    mapping(bytes32 => uint256) public userIndexById;
    mapping(address => bool) public whitelists;

    mapping(bytes32 => mapping(uint256 => bytes32)) public followings;
    mapping(bytes32 => mapping(uint256 => bytes32)) public followers;
    mapping(bytes32 => mapping(bytes32 => uint256)) public followingsMap;
    mapping(bytes32 => mapping(bytes32 => uint256)) public followersMap;

    /// @dev Events of the contract
    event Minted(uint256 tokenId, address minter);

    uint256 private _nextTokenId = 0;

    string public baseUri;

    uint256 public maxNftPerAddress = 1;
    bool public isTransferEnabled;
    bool public isWhitelistingMode;
    bool private isInitialized = false;
    address public rewardHub;
    address public subscribeHub;
    address public shareCoinHub;
    address public relayer;

    modifier onlyWhitelisted() {
        require(
            msg.sender == owner || !isWhitelistingMode || whitelists[msg.sender]
        );
        _;
    }

    modifier onlyInitializing() {
        require(!isInitialized);
        _;
        isInitialized = true;
    }

    modifier onlyRelayer() {
        require(
            msg.sender == relayer ||
                msg.sender == owner ||
                msg.sender == address(this)
        );
        _;
    }

    /// @notice Contract constructor
    constructor() public {}

    function initialize(
        string memory _name,
        string memory _symbol
    ) public onlyInitializing {
        __ERC721__init(_name, _symbol, type(uint256).max, type(uint256).max);
        __Ownable_init();
    }

    /// @inheritdoc	ERC165
    function supportsInterface(
        bytes4 interfaceId
    ) public view virtual override(ERC721Upgradeable) returns (bool) {
        return ERC721Upgradeable.supportsInterface(interfaceId);
    }

    function setBaseUrl(string memory _baseUrl) external onlyOwner {
        super._setBaseURI(_baseUrl);
        baseUri = _baseUrl;
    }

    function setWhitelistMode(bool _mode) external onlyOwner {
        isWhitelistingMode = _mode;
    }

    function setWhitelistAddresses(
        address[] calldata _addrs
    ) external onlyOwner {
        for (uint256 i = 0; i < _addrs.length; i++) {
            whitelists[_addrs[i]] = true;
        }
    }

    function removeWhitelistAddresses(
        address[] calldata _addrs
    ) external onlyOwner {
        for (uint256 i = 0; i < _addrs.length; i++) {
            whitelists[_addrs[i]] = false;
        }
    }

    function mintByRelayer(
        address _sender,
        bytes32 id,
        string calldata name,
        string calldata banner,
        string calldata icon,
        string calldata social,
        bytes32 referred
    ) public onlyRelayer returns (uint256) {
        return
            _mintByRelayer(_sender, id, name, banner, icon, social, referred);
    }

    function _mintByRelayer(
        address _sender,
        bytes32 id,
        string calldata name,
        string calldata banner,
        string calldata icon,
        string calldata social,
        bytes32 referred
    ) private returns (uint256) {
        require(
            balanceOf(_sender) < maxNftPerAddress && userIndexById[id] == 0
        );

        uint256 newTokenId = getNextTokenId();
        _safeMint(_sender, 1);

        users[newTokenId].id = id;
        users[newTokenId].name = name;
        users[newTokenId].banner = banner;
        users[newTokenId].icon = icon;
        users[newTokenId].social = social;
        users[newTokenId].createdAt = block.timestamp;
        users[newTokenId].updatedAt = block.timestamp;

        userIndexById[id] = newTokenId + 1;

        _incrementTokenId();

        if (userIndexById[referred] > 0) {
            IRewardHub(rewardHub).addReferral(
                userIndexById[referred].sub(1),
                newTokenId
            );
            IRewardHub(rewardHub).increaseScore(
                4,
                userIndexById[referred].sub(1)
            );
        }
        IShareCoinHub(shareCoinHub).deployShareCoin(newTokenId);
        emit Minted(newTokenId, _sender);
        return newTokenId;
    }

    /**
     * @dev Mints a token to an address with a tokenURI.
     */
    function mint(
        bytes32 id,
        string calldata name,
        string calldata banner,
        string calldata icon,
        string calldata social,
        bytes32 referred
    ) external onlyWhitelisted returns (uint256) {
        return
            _mintByRelayer(
                _msgSender(),
                id,
                name,
                banner,
                icon,
                social,
                referred
            );
    }

    function createShareCoin(uint256 _tokenId) external {
        require(ownerOf(_tokenId) == _msgSender());
        IShareCoinHub(shareCoinHub).deployShareCoin(_tokenId);
    }

    function getSubscribe(
        uint256 _fromTokenId,
        uint256 _toTokenId
    ) external view returns (uint256) {
        return
            ISubscribeHub(subscribeHub).getSubscribe(_fromTokenId, _toTokenId);
    }

    function getReferredUsers(
        uint256 _tokenId
    )
        external
        view
        returns (UserInfo[] memory, address[] memory, uint256[] memory)
    {
        uint256[] memory userIds = IRewardHub(rewardHub).getReferredUsers(
            _tokenId
        );
        UserInfo[] memory fls = new UserInfo[](userIds.length);
        address[] memory addrs = new address[](userIds.length);
        uint256[] memory tokenIds = new uint256[](userIds.length);

        for (uint256 i = 0; i < userIds.length; i++) {
            fls[i] = users[userIds[i]];
            addrs[i] = ownerOf(userIds[i]);
            tokenIds[i] = userIds[i];
        }
        return (fls, addrs, tokenIds);
    }

    function updateUserInfoByRelayer(
        address _sender,
        uint256 _tokenId,
        bytes32 id,
        string calldata name,
        string calldata banner,
        string calldata icon,
        string calldata social
    ) public onlyRelayer {
        _updateUserInfoByRelayer(
            _sender,
            _tokenId,
            id,
            name,
            banner,
            icon,
            social
        );
    }

    function _updateUserInfoByRelayer(
        address _sender,
        uint256 _tokenId,
        bytes32 id,
        string calldata name,
        string calldata banner,
        string calldata icon,
        string calldata social
    ) private {
        require(ownerOf(_tokenId) == _sender);

        UserInfo storage _user = users[_tokenId];
        if (_user.id != id) {
            require(userIndexById[id] == 0);
            userIndexById[_user.id] = 0;
        }

        _user.id = id;
        _user.name = name;
        _user.banner = banner;
        _user.icon = icon;
        _user.social = social;
        _user.updatedAt = block.timestamp;

        userIndexById[id] = _tokenId + 1;
    }

    function updateUserInfo(
        uint256 _tokenId,
        bytes32 id,
        string calldata name,
        string calldata banner,
        string calldata icon,
        string calldata social
    ) external {
        _updateUserInfoByRelayer(
            _msgSender(),
            _tokenId,
            id,
            name,
            banner,
            icon,
            social
        );
    }

    function getFollowers(
        uint256 _tokenId
    )
        external
        view
        returns (UserInfo[] memory, address[] memory, uint256[] memory)
    {
        UserInfo memory _user = users[_tokenId];
        uint256 userFollowers = _user.followers;
        UserInfo[] memory fls = new UserInfo[](userFollowers);
        address[] memory addrs = new address[](userFollowers);
        uint256[] memory tokenIds = new uint256[](userFollowers);

        for (uint256 i = 0; i < userFollowers; i++) {
            if (userIndexById[followers[_user.id][i]] > 0) {
                uint256 sTokenId = userIndexById[followers[_user.id][i]].sub(1);
                fls[i] = users[sTokenId];
                addrs[i] = ownerOf(sTokenId);
                tokenIds[i] = sTokenId;
            }
        }
        return (fls, addrs, tokenIds);
    }

    function getFollowings(
        uint256 _tokenId
    )
        external
        view
        returns (UserInfo[] memory, address[] memory, uint256[] memory)
    {
        UserInfo memory _user = users[_tokenId];
        uint256 userFollowings = _user.following;
        UserInfo[] memory fls = new UserInfo[](userFollowings);
        address[] memory addrs = new address[](userFollowings);
        uint256[] memory tokenIds = new uint256[](userFollowings);

        for (uint256 i = 0; i < userFollowings; i++) {
            if (userIndexById[followings[_user.id][i]] > 0) {
                uint256 sTokenId = userIndexById[followings[_user.id][i]].sub(
                    1
                );
                fls[i] = users[sTokenId];
                addrs[i] = ownerOf(sTokenId);
                tokenIds[i] = sTokenId;
            }
        }
        return (fls, addrs, tokenIds);
    }

    function getSubscribers(
        uint256 _tokenId
    )
        external
        view
        returns (UserInfo[] memory, address[] memory, uint256[] memory)
    {
        uint256[] memory _subscribers = ISubscribeHub(subscribeHub)
            .getSubscribedUsers(_tokenId);
        UserInfo[] memory fls = new UserInfo[](_subscribers.length);
        address[] memory addrs = new address[](_subscribers.length);
        for (uint256 i = 0; i < _subscribers.length; i++) {
            fls[i] = users[_subscribers[i]];
            addrs[i] = ownerOf(_subscribers[i]);
        }
        return (fls, addrs, _subscribers);
    }

    function getSubscribedByUsers(
        uint256 _tokenId
    )
        external
        view
        returns (UserInfo[] memory, address[] memory, uint256[] memory)
    {
        uint256[] memory _subscribers = ISubscribeHub(subscribeHub)
            .getSubscribedByUsers(_tokenId);
        UserInfo[] memory fls = new UserInfo[](_subscribers.length);
        address[] memory addrs = new address[](_subscribers.length);
        for (uint256 i = 0; i < _subscribers.length; i++) {
            fls[i] = users[_subscribers[i]];
            addrs[i] = ownerOf(_subscribers[i]);
        }
        return (fls, addrs, _subscribers);
    }

    function followByRelayer(
        address _sender,
        bytes32 _sourceId,
        bytes32 _targetId
    ) public onlyRelayer {
        _followByRelayer(_sender, _sourceId, _targetId);
    }

    function _followByRelayer(
        address _sender,
        bytes32 _sourceId,
        bytes32 _targetId
    ) private {
        require(
            userIndexById[_sourceId] > 0 &&
                userIndexById[_targetId] > 0 &&
                _sourceId != _targetId
        );
        uint256 sourceTokenId = userIndexById[_sourceId].sub(1);
        uint256 targetTokenId = userIndexById[_targetId].sub(1);

        require(ownerOf(sourceTokenId) == _sender);
        require(followingsMap[_sourceId][_targetId] == 0);

        UserInfo storage sourceUser = users[sourceTokenId];
        UserInfo storage targetUser = users[targetTokenId];

        followings[_sourceId][sourceUser.following] = _targetId;
        followers[_targetId][targetUser.followers] = _sourceId;

        sourceUser.following = sourceUser.following + 1;
        targetUser.followers = targetUser.followers + 1;

        followingsMap[_sourceId][_targetId] = sourceUser.following;
        followersMap[_targetId][_sourceId] = targetUser.followers;
        IRewardHub(rewardHub).increaseScore(3, targetTokenId);
    }

    function follow(bytes32 _sourceId, bytes32 _targetId) external {
        _followByRelayer(_msgSender(), _sourceId, _targetId);
    }

    function unfollowByRelayer(
        address _sender,
        bytes32 _sourceId,
        bytes32 _targetId
    ) public onlyRelayer {
        _unfollowByRelayer(_sender, _sourceId, _targetId);
    }

    function _unfollowByRelayer(
        address _sender,
        bytes32 _sourceId,
        bytes32 _targetId
    ) private {
        require(userIndexById[_sourceId] > 0 && userIndexById[_targetId] > 0);
        uint256 sourceTokenId = userIndexById[_sourceId].sub(1);
        uint256 targetTokenId = userIndexById[_targetId].sub(1);

        require(ownerOf(sourceTokenId) == _sender);
        require(followingsMap[_sourceId][_targetId] > 0);

        UserInfo storage sourceUser = users[sourceTokenId];
        UserInfo storage targetUser = users[targetTokenId];

        sourceUser.following = sourceUser.following.sub(1);
        targetUser.followers = targetUser.followers.sub(1);

        if (sourceUser.following > 0) {
            bytes32 lastFollowingToReplace = followings[_sourceId][
                sourceUser.following
            ];
            followings[_sourceId][
                followingsMap[_sourceId][_targetId].sub(1)
            ] = lastFollowingToReplace;
            followings[_sourceId][sourceUser.following] = 0;
            followingsMap[_sourceId][lastFollowingToReplace] = followingsMap[
                _sourceId
            ][_targetId];
            followingsMap[_sourceId][_targetId] = 0;
        } else {
            followings[_sourceId][0] = 0;
            followingsMap[_sourceId][_targetId] = 0;
        }

        if (targetUser.followers > 0) {
            bytes32 lastFollowersToReplace = followers[_targetId][
                targetUser.followers
            ];
            followers[_targetId][
                followersMap[_targetId][_sourceId].sub(1)
            ] = lastFollowersToReplace;
            followers[_targetId][targetUser.followers] = 0;
            followersMap[_targetId][lastFollowersToReplace] = followersMap[
                _targetId
            ][_sourceId];
            followersMap[_targetId][_sourceId] = 0;
        } else {
            followers[_targetId][0] = 0;
            followersMap[_targetId][_sourceId] = 0;
        }
    }

    function unfollow(bytes32 _sourceId, bytes32 _targetId) external {
        _unfollowByRelayer(_msgSender(), _sourceId, _targetId);
    }

    function subscribeByRelayer(
        address _sender,
        bytes32 _sourceId,
        bytes32 _targetId,
        uint256 _subscribeLength // 1: 1 month, 2: 2 months, 3: 3 months
    ) public onlyRelayer {
        _subscribeByRelayer(_sender, _sourceId, _targetId, _subscribeLength);
    }

    function _subscribeByRelayer(
        address _sender,
        bytes32 _sourceId,
        bytes32 _targetId,
        uint256 _subscribeLength // 1: 1 month, 2: 2 months, 3: 3 months
    ) private {
        require(
            userIndexById[_sourceId] > 0 &&
                userIndexById[_targetId] > 0 &&
                _sourceId != _targetId
        );
        uint256 sourceTokenId = userIndexById[_sourceId].sub(1);
        uint256 targetTokenId = userIndexById[_targetId].sub(1);

        require(ownerOf(sourceTokenId) == _sender);
        require(followingsMap[_sourceId][_targetId] != 0);

        IERC20 subscribeToken = IERC20(
            ISubscribeHub(subscribeHub).getSubscribeToken()
        );

        uint256 basicSubscribeFee = ISubscribeHub(subscribeHub)
            .getSubscribeFee();

        if (basicSubscribeFee > 0) {
            subscribeToken.transferFrom(
                _sender,
                subscribeHub,
                basicSubscribeFee.mul(_subscribeLength)
            );
        }
        ISubscribeHub(subscribeHub).addSubscribe(
            sourceTokenId,
            ownerOf(sourceTokenId),
            targetTokenId,
            _subscribeLength.mul(30 * 24 * 3600)
        );
        IRewardHub(rewardHub).increaseScore(4, targetTokenId);
    }

    function subscribe(
        bytes32 _sourceId,
        bytes32 _targetId,
        uint256 _subscribeLength // 1: 1 month, 2: 2 months, 3: 3 months
    ) external {
        _subscribeByRelayer(
            _msgSender(),
            _sourceId,
            _targetId,
            _subscribeLength
        );
    }

    function setVariables(
        uint256 _maxNftCount,
        bool _isTransferEnabled,
        address _rewardHub,
        address _subscribeHub,
        address _shareCoinHub,
        address _relayer
    ) public onlyOwner {
        maxNftPerAddress = _maxNftCount;
        isTransferEnabled = _isTransferEnabled;
        rewardHub = _rewardHub;
        subscribeHub = _subscribeHub;
        shareCoinHub = _shareCoinHub;
        relayer = _relayer;
    }

    /**
     * @dev calculates the next token ID based on value of _nextTokenId
     * @return uint256 for the next token ID
     */
    function getNextTokenId() public view returns (uint256) {
        return _nextTokenId;
    }

    /**
     * @dev increments the value of _nextTokenId
     */
    function _incrementTokenId() private {
        _nextTokenId++;
    }

    function isApproved(
        uint256 _tokenId,
        address _operator
    ) public view returns (bool) {
        return
            isApprovedForAll(ownerOf(_tokenId), _operator) ||
            getApproved(_tokenId) == _operator;
    }

    function _beforeTokenTransfers(
        address from,
        address,
        uint256,
        uint256
    ) internal override {
        require(from == address(0) || isTransferEnabled);
    }

    function createShareCoinByRelayer(
        address _sender,
        uint256 _tokenId
    ) public onlyRelayer {
        require(ownerOf(_tokenId) == _sender);
        IShareCoinHub(shareCoinHub).deployShareCoin(_tokenId);
    }

    function addShareCoinVolume(uint256 _amount, uint256 _tokenId) external {
        require(msg.sender == shareCoinHub, "share coin");
        IRewardHub(rewardHub).increaseShareVolume(_tokenId, _amount);
    }

    uint256[45] private __gap;
}
