     * @dev Whenever an {IERC721} `tokenId` token is transferred to this contract via {IERC721-safeTransferFrom}
     * by `operator` from `from`, this function is called.
     *
     * It must return its Solidity selector to confirm the token transfer.
     * If any other value is returned or the interface is not implemented by the recipient, the transfer will be reverted.
     *
     * The selector can be obtained in Solidity with `IERC721.onERC721Received.selector`.
     */
    function onERC721Received(
        address operator,
        address from,
        uint256 tokenId,
        bytes calldata data
    ) external returns (bytes4);
}


contract MOONCARD is ReferralNFT, TierNFT {

    constructor()
        AvatarNFT(
            1.3 ether, //0.08 ether, // starting price
            15111, // total
            0, // reserved
            20, // max tokens per mint
            "ipfs://",
            "NNFT MOON IDCARD", "MOONCARD"
        )
        ReferralNFT(3000 /* referral fee in 0.01% */)
        TierNFT(7)
        {}

    function initTiers(uint8) internal override {
        // This is in MATIC
        _priceByTier[TierId.wrap(uint8(Tier.Guardian))] = 1.3 ether;
        _priceByTier[TierId.wrap(uint8(Tier.Magician))] = 19 ether;
        _priceByTier[TierId.wrap(uint8(Tier.Consul))] = 32 ether;
        _priceByTier[TierId.wrap(uint8(Tier.Vizier))] = 65 ether;
        _priceByTier[TierId.wrap(uint8(Tier.Enclave))] = 163 ether;
        _priceByTier[TierId.wrap(uint8(Tier.Regent))] = 980 ether; // isn't used, because only 1 and it's reserved
		_priceByTier[TierId.wrap(uint8(Tier.SupremeRuler))] = 47059 ether;
		

        // inverted!
        _tierRanges[TierId.wrap(uint8(Tier.SupremeRuler))] = Range(0, 0);
        _tierRanges[TierId.wrap(uint8(Tier.Regent))] = Range(1, 10);
        _tierRanges[TierId.wrap(uint8(Tier.Enclave))] = Range(11, 510);
        _tierRanges[TierId.wrap(uint8(Tier.Vizier))] = Range(511, 3110);
        _tierRanges[TierId.wrap(uint8(Tier.Consul))] = Range(3111, 7110);
        _tierRanges[TierId.wrap(uint8(Tier.Magician))] = Range(7111, 12110);
		_tierRanges[TierId.wrap(uint8(Tier.Guardian))] = Range(12111, 15110);
    }


    function setTierPrice(TierId tier, uint256 _newPrice) external onlyOwner {	 
      _priceByTier[tier] = _newPrice;
    }

    function mint(uint256, address payable) public payable override {
        revert("Not implemented");
    }

    function mint(TierId tier, uint256 nTokens) public payable override {
        super.mint(tier, nTokens);

        // Balance is transferred right away at purchase
        require(payable(beneficiary).send(msg.value));
    }

    function mint(TierId tier, uint256 nTokens, address payable referral) whenSaleStarted public payable {
        super.mint(tier, nTokens);

        _updateReferral(nTokens, referral);

        // Balance is transferred right away at purchase
        require(payable(beneficiary).send(msg.value));
    }	

    // ------ Overrides FOR TierNFT

    // Override works right-to-left, https://solidity-by-example.org/inheritance/
    function getReservedLeft() public view override(AvatarNFT, TierNFT) returns(uint256) {
        return super.getReservedLeft();
    }

    function getPrice() public view override(AvatarNFT, TierNFT) returns(uint256 price) {
        return super.getPrice();
    }

    function claimReserved(uint256 nTokens, address receiver) public pure override(AvatarNFT, TierNFT) {
        // revert("Not implemented");
        super.claimReserved(nTokens, receiver);
    }

    function setStartingIndex() public override(AvatarNFT, TierNFT) {
        super.setStartingIndex();
    }

    function mint(uint256 nTokens) public payable override(AvatarNFT, TierNFT) {
        super.mint(nTokens);
    }

}
