    "contracts/Dividends.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.18;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/utils/math/Math.sol";
import "./interfaces/IGrabCoinClub.sol";

contract Dividends is Ownable, ReentrancyGuard {
    using SafeERC20 for IERC20Metadata;

    uint8 private constant MULTIPLIER = 10;

    IGrabCoinClub public nftCollection;
    IERC20Metadata public rewardToken;
    address public treasury;

    mapping(uint256 => uint256) public lastGrabbedAt;

    uint256 public startedAt;

    uint256[] public dividendsRate = [
        47, // 4.7
        75, // 7.5
        131, // 13.1
        244, // 24.4
        469, // 46.9
        750, // 75
        1313, // 131.3
        2438, // 243.8
        4688, // 468.8
        7500, // 750
        13125, // 1312.5
        24375, // 2437.5
        46875, // 4687.5
        75000, // 7500
        131250 // 13125
    ];

    event UpdatedNFTCollection(address indexed newAddress);
    event UpdatedRewardToken(address indexed newAddress);
    event UpdatedTreasury(address indexed newTreasury);
    event UpdatedStartedAt(uint256 newStartedAt);
    event UpdatedDividendsRate(uint256[] newDividendsRate);
    event GrabbedDividends(address indexed account, uint256 indexed tokenId, uint256 amount);

    constructor(address _nftCollection, address _rewardToken) {
        nftCollection = IGrabCoinClub(_nftCollection);
        rewardToken = IERC20Metadata(_rewardToken);
        startedAt = block.timestamp;
    }

    function setNFTCollection(address newAddress) external onlyOwner {
        nftCollection = IGrabCoinClub(newAddress);
        emit UpdatedNFTCollection(newAddress);
    }

    function setRewardToken(address newAddress) external onlyOwner {
        rewardToken = IERC20Metadata(newAddress);
        emit UpdatedRewardToken(newAddress);
    }

    function setTreasury(address newTreasury) external onlyOwner {
        treasury = newTreasury;
        emit UpdatedTreasury(newTreasury);
    }

    function setDividendsRate(uint256[] memory newDividendsRate) external onlyOwner {
        dividendsRate = newDividendsRate;
        emit UpdatedDividendsRate(newDividendsRate);
    }

    function setStartedAt(uint256 newStartedAt) external onlyOwner {
        startedAt = newStartedAt;
        emit UpdatedStartedAt(newStartedAt);
    }

    function calcDividends(uint256 tokenId) public view returns (uint256) {
        uint256 from = Math.max(nftCollection.mintedAt(tokenId), startedAt);
        uint256 to = Math.min(from + SECONDS_PER_DAY * 365 * 3, block.timestamp);

        return
            (dividendsRate[nftCollection.seriesOf(tokenId)] *
                _diffMonths(Math.max(from, lastGrabbedAt[tokenId]), to) *
                (10 ** rewardToken.decimals())) / MULTIPLIER;
    }

    function calcAllDividends() public view returns (uint256 total) {
        uint256[] memory tokens = nftCollection.tokensOfOwner(_msgSender());
        uint256 length = tokens.length;
        for (uint256 i; i < length; ) {
            total += calcDividends(tokens[i]);
            unchecked {
                ++i;
            }
        }
        return total;
    }

    function grabDividends(uint256 tokenId) public returns (uint256) {
        require(nftCollection.rawOwnerOf(tokenId) == _msgSender(), "Invalid token id");
        uint256 amount = calcDividends(tokenId);
        require(amount > 0, "no claimable reward");

        rewardToken.safeTransferFrom(treasury, _msgSender(), amount);

        lastGrabbedAt[tokenId] = block.timestamp;
        emit GrabbedDividends(_msgSender(), tokenId, amount);

        return amount;
    }

    function grabAllDividends() public returns (uint256 total) {
        uint256[] memory tokens = nftCollection.tokensOfOwner(_msgSender());
        uint256 length = tokens.length;
        for (uint256 i; i < length; ) {
            uint256 amount = calcDividends(tokens[i]);
            if (amount > 0) {
                lastGrabbedAt[tokens[i]] = block.timestamp;
                emit GrabbedDividends(_msgSender(), tokens[i], amount);
                total += amount;
            }
            unchecked {
                ++i;
            }
        }

        rewardToken.safeTransferFrom(treasury, _msgSender(), total);

        return total;
    }

    uint256 private constant SECONDS_PER_DAY = 24 * 60 * 60;
    int256 private constant OFFSET19700101 = 2440588;

    function _daysToDate(uint256 _days) internal pure returns (uint256 year, uint256 month, uint256 day) {
        int256 __days = int256(_days);

        int256 m = __days + 68569 + OFFSET19700101;
        int256 n = (4 * m) / 146097;
        m = m - (146097 * n + 3) / 4;
        int256 _year = (4000 * (m + 1)) / 1461001;
        m = m - (1461 * _year) / 4 + 31;
        int256 _month = (80 * m) / 2447;
        int256 _day = m - (2447 * _month) / 80;
        m = _month / 11;
        _month = _month + 2 - 12 * m;
        _year = 100 * (n - 49) + _year + m;

        year = uint256(_year);
        month = uint256(_month);
        day = uint256(_day);
    }

    struct Date {
        uint256 year;
        uint256 month;
        uint256 day;
    }

    function _diffMonths(uint256 timestamp1, uint256 timestamp2) internal pure returns (uint256) {
        Date[2] memory date;
        (date[0].year, date[0].month, ) = _daysToDate(timestamp1 / SECONDS_PER_DAY);
        (date[1].year, date[1].month, ) = _daysToDate(timestamp2 / SECONDS_PER_DAY);
        return (date[1].year - date[0].year) * 12 + date[1].month - date[0].month;
    }
}
"
