    "contracts/Runiverse/Jackpot/RuniverseJackpot.sol": {
      "content": "// SPDX-License-Identifier: MIT

pragma solidity ^0.8.9;
import "hardhat/console.sol";
import "@openzeppelin/contracts/utils/Counters.sol";
import "@openzeppelin/contracts/utils/cryptography/draft-EIP712.sol";
import "./JackpotAdministrative.sol";
import "../IRuniverse.sol";

contract RuniverseJackpot is JackpotAdministrative, ReentrancyGuard {
    using SafeERC20 for IERC20;
    using SafeMath for uint256;

    uint256 private ticket; //last registered number
    uint256 public jackpotId;
    uint256 public fromGameId; //gameId from which to start taking into consideration the runs to check if the player can participate in the jackpot

    struct Credentials {
        address playerAddress;
        uint256 gameId1;
        uint256 gameId2;
        uint256 gameId3;
        uint256 gameId4;
        uint256 gameId5;
    }

    // map rollers to requestIds
    mapping(uint256 => address) private s_rollers;
    // map vrf results to rollers
    mapping(address => uint256) private s_results;

    //mapping for the random drawing. JackpotId => playerAddress => ticketId 
    mapping(uint256 => mapping(uint256 => address)) public partecipants;
    mapping(address => uint256) private qualifiedForJackpot;
    mapping(uint256 => address) public winners;
    mapping(address => uint256) public donations;

    event DiceRolled(uint256 indexed requestId, address indexed roller);
    event DiceLanded(uint256 indexed requestId, uint256 indexed result);

    event NewPartecipant(uint256 jackpotId, address playerAddress, uint256 ticket, uint256 timestamp);
    event CheckAddress(address playerAddress, uint256 gameId);
    event Award(uint256 jackpotId, address winner, address currency, uint256 amountWon);
    event Donation(address sponsor, address currency, uint256 donatedAmount);
    event Winning(uint256 jackpotId, address winner);
    
    constructor(
        address _adminAddress,
        address _operatorAddress
    ) {
        require(_adminAddress != address(0), "Cannot be zero address");
        require(_operatorAddress != address(0), "Cannot be zero address");

        adminAddress = _adminAddress;
        operatorAddress = _operatorAddress;
        jackpotId = 9;
        ticket = 0;
        fromGameId = 8058;
        arenaId = 1;
    }
    
    function addPartecipant(Credentials calldata credentials) external onlyOperator whenNotPaused nonReentrant{
        
        require(qualifiedForJackpot[credentials.playerAddress] < jackpotId, "Player already registered");

        IRuniverse runiverse = IRuniverse(runiverseContract);

        checkAddress(credentials.playerAddress, runiverse.getRunWinner(credentials.gameId1), credentials.gameId1);
        checkAddress(credentials.playerAddress, runiverse.getRunWinner(credentials.gameId2), credentials.gameId2);
        checkAddress(credentials.playerAddress, runiverse.getRunWinner(credentials.gameId3), credentials.gameId3);
        checkAddress(credentials.playerAddress, runiverse.getRunWinner(credentials.gameId4), credentials.gameId4);
        checkAddress(credentials.playerAddress, runiverse.getRunWinner(credentials.gameId5), credentials.gameId5);

        ticket++;

        partecipants[jackpotId][ticket] = credentials.playerAddress;
        qualifiedForJackpot[credentials.playerAddress] = jackpotId;

        emit NewPartecipant(jackpotId, credentials.playerAddress, ticket, block.timestamp);
    }
 
    function drawJackpotWinner() external onlyOperator whenPaused nonReentrant {

        require(ticket > 0, "there are no player qualified");

        //Update fromGameId
        IRuniverse runiverse = IRuniverse(runiverseContract); 
        fromGameId = runiverse.biggestGameId();

        uint256 randomNumber = uint256(keccak256(abi.encodePacked(block.difficulty, _msgSender(), block.timestamp, fromGameId, ticket, jackpotId)));
        uint256 winnerTicket = (randomNumber % ticket) + 1;
        console.log("ticket", ticket);
        console.log("winnerTicket", winnerTicket);

        address winner = partecipants[jackpotId][winnerTicket];
        winners[jackpotId] = winner;

        //update JackpotId
        jackpotId++;

        ticket = 0;

        emit Winning(jackpotId - 1, winner);
        
    }

    function award(uint256 _jackpotId, address currencyFee) external onlyAdminOrOperator whenPaused nonReentrant {
        //Claim
        IRuniverse runiverse = IRuniverse(runiverseContract);
        uint256 fee = runiverse.ClaimArenaFee(arenaId, currencyFee);
        uint256 donationAmount = donations[currencyFee];
        
        //Transfer
        address winner = winners[_jackpotId];
        donations[currencyFee] = 0;
        IERC20(currencyFee).approve(address(this), fee + donationAmount);
        IERC20(currencyFee).safeTransferFrom(address(this), winner, fee + donationAmount);

        emit Award(_jackpotId, winner, currencyFee, fee + donationAmount);
    }

    function donation(address currency, uint256 amount) external whenNotPaused nonReentrant {

        IERC20(currency).safeTransferFrom(msg.sender, address(this), amount);

        if(donations[currency] == 0)
        {
            donations[currency] = amount;
        }
        else
        {
            uint256 _donation = donations[currency];
            donations[currency] = _donation + amount;
        }

        emit Donation(msg.sender, currency, amount);

    }

    function checkAddress(address playerAddress, address winnerAddress, uint256 gameId) internal
    {
        require(gameId > fromGameId, "match not valid for qualification");
        require(playerAddress == winnerAddress, "address not matching");

        emit CheckAddress(playerAddress, gameId);
    }
    
}
"
