pragma solidity ^0.8.0;

/**
 * @dev Interface of the ERC20 standard as defined in the EIP.
 */
interface IRuniverse {
    /**
     * @dev Returns the biggest gamedId used.
     */
    function biggestGameId() external view returns (uint256);

    /**
     * @dev Returns the winner by gameId.
     */
    function getRunWinner(uint256 gameId) external view returns (address);

    /**
     * @dev Returns the loser by GameId.
     */
    function getRunLoser(uint256 gameId) external view returns (address);

    function ClaimArenaFee(uint256 arenaTokenId, address currencyFee) external returns (uint256);
}
