    "contracts/Runiverse/Jackpot/JackpotAdministrative.sol": {
      "content": "// SPDX-License-Identifier: MIT

pragma solidity ^0.8.9;

import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/token/ERC721/utils/ERC721Holder.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

import "../../Modifier/JackpotModifier.sol";


abstract contract JackpotAdministrative is Ownable, JackpotModifier, ERC721Holder {
	using SafeMath for uint256;

    address public runiverseContract;
    address public arenaContract;
    uint256 public arenaId;

	event NewAdminAddress(address admin);
	event NewOperatorAddress(address operator);
    event NewRuniverseContract(address runiverse);
    event NewArenaContract(address arena);
    event NewArenaId(uint256 arenaId);

	function setAdmin(address _adminAddress) external onlyOwner {
		require(_adminAddress != address(0), "Cannot be zero address");
		adminAddress = _adminAddress;

		emit NewAdminAddress(_adminAddress);
	}

	function setOperator(address _operatorAddress) external whenPaused onlyAdmin {
		require(_operatorAddress != address(0), "Cannot be zero address");
		operatorAddress = _operatorAddress;

		emit NewOperatorAddress(_operatorAddress);
	}

    function setRuniverseContract(address _contract) external whenPaused onlyAdmin {
        require(_contract != address(0), "Cannot be zero address");
        runiverseContract = _contract;
        emit NewRuniverseContract(_contract);
    }

    function setArenaContract(address _contract) external whenPaused onlyAdmin {
        require(_contract != address(0), "Cannot be zero address");
        arenaContract = _contract;
        emit NewArenaContract(_contract);
    }

    function setArenaId(uint256 _arenaId) external whenPaused onlyAdmin {
        arenaId = _arenaId;
        emit NewArenaId(_arenaId);
    }

    function transferNFTOwnership(address newOwner) external whenPaused onlyAdmin {

        IERC721(arenaContract).safeTransferFrom(address(this), newOwner, arenaId);
        
    }
    
}
"
