    "contracts/Modifier/JackpotModifier.sol": {
      "content": "// SPDX-License-Identifier: MIT

pragma solidity ^0.8.9;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "@openzeppelin/contracts/access/Ownable.sol";


abstract contract JackpotModifier is Pausable {
	using SafeMath for uint256;

	address public adminAddress; // address of the admin
	address public operatorAddress; // address of the operator

	event Pause(uint256 blocktimestamp);
	event Unpause(uint256 indexed gameId);

	modifier onlyAdmin() {
		require(msg.sender == adminAddress, "Not admin");
		_;
	}

	modifier onlyAdminOrOperator() {
		require(msg.sender == adminAddress || msg.sender == operatorAddress, "Not operator/admin");
		_;
	}

    modifier onlyOperator() {
		require(msg.sender == operatorAddress, "Not operator");
		_;
	}

	modifier notContract() {
		require(!_isContract(msg.sender), "Contract not allowed");
		require(msg.sender == tx.origin, "Proxy contract not allowed");
		_;
	}
	
	function _isContract(address account) internal view returns (bool) {
		uint256 size;
		assembly {
			size := extcodesize(account)
		}
		return size > 0;
	}

	function pause() external whenNotPaused onlyAdminOrOperator {
		_pause();

		emit Pause(block.timestamp);
	}

	function unpause() external whenPaused onlyAdminOrOperator {
		_unpause();

		emit Unpause(block.timestamp);
	}
}
"
    },
