// File: contracts/AFRNFT.sol


pragma solidity 0.8.11;







contract AFRNFT is
    ERC2771Context,
    ERC721Enumerable,
    AccessControl,
    Ownable,
    ReentrancyGuard
{
    using Strings for uint256;

    // Prefix for tokens metadata URIs
    string public baseURI;

    // Suffix for tokens metadata URIs
    string public baseExtension = ".json";

    // Operator that sets the tradeability of this token
    bool public tradingPaused = true;

    // Claim start date
    uint256 public claimStartDate = 0;

    // Array of payout percentages
    uint8[10] public payoutPercentages = [10,10,10,10,10,10,10,10,10,10];

    // Array of days until next payout
    uint256[10] public payoutDays = [0,30,60,90,120,150,180,210,240,270];

    // Mapping of token ids to player statistics
    mapping(uint256 => uint256[12]) public TokenIdToStats;

    // Mapping of token ids to rewards
    mapping(uint256 => uint256) public TokenIdToRewards;

    // Mapping of token ids to claimed rewards
    mapping(uint256 => uint256) public TokenIdToClaimedRewards;

    // Mapping of token ids to index claimed
    mapping(uint256 => uint8) public TokenIdToIndexClaimed;

    // Role for off-chain player statistics verification
    bytes32 public constant WHITELISTER_ROLE = keccak256("WHITELISTER_ROLE");

    // Role for off-chain withdrawing verification
    bytes32 public constant WITHDRAWER_ROLE = keccak256("WITHDRAWER_ROLE");

    // Role for off-chain claim checks
    bytes32 public constant CLAIM_CHECK_ROLE = keccak256("CLAIM_CHECK_ROLE");

    // Rewards token interface
    IERC20 public rewardsToken;

    //////////////
    /// Events ///
    //////////////

    /**
     * An event that is emitted when update metadata function is called
     */
    event UpdateMetadata(address indexed _caller, uint256 indexed _tokenId, uint256[12] _playerStats);

    /**
     * An event that is emitted when a player claims their rewards
     */
    event RewardsClaimed(address indexed _caller, uint256 indexed _tokenId, uint256 indexed _transferAmount);

    /**
     * An event that is emitted when a the payout percentages change
     */
    event PayoutsChanged(uint8[10] _payoutPercentages, uint8[10] _newPayoutPercentages);

    /**
     * An event that is emitted when the days between payouts change
     */
    event PayoutDaysChanged(uint256[10] _payoutDays, uint256[10] _newPayoutDays);

    /**
     * An event that is emitted when the tokens tradeability is paused or unpaused
     */
    event TradingPaused(bool _state);

    /**
     * An event that is emitted when the contracts native chain token is withdrawn
     */
    event Withdraw(uint256 _amount);

    /**
     * An event that is emitted when the owner withdraws all the rewards token from the contract
     */
    event WithdrawRewardsToken(uint256 _amount);

 
    ///////////////////
    /// CONSTRUCTOR ///
    ///////////////////

    constructor(
        string memory _name,
        string memory _symbol,
        string memory _initBaseURI,
        address _forwarder, 
        address _rewardsToken
    ) ERC721(_name, _symbol) ERC2771Context(_forwarder) {
        setBaseURI(_initBaseURI);
        rewardsToken = IERC20(_rewardsToken);
        _setupRole(WHITELISTER_ROLE, _msgSender());
        _setupRole(WITHDRAWER_ROLE, _msgSender());
        _setupRole(CLAIM_CHECK_ROLE, _msgSender());
        _setupRole(DEFAULT_ADMIN_ROLE, _msgSender());
    }

    /////////////////
    /// FUNCTIONS ///
    /////////////////

    /**
     * Mints NFTs for multiple addresses.
     * 
     * @param _addresses the addresses to mint for.
     */ 

    function mintBatch(address[] calldata _addresses) public onlyOwner {       

        // Get Supply
        uint256 supply = totalSupply();
        uint256 count = supply + 1;

        // Loop through array of addresses
        for (uint i = 0; i < _addresses.length; i++) {

            // Check if owner has nft
            if(balanceOf(_addresses[i]) == 0) {

                // Mint for address
                _safeMint(_addresses[i], count);
                count++;
            } 
        }
    }


    /**
     * Updates a players on-chain progress to match with their in game statistics.
     * 
     * @param _playerStatistics the callers in game statistics.
     * @param _signature signed message of the stats by an address with WHITELISTER_ROLE
     */

    function updateMetadata(
        uint[12] calldata _playerStatistics,
        bytes memory _signature
    ) public {

        // Verify stats
        address player = _msgSender();

        require(balanceOf(player) > 0, "You need to have a token to update its metadata.");
        require(
            verifyStats(player, _playerStatistics, _signature) == true,
            "These statistics do not match our signature."
        );

        // Get token id
        uint256 tokenId = tokenOfOwnerByIndex(player, 0);

        // Update player stats
        TokenIdToStats[tokenId] = _playerStatistics;

        // Emit update metadata event
        emit UpdateMetadata(
            player,
            tokenId,
            TokenIdToStats[tokenId]
        );
    }

    ///////////////
    /// REWARDS ///
    ///////////////

    /**
     * A setter function to set the rewards for each token id
     *
     * @param _tokenIds array of token ids associated with the reward
     * @param _amounts array of the reward amount for the token ids
     */
    function setRewards(uint256[] calldata _tokenIds, uint256[] calldata _amounts) public onlyOwner {
        require(_tokenIds.length == _amounts.length, "Both arrays need to be the same size.");
        for (uint256 i = 0; i < _tokenIds.length; i++) {

            // Set new reward amount
            TokenIdToRewards[_tokenIds[i]] = _amounts[i];
        }
    }

    /**
     * A setter function to increase the rewards for each token id
     *
     * @param _tokenIds array of token ids associated with the reward
     * @param _amounts array of the reward amount to add for the token ids
     */
    function addRewards(uint256[] calldata _tokenIds, uint256[] calldata _amounts) public onlyOwner {
        require(_tokenIds.length == _amounts.length, "Both arrays need to be the same size.");
        for (uint256 i = 0; i < _tokenIds.length; i++) {
            
            // Add rewards to existing reward amount
            TokenIdToRewards[_tokenIds[i]] += _amounts[i];
        }
    }

    /**
     * Set the start date for players to start claiming rewards
     */
    function startClaimingPeriod() public onlyOwner {
        require(claimStartDate == 0, "Claiming has already started.");
        claimStartDate = block.timestamp;
    }
    
    /**
     * Allows players to retrieve rewards allocated to their token id
     * 
     * @param _tokenId the id of the token to claim rewards for
     * @param _signature verification that the caller is able to claim rewards tokens
     */
    function getRewards(uint256 _tokenId, bytes memory _signature) public nonReentrant {
        require(claimStartDate > 0, "Claiming has not started yet.");

        // Get player address
        address player = _msgSender();

        require(
            verifyClaim(player, _signature) == true,
            "Claim signature check invalid."
        );

        require(
            ownerOf(_tokenId) == player, 
            "You must own this token to claim the rewards."
        );

        require(
            TokenIdToRewards[_tokenId] > 0 
            && TokenIdToClaimedRewards[_tokenId] < TokenIdToRewards[_tokenId],
            "There is no reward for this token id at this time."
        );

        // Get transfer amount
        uint256 transferAmount;
        uint8 indexClaimed;
        (transferAmount, indexClaimed) = getTransferAmount(_tokenId);

        // Require the contract to have enough tokens
        require(
            rewardsToken.balanceOf(address(this)) >= transferAmount, 
            "Not enough tokens to claim yet. Please try again later."
        );

        // Update claimed amounts and index claimed for the account
        TokenIdToClaimedRewards[_tokenId] += transferAmount;
        TokenIdToIndexClaimed[_tokenId] = indexClaimed;
        // Transfer tokens
        rewardsToken.transfer(player, transferAmount);

        // Emit rewards claimed event
        emit RewardsClaimed(player, _tokenId, transferAmount);
    }

    /**
     * @dev Returns the amount of rewards token claimable for a token id
     * 
     * @param _tokenId the id of the token to find the claimable rewards for 
     */
    function getTransferAmount(uint256 _tokenId) internal view returns (uint256, uint8) {

        // Require this months rewards to be unclaimed for this token id
        uint256 daysPassed = ((block.timestamp - claimStartDate) / 1 days) + 1;
        require(
            daysPassed > payoutDays[TokenIdToIndexClaimed[_tokenId]],
            "This token's rewards have already been claimed for this period."
        );

        // Find what days the player is able to claim to
        uint8 endIndex;
        for (uint8 i = uint8(payoutDays.length); i > 0; i--) {
            if (daysPassed > payoutDays[i - 1]) {
                endIndex = i;
                break;
            }
        }

        // Caclulate the payout percentage for this token id
        uint256 percentagePayout;
        for (uint8 i = TokenIdToIndexClaimed[_tokenId]; i < endIndex; i++) {
            percentagePayout += payoutPercentages[i];
        }

        // Calculate the transfer amount
        uint256 transferAmount = (TokenIdToRewards[_tokenId] * percentagePayout) / 100;

        return (transferAmount, endIndex);
    }

    /**
     * Allows players to retrieve rewards allocated for all their held tokens
     * 
     * @param _signature verification that the caller is able to claim rewards tokens
     */
    function getRewardsBatch(bytes memory _signature) public {

        // Get player address
        address player = _msgSender();
        uint256 balance = balanceOf(player);

        // Check if owner has a token
        require(balance > 0, "You must have a token to claim rewards.");

        for (uint256 i = 0; i < balance; i++) {
            uint256 tokenId = tokenOfOwnerByIndex(player, i);
            getRewards(tokenId, _signature);
        }
    }

    /**
     * Setter function to set the payout percentages for the rewards token
     * 
     * @param _newPayoutPercentages an array of the new payout percentages
     */
    function setPayoutPercentages(uint8[10] calldata _newPayoutPercentages) public onlyOwner {

        // Find current index of payout days
        uint256 daysPassed;
        if (claimStartDate > 0) daysPassed = (block.timestamp - claimStartDate) / 1 days + 1;

        // Find the current index
        uint8 index;
        for (uint8 i = 1; i < 10; i++) {
            if (payoutDays[i] > daysPassed) {
                index = i - 1;
                break;
            }
        }

        // Calculate the new array sum
        uint8 newSum;
        for (uint8 i = 0; i < 10; i++) {
            // Require past and current payouts to be the same
            if (i <= index) {
                require(
                    _newPayoutPercentages[i] == payoutPercentages[i], 
                    "Past and current payouts must be the same."
                );
            }
            newSum += _newPayoutPercentages[i];
        }        
        
        // Require the sum of new payouts to equal 100
        require(newSum == 100, "Payouts must equal 100%.");

        // Change payout and emit event
        emit PayoutsChanged(payoutPercentages, _newPayoutPercentages);
        payoutPercentages = _newPayoutPercentages;
    }


    /**
     * Setter function to set the days between payouts for the rewards token
     * 
     * @param _newPayoutDays an array of the new days that payouts are to be given in
     */
    function setPayoutDays(uint256[10] calldata _newPayoutDays) public onlyOwner
    {
        // Get days passed
        uint256 daysPassed;
        if (claimStartDate > 0) daysPassed = (block.timestamp - claimStartDate) / 1 days + 1;

        // Require the new array to be incrementing each index
        uint8 index;
        bool indexFound = false;
        for (uint8 i = 1; i < 10; i++) {
            require(_newPayoutDays[i - 1] < _newPayoutDays[i], "Each item in payout days array must be greater than the previous.");
            if (payoutDays[i] > daysPassed && !indexFound) {
                index = i - 1;
                indexFound = true;
            }
        }
        
        // Require this periods payout days to be the same
        require(_newPayoutDays[index] == payoutDays[index], "The current index of both arrays must be the same.");

        // Change payout and emit event
        emit PayoutDaysChanged(payoutDays, _newPayoutDays);
        payoutDays = _newPayoutDays;
    }

    /**
     * Setter function to set the address of the rewards token
     * 
     * @param _tokenAddress of the new rewards token
     */
    function setRewardsToken(address _tokenAddress) public onlyOwner {
        rewardsToken = IERC20(_tokenAddress);
    }

    ///////////////
    /// HELPERS ///
    ///////////////

    /**
     * @dev Returns true if the senders statistics are signed by an account with a WHITELISTER_ROLE.
     *
     * @param _to address that is part of the verified hash
     * @param _stats player statistics that are part of the verified hash
     * @param  _signature of the signer that has WHITELISTER_ROLE
     */
    function verifyStats(
        address _to,
        uint[12] calldata _stats,
        bytes memory _signature
    ) internal view returns (bool) {
        bytes32 messageHash = keccak256(abi.encode(_to, _stats));
        bytes32 ethSignedMessageHash = keccak256(
                abi.encodePacked("\x19Ethereum Signed Message:\n32", messageHash)
            );

        bytes32 r;
        bytes32 s;
        uint8 v;

        assembly {
            r := mload(add(_signature, 32))
            s := mload(add(_signature, 64))
            v := byte(0, mload(add(_signature, 96)))
        }

        address _signer = ecrecover(ethSignedMessageHash, v, r, s);

        return hasRole(WHITELISTER_ROLE, _signer);
    }

    /**
     * @dev Returns true if the senders statistics are signed by an account with a CLAIM_CHECK_ROLE.
     *
     * @param _to address that is part of the verified hash
     * @param  _signature of the signer that has CLAIM_CHECK_ROLE
     */
    function verifyClaim(
        address _to,
        bytes memory _signature
    ) internal view returns (bool) {
        bytes32 messageHash = keccak256(abi.encode(_to));
        bytes32 ethSignedMessageHash = keccak256(
                abi.encodePacked("\x19Ethereum Signed Message:\n32", messageHash)
            );

        bytes32 r;
        bytes32 s;
        uint8 v;

        assembly {
            r := mload(add(_signature, 32))
            s := mload(add(_signature, 64))
            v := byte(0, mload(add(_signature, 96)))
        }

        address _signer = ecrecover(ethSignedMessageHash, v, r, s);

        return hasRole(CLAIM_CHECK_ROLE, _signer);
    }

    /**
     * @dev Internal baseURI getter.
     */
    function _baseURI() internal view virtual override returns (string memory) {
        return baseURI;
    }

    /**
     * @dev Returns the Uniform Resource Identifier (URI) for requested token.
     */
    function tokenURI(uint256 tokenId)
        public
        view
        virtual
        override
        returns (string memory)
    {
        require(
            _exists(tokenId),
            "AFR721: URI query for nonexistent token."
        );

        string memory currentBaseURI = _baseURI();

        return
            bytes(currentBaseURI).length > 0
                ? string(
                    abi.encodePacked(
                        currentBaseURI,
                        tokenId.toString(),
                        baseExtension
                    )
                )
                : "";
    }

    /**
     * @dev Returns true if this contract implements the interface defined by
     * `interfaceId`.
     *
     * This function call must use less than 30 000 gas.
     * 
     * @param _interfaceId to be checked for support
     */
    function supportsInterface(bytes4 _interfaceId)
        public
        view
        virtual
        override(ERC721Enumerable, AccessControl)
        returns (bool)
    {
        return super.supportsInterface(_interfaceId);
    }

    /**
     * Changes the base URI for token metadata.
     *
     * @param _newBaseURI new value
     */
    function setBaseURI(string memory _newBaseURI) 
        public 
        onlyOwner 
    {
        baseURI = _newBaseURI;
    }

    /**
     * Changes the base extension for token metadata.
     *
     * @param _newBaseExtension new value
     */
    function setBaseExtension(string memory _newBaseExtension)
        public
        onlyOwner
    {
        baseExtension = _newBaseExtension;
    }


    /**
     * Set trading state of NFT's
     *
     * @param _state operator set the contract on pause
     */
    function setTradingPaused(bool _state) public onlyOwner {
        tradingPaused = _state;
        emit TradingPaused(_state);
    }

    /**
     * Transfers the total native coin balance to contract's owner account.
     * The balance must be > 0 so a zero transfer is avoided.
     *
     */
    function withdraw() public nonReentrant onlyRole(WITHDRAWER_ROLE) {
        uint256 balance = address(this).balance;
        require(balance != 0, "AFR721: contract balance is zero.");

        (bool sent, ) = payable(owner()).call{value: balance}("");
        require(sent, "AFR721: failed to send amount.");

        emit Withdraw(balance);
    }

    /**
     * Transfers the total rewards coin balance to contract's owner account.
     * The balance must be > 0 so a zero transfer is avoided.
     *
     */
    function withdrawRewardsToken() public nonReentrant onlyRole(WITHDRAWER_ROLE) {
        uint256 balance = rewardsToken.balanceOf(address(this));
        require(balance != 0, "AFR721: contract balance is zero.");
        rewardsToken.transfer(payable(owner()), balance);

        emit WithdrawRewardsToken(balance);
    }

    /**
     * @dev Hook that is called before any token transfer. This includes minting
     * and burning.
     *
     * This does not allow any transferring of tokens while trading is paused.
     */
    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 tokenId
    ) internal virtual override {
        super._beforeTokenTransfer(from, to, tokenId);

        if (tradingPaused) require(from == address(0), "The trading of this token is paused at the moment");
    }

    /**
     * @dev function to return the msg.sender from ERC2771Context
     */
    function _msgSender()
        internal
        view
        virtual
        override(Context, ERC2771Context)
        returns (address ret)
    {
        return ERC2771Context._msgSender();
    }

    /**
     * @dev function to return the msg.data from ERC2771Context
     */
    function _msgData()
        internal
        view
        virtual
        override(Context, ERC2771Context)
        returns (bytes memory ret)
    {
        return ERC2771Context._msgData();
    }

    receive() external payable {}
}
