// SPDX-License-Identifier: MIT LICENSE

pragma solidity 0.8.9;



// Part: IBarn

interface IBarn {
    function addManyToBarnAndPack(address account, uint16[] calldata tokenIds)
        external;

    function randomWolfOwner(uint256 seed) external view returns (address);
}

// Part: IEntropy

interface IEntropy {
    function random(uint256 seed) external view returns (uint256);
}

// Part: ITraits

interface ITraits {
    function tokenURI(uint256 tokenId) external view returns (string memory);
}

// Part: IWoolf

interface IWoolf {
    // struct to store each token's traits
    struct SheepWolf {
        bool isSheep;
        uint8 fur;
        uint8 head;
        uint8 ears;
        uint8 eyes;
        uint8 nose;
        uint8 mouth;
        uint8 neck;
        uint8 feet;
        uint8 alphaIndex;
    }

    function getPaidTokens() external view returns (uint256);

    function getTokenTraits(uint256 tokenId)
        external
        view
        returns (SheepWolf memory);
}

