// Part: WOOL

contract WOOL is ERC20, Ownable {
    // a mapping from an address to whether or not it can mint / burn
    mapping(address => bool) controllers;

    constructor() ERC20("FFWOOL", "FFWOOL") {}

    /**
     * mints $FFWOOL to a recipient
     * @param to the recipient of the $FFWOOL
     * @param amount the amount of $FFWOOL to mint
     */
    function mint(address to, uint256 amount) external {
        require(controllers[msg.sender], "Only controllers can mint");
        _mint(to, amount);
    }

    /**
     * burns $FFWOOL from a holder
     * @param from the holder of the $FFWOOL
     * @param amount the amount of $FFWOOL to burn
     */
    function burn(address from, uint256 amount) external {
        require(controllers[msg.sender], "Only controllers can burn");
        _burn(from, amount);
    }

    /**
     * enables an address to mint / burn
     * @param controller the address to enable
     */
    function addController(address controller) external onlyOwner {
        controllers[controller] = true;
    }

    /**
     * disables an address from minting / burning
     * @param controller the address to disbale
     */
    function removeController(address controller) external onlyOwner {
        controllers[controller] = false;
    }

    // mintCost doesn't need to be changed this way.
    function decimals() public view virtual override returns (uint8) {
        return 9;
    }
}

