// Welcome to cosmix casino smart contract

// Extends the OpenZeppelin ERC20 contract, a huge thank you to OZ teams great work!
contract Token is ERC20 {
    // Users history required for calculating number of shares owed
    mapping(address => mapping(uint256 => uint256)) public userBalanceHistory;

    // Past performance of casino, used to divide shares between users
    mapping(uint256 => uint256) public circulationHistory;
    mapping(uint256 => uint256) public profitHistory;

    // Addresses which are excluded from receiving shares, casino, owner, prodit etc.
    mapping(address => bool) public isExcluded;

    // User lottery tickets
    address[] public ticketsAddress;
    uint256[] public ticketsAmount;

    // Key variables for each user
    struct UserSave {
        // Period which user last withdrew funds
        uint256 period;
        // Total number of lottery tickets owned
        uint256 tickets;
        // Amount of shares withdrawn, minimises computation
        uint256 shares;
        // Last nonce used for signatures
        uint256 nonce;
        // Hash of comitted random number set by the casino
        bytes32 seedHash;
        // Block number which the random number was comitted
        uint256 blockNumber;
        // Casino approval setting
        bool lossLimit;
        // Last period that the user is excluded from lottery win
        uint256 lottoExclusion;
        // First period that user is active, eases pulling historic data
        uint256 firstPeriod;
        // Required for setting first period
        bool isInitialised;
        // Allows users to disable signatures which can distupt gameplay
        bool signatureDisabled;
    }

    struct KeyAddress {
        address owner;
        address dealer;
        address marketing;
        address userProfits;
        address liquidityPool;
    }

    // Casino variables
    struct Store {
        uint256 lotteryTotal;
        uint256 currentPeriod;
        uint256 initialBalance;
        uint256 payoutTime;
        uint256 lastBlock;
        uint256 circulation;
        uint256 lotteryIndex;
        uint256 lotterySum;
        uint256 profitCount;
        uint256 rollOver;
        uint256 contractStart;
    }

    struct Lottery {
        uint256 period;
        address winner;
        uint256 value;
    }

    struct SignData {
        uint256 amount;
        string action;
        uint256 deadline;
        uint256 nonce;
        bytes signature;
    }

    Lottery[] public lotteryHistory;

    Store public store;
    KeyAddress public keyAddress;

    mapping(address => UserSave) public userSave;
    mapping(address => bool) public _approvedGames;

    // Used to track play and payout history
    event Payment(
        address user,
        int256 value,
        string name,
        uint256 period,
        uint256 timeStamp
    );

    // Emits details of the lottery draw allowing validation
    event LottoDraw(
        address winner,
        uint256 value,
        uint256 result,
        uint256 seed,
        bytes32 seedHash,
        uint256 blockNumber,
        address userAddress,
        uint256 period
    );

    // Hash informaion to validate each random number generation
    event HashSet(address user, bytes32 seedHash, uint256 blockTime);

    event GameAdded(address gameAddress);
    event GameRemoved(address gameAddress);

    constructor() ERC20("Cosmix Token", "COSMIX") {
        keyAddress.owner = msg.sender;

        // Mint 1.28B CMX
        _mint(keyAddress.owner, 1280000000 * 10**18);

        /* solhint-disable not-rely-on-time */
        store.payoutTime = block.timestamp + (1 * 24 * 60 * 60);

        // Sets first lottery position as this address
        ticketsAddress.push(address(this));
        ticketsAmount.push(0);
        store.lotteryIndex = 1;

        isExcluded[msg.sender] = true;
        isExcluded[address(this)] = true;

        lotteryHistory.push(Lottery(0, address(0), 0));

        store.contractStart = block.timestamp;
    }

    // Provides the casino dealer with approval to move users funds on their behalf
    // Provides better experince by avoiding approvals as well as free gas

    // Users can do this manualy using the super approve
    // This method exists so that users can change the amounts without the need for gas

    // Safe guards have been put in place to prevent any casino missuse
    // User Signature is required to prevent the Casino from self approving access to funds
    // Signatures also verifies that the amount has not been untampered
    // Nonce is used to prevent any re-use of known signatures

    function setCasinoLimit(
        address user,
        uint256 amount,
        SignData memory signData
    ) external returns (bool) {
        require(msg.sender == keyAddress.dealer, "Not Dealer");

        require(validateSignature(user, signData), "Invalid Signature");
        require(signData.nonce > userSave[user].nonce, "Already Known");
        require(
            compareStrings(signData.action, "Casino Limit"),
            "Invalid Signature Action"
        );
        require(signData.amount == amount, "Invalid amount signed");
        userSave[user].nonce++;

        super._approve(user, keyAddress.dealer, amount);
        return true;
    }

    function compareStrings(string memory a, string memory b)
        public
        pure
        returns (bool)
    {
        return (keccak256(abi.encodePacked((a))) ==
            keccak256(abi.encodePacked((b))));
    }

    function disableSignature() public returns (bool) {
        userSave[msg.sender].signatureDisabled = !userSave[msg.sender]
            .signatureDisabled;
        return true;
    }

    function disableSignatureCasino(address user, SignData memory signData)
        public
        returns (bool)
    {
        require(msg.sender == keyAddress.dealer, "Not Dealer");

        require(validateSignature(user, signData), "Invalid Signature");

        if (userSave[user].signatureDisabled) {
            // Enable Signature
            require(
                compareStrings(signData.action, "Enable Signature"),
                "Invalid Signature Action"
            );
            userSave[user].signatureDisabled = false;
        } else {
            // Diable Signature
            require(
                compareStrings(signData.action, "Disable Signature"),
                "Invalid Signature Action"
            );
            userSave[user].signatureDisabled = true;
        }

        return true;
    }

    // EIP712 Signature Standard
    function validateSignature(address signer, SignData memory signData)
        public
        view
        returns (bool)
    {
        // Valid user signature always required for dealer to update approval limit
        if (
            userSave[signer].signatureDisabled &&
            !compareStrings(signData.action, "Casino Limit")
        ) {
            return true;
        }

        require(
            block.timestamp < signData.deadline,
            "Signed transaction expired"
        );
        require(
            userSave[signer].nonce < signData.nonce,
            "Signed transaction known"
        );

        uint256 _chainId;
        assembly {
            _chainId := chainid()
        }

        bytes32 eip712DomainHash = keccak256(
            abi.encode(
                keccak256(
                    "EIP712Domain(string name,string version,uint256 chainId,address verifyingContract)"
                ),
                keccak256(bytes("Cosmix Casino")),
                keccak256(bytes("1")),
                _chainId,
                address(this)
            )
        );

        bytes32 hashStruct = keccak256(
            abi.encode(
                keccak256(
                    "set(address sender,uint256 amount,string action,uint256 deadline,uint256 nonce)"
                ),
                signer,
                signData.amount,
                keccak256(bytes(signData.action)),
                signData.deadline,
                signData.nonce
            )
        );

        bytes32 hash = keccak256(
            abi.encodePacked("\x19\x01", eip712DomainHash, hashStruct)
        );

        uint8 v;
        bytes32 r;
        bytes32 s;
        bytes memory sig = signData.signature;

        assembly {
            r := mload(add(sig, 32))
            s := mload(add(sig, 64))
            v := byte(0, mload(add(sig, 96)))
        }
        if (v < 27) v += 27;

        address signerRecovered = ecrecover(hash, v, r, s);

        require(signer == signerRecovered, "Invalid signature");
        require(signerRecovered != address(0), "Invalid signature");

        return signer == signerRecovered;
    }

    // Override to display the number of shares owed to users
    function balanceOf(address account)
        public
        view
        virtual
        override
        returns (uint256)
    {
        uint256 base = super.balanceOf(account);
        uint256 shares = sharesOwed(account);
        return base + shares;
    }

    // Get Total Shares Owed, that have not yet been withdrawn
    function sharesOwed(address user) public view returns (uint256) {
        return _sharesOwed(user, false, false);
    }

    // Get estimated shares for the current period
    function sharesUpcoming(address user) public view returns (uint256) {
        return _sharesOwed(user, true, false);
    }

    // Get shares received in the previous period
    function sharesLast(address user) public view returns (uint256) {
        return _sharesOwed(user, false, true);
    }

    // Get Total Shares
    function _sharesOwed(
        address user,
        bool upcoming,
        bool lastPeriod
    ) public view returns (uint256) {
        // Only users can receive shares, else return zero
        if (!isUser(user)) {
            return 0;
        }

        uint256 startPeriod = userSave[user].period;

        uint256 userBalance = userBalanceHistory[user][startPeriod];
        uint256 sharesCount = 0;

        // If widthdrawn last period, then no shares owed
        if (
            startPeriod == store.currentPeriod &&
            upcoming == false &&
            lastPeriod == false
        ) {
            return 0;
        }

        // If user has never saved, then get the first period
        if (startPeriod == 0) {
            startPeriod = userSave[user].firstPeriod;
        }

        if (lastPeriod && startPeriod != 0) {
            startPeriod -= 1;
        }

        for (uint256 i = startPeriod; i < store.currentPeriod; i++) {
            uint256 share = ((userBalance + sharesCount) * profitHistory[i]) /
                circulationHistory[i];
            sharesCount += share;
            if (userBalanceHistory[user][i + 1] != 0) {
                userBalance = userBalanceHistory[user][i + 1];
            }
            if (lastPeriod && i == store.currentPeriod - 1) {
                return share;
            }
        }

        if (upcoming) {
            // Returns the amount of shares expected in the current period
            // if casino in profit, else return 0 upcoming
            if (balanceOf(address(this)) > store.initialBalance) {
                uint256 profits = balanceOf(address(this)) -
                    store.initialBalance;
                uint256 circ = store.circulation + store.profitCount + profits;
                profits = (profits * 60) / 100;
                if (userBalance == 0) {
                    userBalance = super.balanceOf(user);
                }
                uint256 share = ((userBalance + sharesCount) * profits) / circ;
                return share;
            } else {
                return 0;
            }
        }

        return sharesCount;
    }

    // If entered into a new period, then transfer from profit holdings address to user
    function withdraw(address user) private returns (bool) {
        // Only users can withdraw
        if (!isUser(user)) {
            return true;
        }

        uint256 startPeriod = userSave[user].period;

        // If last withdraw was in the past
        if (store.currentPeriod > startPeriod) {
            uint256 shares = sharesOwed(user);
            if (shares > 0) {
                _transfer(keyAddress.userProfits, user, shares);
            }

            userBalanceHistory[user][store.currentPeriod] =
                balanceOf(user) -
                shares;
            userSave[user].period = store.currentPeriod;
            userSave[user].shares += shares;
            userSave[user].tickets = 0;
        }
        return true;
    }

    function isUser(address _address) public view returns (bool) {
        return !isExcluded[_address];
    }

    // Ends the buy only phase, to protect against unlikly lock out issues
    function endBuyOnly() public returns (bool) {
        store.contractStart = 0;
        return true;
    }

    function excludeAddress(address _address) external returns (bool) {
        require(msg.sender == keyAddress.owner, "Not Owner");

        if (!isExcluded[_address]) {
            isExcluded[_address] = true;
            uint256 balance = balanceOf(_address);
            store.circulation -= balance;
            if (store.currentPeriod == 0) {
                circulationHistory[0] -= balance;
            }
        } else {
            // If already excluded the undo
            isExcluded[_address] = false;
            uint256 balance = balanceOf(_address);
            store.circulation += balance;
            if (store.currentPeriod == 0) {
                circulationHistory[0] += balance;
            }
        }

        return true;
    }

    // Modified so the casino can track the number of tokens in circulation
    // Also allows the removal/reduction of upcoming shares for tokens sold and/or transfered
    function _transfer(
        address sender,
        address recipient,
        uint256 amount
    ) internal virtual override {
        require(sender != address(0), "ERC20: transfer from the zero address");
        require(recipient != address(0), "ERC20: transfer to the zero address");
        require(
            balanceOf(sender) >= amount,
            "ERC20: transfer amount exceeds balance"
        );

        // Avoids infinate loop from withdraw function
        if (
            sender != keyAddress.userProfits &&
            recipient != keyAddress.userProfits
        ) {
            // Transfer any shares owed to the sender and recipient
            withdraw(sender);
            withdraw(recipient);

            // If Non-User to User, increase circulation
            if (!isUser(sender) && isUser(recipient)) {
                store.circulation += amount;
            }

            // If User to Non-User, decrease circulation
            if (isUser(sender) && !isUser(recipient)) {
                require(
                    store.circulation >= amount,
                    "Amout greater than circulation"
                );
                store.circulation -= amount;

                // if user selling then remove shares and lottery
                if (
                    recipient == keyAddress.liquidityPool &&
                    store.currentPeriod != 0
                ) {
                    // 8 week initial sell only period
                    require(
                        block.timestamp > store.contractStart + 4838400,
                        "Cannot sell during ICO period"
                    );

                    uint256 senderBalance = userBalanceHistory[sender][
                        store.currentPeriod
                    ];
                    if (senderBalance > 1) {
                        require(
                            circulationHistory[store.currentPeriod] >=
                                senderBalance,
                            "Circulation error"
                        );
                        circulationHistory[store.currentPeriod] -= (
                            senderBalance
                        );

                        userBalanceHistory[sender][store.currentPeriod] = 1;

                        // Add lottery exclusion
                        userSave[sender].lottoExclusion = store.currentPeriod;
                    }
                }
            }

            // If User to User, then reduce the shares owed for this period
            if (
                isUser(sender) && isUser(recipient) && store.currentPeriod != 0
            ) {
                if (userBalanceHistory[sender][store.currentPeriod] > 1) {
                    require(
                        circulationHistory[store.currentPeriod] >= amount,
                        "Amout greater than circulation"
                    );
                    circulationHistory[store.currentPeriod] -= amount;

                    require(
                        userBalanceHistory[sender][store.currentPeriod] >=
                            amount,
                        "Amout greater than balance"
                    );
                    userBalanceHistory[sender][store.currentPeriod] -= amount;
                }
            }
        }

        super._transfer(sender, recipient, amount);

        // Update senders shares for the next period
        if (isUser(sender)) {
            uint256 senderBalance = balanceOf(sender);
            userBalanceHistory[sender][store.currentPeriod + 1] = senderBalance;
            if (store.currentPeriod == 0) {
                userBalanceHistory[sender][0] = senderBalance;
            }
        }

        // Update recipient shares for the next period
        if (isUser(recipient)) {
            uint256 recipientBalance = balanceOf(recipient);
            userBalanceHistory[recipient][
                store.currentPeriod + 1
            ] = recipientBalance;

            if (store.currentPeriod == 0) {
                userBalanceHistory[recipient][0] = recipientBalance;
            }

            if (!userSave[recipient].isInitialised) {
                userSave[recipient].isInitialised = true;
                userSave[recipient].firstPeriod = store.currentPeriod;
            }
        }
    }

    //to receive MATIC from quickswap when swapping
    /* solhint-disable no-empty-blocks */
    receive() external payable {}

    // Split funds in half, converts first half into MATIC
    // Used with second half to add liquidity
    function swapAndLiquify(uint256 tokenAmount) private returns (bool) {
        Router router = Router(0xa5E0829CaCEd8fFDD4De3c43696c57F7D7A678ff);

        uint256 half = tokenAmount / 2;
        uint256 otherHalf = tokenAmount - half;

        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = router.WETH();

        _approve(address(this), address(router), half);
        uint256 received = router.swapExactTokensForETH(
            half,
            0,
            path,
            address(this),
            block.timestamp + (60 * 10)
        )[1];

        _approve(address(this), address(router), otherHalf);
        router.addLiquidityETH{value: received}(
            address(this),
            otherHalf,
            0,
            0,
            address(this),
            block.timestamp + (60 * 10)
        );

        return true;
    }

    function payOut(uint256 _random) private returns (address, uint256) {
        address winner;
        uint256 lotteryAmount;

        // Profits only shares if balance is greater than initial plus any rollover amount due
        if (balanceOf(address(this)) > store.initialBalance + store.rollOver) {
            if (store.currentPeriod != 0) {
                uint256 profits = balanceOf(address(this)) -
                    store.initialBalance;

                profits -= store.rollOver;

                lotteryAmount = store.rollOver + ((profits * 20) / 100);
                uint256 poolAmount = (profits * 5) / 100;
                uint256 overheadAmount = (profits * 15) / 100;

                winner = getLottoWinner(_random);

                // If user is excluded then do not give funds
                if (winner != address(0)) {
                    giveFunds(winner, lotteryAmount, "lottery");
                    store.rollOver = 0;
                } else {
                    // Roll over lottery to next period
                    store.rollOver += lotteryAmount;
                    lotteryAmount = 0;
                    winner = address(0);
                }

                lotteryHistory.push(
                    Lottery(store.currentPeriod, winner, lotteryAmount)
                );

                swapAndLiquify(poolAmount);

                super._transfer(
                    address(this),
                    keyAddress.marketing,
                    overheadAmount
                );

                emitEvent(
                    keyAddress.liquidityPool,
                    poolAmount,
                    "liquidity",
                    false
                );

                emitEvent(
                    keyAddress.marketing,
                    overheadAmount,
                    "overHead",
                    false
                );
            } else {
                store.lotterySum = 0;
                store.lotteryIndex = 1;
            }

            // Send remainder to users as shares
            uint256 sharesAmount = balanceOf(address(this)) -
                store.initialBalance;

            // Remaining to be divided to players
            profitHistory[store.currentPeriod] = sharesAmount;
            store.profitCount += sharesAmount;

            circulationHistory[store.currentPeriod + 1] =
                store.circulation +
                store.profitCount;

            if (store.currentPeriod == 0) {
                circulationHistory[0] = store.circulation;
            }

            _transfer(address(this), keyAddress.userProfits, sharesAmount);
            emitEvent(keyAddress.userProfits, sharesAmount, "shares", false);

            store.currentPeriod++;
        }

        return (winner, lotteryAmount);
    }

