pragma solidity ^0.8.17;

interface ITranche {
    /* EVENTS */
    event TokenTrancheAdded(address projectToken, uint8 trancheAction, Tranche tranche, uint8 trancheIndex);

    /* ERRORS */
    error OwnerAddressZero();
    error TrancheAddressZero();
    error TokenAddressZero();
    error TokenManagerAddressZero();
    error InvalidAmount(uint256 amount);
    error InvalidStartDate(uint32 start);
    error InvalidDuration(uint32 duration);
    error TrancheHashUnknown(bytes32 vestedTrancheHash);
    error TrancheAlreadyCreated(bytes32 vestedTrancheHash);
    error InvalidMaxSupplyAmount();

    /* STRUCTS & ENUMS */
    struct Tranche {
        TrancheAction action;
        TrancheParams params;
    }

    struct TrancheParams {
        // beneficiary of tokens after they are released
        address account;
        // total amount of tokens assigned to the tranche
        uint256 amount;
        // start time of the vesting period (set only if action = vest otherwise can be 0)
        uint32 start;
        // duration of the vesting period in seconds (set only if action = vest otherwise can be 0)
        uint32 duration;
    }

    /*
     * none - no action is done
     * mint - new tokens are minted immediately
     * vest - new tokens are minted gradually after a certain period of time
     * fundraise - no action is done, tokens are for fundraising phases
     */
    enum TrancheAction {
        none,
        mint,
        vest,
        fundraise
    }
}
"
    },
    "contracts/projectToken/ProjectToken.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.17;

import {Initializable} from "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import {ERC20Upgradeable} from "@openzeppelin/contracts-upgradeable/token/ERC20/ERC20Upgradeable.sol";
import {IAccessControlUpgradeable} from "@openzeppelin/contracts-upgradeable/access/IAccessControlUpgradeable.sol";
import {AccessControlUpgradeable} from "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";
import {IProjectToken} from "./IProjectToken.sol";
import {IStakeTokenBase} from "../stakeToken/IStakeTokenBase.sol";
import {StakeTokenBase} from "../stakeToken/StakeTokenBase.sol";

contract ProjectToken is IProjectToken, Initializable, ERC20Upgradeable, AccessControlUpgradeable, StakeTokenBase {
    bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");
    bytes32 public constant MINTER_ADMIN_ROLE = keccak256("MINTER_ADMIN_ROLE");

    // all tokens that will ever be minted
    uint256 private maxSupply;

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    /**
     * @dev Initialize Project Token contract.
     * @param _name Project Token name
     * @param _symbol Project Token symbol
     * @param _tokenOwner address of the token owner
     * @param _maxTokenSupply amount of all tokens that will ever be minted
     * @param _rewardTokens array of the reward token parameters
     */
    function initialize(
        string memory _name,
        string memory _symbol,
        address _tokenOwner,
        uint256 _maxTokenSupply,
        RewardTokenData[] calldata _rewardTokens
    ) public initializer {
        __ERC20_init(_name, _symbol);

        if (_tokenOwner == address(0)) revert AddressZero();
        __AccessControl_init();

        // set Token Factory as minter admin to mint tranches
        _setRoleAdmin(MINTER_ROLE, MINTER_ADMIN_ROLE);
        _grantRole(MINTER_ADMIN_ROLE, _tokenOwner);
        _grantRole(MINTER_ADMIN_ROLE, _msgSender());

        _grantRole(DEFAULT_ADMIN_ROLE, _tokenOwner);
        _grantRole(OWNER_ROLE, _tokenOwner);

        _setRewardTokens(_rewardTokens);
        _setMaxSupply(_maxTokenSupply);
    }

    /**
     * @dev Creates tokens and assigns them to account increasing the total supply.
     * @param account investor address
     * @param amount token amount
     */
    function mint(address account, uint256 amount) external onlyRole(MINTER_ROLE) {
        if (amount == 0) revert AmountZero();
        if (totalSupply() + amount > maxSupply) revert TotalSupplyExceedsMaxSupply(totalSupply(), amount);
        _mint(account, amount);
        _updateStakeSum(account);
    }

    /**
     * @dev Destroys tokens from account, reducing the total supply.
     * @param account investor address
     * @param amount token amount
     */
    function burnFrom(address account, uint256 amount) external onlyRole(MINTER_ROLE) {
        _spendAllowance(account, _msgSender(), amount);
        _burn(account, amount);
    }

    /**
     * @dev Returns amount of all tokens that will ever be minted.
     */
    function maxTokenSupply() external view returns (uint256) {
        return maxSupply;
    }

    function _setMaxSupply(uint256 _maxTokenSupply) internal {
        if (_maxTokenSupply == 0) revert InvalidMaxSupplyAmount();
        maxSupply = _maxTokenSupply;
        emit MaxSupplySet(_maxTokenSupply);
    }

    /**
     * @dev Returns the number of decimals used to get its user representation.
     * @notice The following function override is required by Solidity.
     */
    function decimals() public view virtual override(ERC20Upgradeable, IProjectToken) returns (uint8) {
        return super.decimals();
    }

    /**
     * @dev See {IERC165-supportsInterface}.
     * The following functions are overrides required by Solidity.
     */
    function supportsInterface(
        bytes4 interfaceId
    ) public view virtual override(AccessControlUpgradeable, StakeTokenBase) returns (bool) {
        return
            interfaceId == type(IAccessControlUpgradeable).interfaceId ||
            interfaceId == type(IStakeTokenBase).interfaceId ||
            super.supportsInterface(interfaceId);
    }

    /**
     * @dev Hook that is called before any transfer of tokens. This includes
     * minting and burning.
     */
    function _beforeTokenTransfer(address from, address to, uint256 amount) internal override {
        super._beforeTokenTransfer(from, to, amount);
        // double call for burn/mint case - address(0) check and skips within functions
        // claim rewards from stake
        _beforeStakeTokenTransfer(from);
        _beforeStakeTokenTransfer(to);
    }
}
"
    },
    "contracts/rewardToken/IRewardToken.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.17;

interface IRewardToken {
    function mint(uint64 tokenId, address account, uint256 amount) external;

    function transfer(uint64 tokenId, address to, uint256 amount) external;
}
"
    },
    "contracts/rewardToken/IRewardTokenParams.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.17;

interface IRewardTokenParams {
    /* EVENTS */
    event RewardTokenAdded(RewardTokenData token);

    /* ERRORS */
    error AmountZero();
    error AddressZero();
    error InsufficientBalance();
    error TokenIdRedeclaration();

    /* STRUCTS & ENUMS */
    struct RewardTokenData {
        // reward token address
        address token;
        // parameters described below
        RewardTokenParams params;
    }

    struct RewardTokenParams {
        // ERC1155 id (omitted by ERC20 - standard unification)
        // must be 0 for ERC20
        uint64 idERC1155;
        // dailyReward - amount of new tokens for 1 staked token per 1 day
        // must have decimals equal to the reward token's decimals (default 18)
        uint256 dailyReward;
    }

    struct LiquidStakeParams {
        // sum of all rewards (reward token)
        uint256 latestRewardsSum;
        // sum of all rewards claimed by all investors (reward token)
        uint256 totalRewardsClaimed;
        // stake parameter used for reward calculation
        uint256 latestStakeSum;
        // investor => latestStakeSum
        mapping(address => uint256) stakeSum;
    }
}
"
    },
    "contracts/stakeToken/IStakeTokenBase.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.17;

import {IRewardTokenParams} from "../rewardToken/IRewardTokenParams.sol";

interface IStakeTokenBase is IRewardTokenParams {
    /* EVENTS */
    event RewardTokensClaimed(address rewardToken, uint64 rewardTokenId, address account, uint256 amount);
    event RewardTokensDistributed(address rewardToken, uint64 rewardTokenId, uint256 amount);

    /* ERRORS */
    error RewardTokenAddressZero();

    /* FUNCTIONS */
    function claimTimeStakeReward(address rewardToken) external;

    function claimTimeStakeRewardFrom(address rewardToken, address account) external;

    function claimLiquidStakeReward(address rewardToken) external;

    function claimLiquidStakeRewardFrom(address rewardToken, address account) external;

    function distributeLiquidStakeReward(address rewardToken) external;

    function setTokenIdERC1155(address rewardToken, uint64 tokenId) external;

    function mintRewardToken(address rewardToken, address account, uint256 reward) external;

    function calculateTimeStakeReward(address rewardToken, address account) external view returns (uint256);

    function calculateLiquidStakeReward(
        address rewardToken,
        address account
    ) external view returns (uint256 investorReward);

    function getRewardTokenId(address rewardToken) external view returns (uint64 id);
}
"
