    "contracts/stakeToken/StakeTokenBase.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.17;

import {IERC20} from "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import {IERC1155} from "@openzeppelin/contracts/token/ERC1155/IERC1155.sol";
import {IAccessControlUpgradeable} from "@openzeppelin/contracts-upgradeable/access/IAccessControlUpgradeable.sol";
import {AccessControlUpgradeable} from "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";
import {ERC20Upgradeable} from "@openzeppelin/contracts-upgradeable/token/ERC20/ERC20Upgradeable.sol";
import {ERC1155HolderUpgradeable} from "@openzeppelin/contracts-upgradeable/token/ERC1155/utils/ERC1155HolderUpgradeable.sol";
import {IERC1155ReceiverUpgradeable} from "@openzeppelin/contracts-upgradeable/token/ERC1155/IERC1155ReceiverUpgradeable.sol";
import {ERC1155ReceiverUpgradeable} from "@openzeppelin/contracts-upgradeable/token/ERC1155/utils/ERC1155ReceiverUpgradeable.sol";
import {ContextUpgradeable} from "@openzeppelin/contracts-upgradeable/utils/ContextUpgradeable.sol";
import {SafeERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import {IStakeTokenBase} from "./IStakeTokenBase.sol";
import {IRewardToken} from "../rewardToken/IRewardToken.sol";

abstract contract StakeTokenBase is
    IStakeTokenBase,
    ERC20Upgradeable,
    ERC1155HolderUpgradeable,
    AccessControlUpgradeable
{
    // allows to use safe safeTransfer
    using SafeERC20 for IERC20;

    bytes32 public constant OWNER_ROLE = keccak256("OWNER_ROLE");
    bytes32 public constant REWARD_MINTER_ROLE = keccak256("REWARD_MINTER_ROLE");

    // address of the Reward Token
    address[] public rewardTokensList;

    // reward token => reward token params
    // used for liquid stake rewards (e.g. stablecoin) OR time-based staking
    mapping(address => RewardTokenParams) public rewardTokens;

    // denominator for liquid stake reward calculation
    uint64 private constant _LIQUID_STAKE_DENOMINATOR = 10 ** 18;

    // reward token => liquid stake params
    mapping(address => LiquidStakeParams) private liquidStakes;

    // reward token => account address => latest claim timestamp
    mapping(address => mapping(address => uint256)) private latestClaimTimestamp;

    /**
     * @dev Sets ERC1155 token id.
     * @param rewardToken reward token address
     * @param tokenId ERC1155 token id which was set while ERC1155 token creation
     */
    function setTokenIdERC1155(address rewardToken, uint64 tokenId) external onlyRole(OWNER_ROLE) {
        if (rewardTokens[rewardToken].idERC1155 != 0 || rewardTokens[rewardToken].idERC1155 == tokenId)
            revert TokenIdRedeclaration();
        rewardTokens[rewardToken].idERC1155 = tokenId;
    }

    /**
     * @dev Claims reward from time based staking.
     */
    function claimTimeStakeReward(address rewardToken) external {
        _claimTimeStakeRewardFrom(rewardToken, _msgSender());
    }

    /**
     * @dev Claims reward from time based staking for specific address.
     * @param account investor address
     */
    function claimTimeStakeRewardFrom(address rewardToken, address account) external {
        _claimTimeStakeRewardFrom(rewardToken, account);
    }

    /**
     * @dev Claims reward from liquid staking.
     */
    function claimLiquidStakeReward(address rewardToken) external {
        _claimLiquidStakeReward(rewardToken, _msgSender());
    }

    /**
     * @dev Claims reward from liquid staking for specific address.
     * @param account investor address
     */
    function claimLiquidStakeRewardFrom(address rewardToken, address account) external {
        _claimLiquidStakeReward(rewardToken, account);
    }

    /**
     * @dev Distributes newly sent reward token funds for liquid staking.
     */
    function distributeLiquidStakeReward(address rewardToken) external {
        _checkIfRewardToDistribute(rewardToken);
    }

    /**
     * @dev Mints reward tokens.
     * @notice Should be used only by reward divider.
     */
    function mintRewardToken(
        address rewardToken,
        address account,
        uint256 amount
    ) external onlyRole(REWARD_MINTER_ROLE) {
        _mintRewardToken(rewardToken, account, amount);
    }

    /**
     * @dev Calculates reward amount from liquid staking which investor can claim at the moment.
     * @param account investor address
     */
    function calculateLiquidStakeReward(address rewardToken, address account) external view returns (uint256 reward) {
        return _calculateLiquidStakeReward(rewardToken, account);
    }

    /**
     * @dev Calculates reward amount from time based staking which investor can claim at the moment.
     * @param rewardToken reward token address
     * @param account investor address
     */
    function calculateTimeStakeReward(address rewardToken, address account) external view returns (uint256) {
        return _calculateTimeStakeReward(rewardToken, account);
    }

    /**
     * @dev Returns reward token parameters: token address, ERC-1155 Id and daily reward value
     * @param rewardToken reward token address
     */
    function getRewardTokenId(address rewardToken) external view returns (uint64 id) {
        id = rewardTokens[rewardToken].idERC1155;
    }

    function _setRewardTokens(RewardTokenData[] memory _rewardTokens) internal {
        for (uint8 i = 0; i < _rewardTokens.length; i++) {
            address tokenAddress = _rewardTokens[i].token;
            if (tokenAddress == address(0)) revert RewardTokenAddressZero();
            rewardTokens[tokenAddress] = _rewardTokens[i].params;
            rewardTokensList.push(tokenAddress);
            emit RewardTokenAdded(_rewardTokens[i]);
        }
    }

    function _claimTimeStakeRewardFrom(address rewardToken, address account) internal {
        if (account == address(0)) return;
        if (latestClaimTimestamp[rewardToken][account] > 0) {
            uint256 reward = _calculateTimeStakeReward(rewardToken, account);
            if (reward > 0) {
                _mintRewardToken(rewardToken, account, reward);
                emit RewardTokensClaimed(rewardToken, rewardTokens[rewardToken].idERC1155, account, reward);
            }
        } else {
            latestClaimTimestamp[rewardToken][account] = block.timestamp;
        }
    }

    function _claimLiquidStakeReward(address rewardToken, address account) internal returns (uint256 reward) {
        if (account == address(0)) return 0;
        uint64 id = rewardTokens[rewardToken].idERC1155;
        _checkIfRewardToDistribute(rewardToken);

        reward = _calculateLiquidStakeReward(rewardToken, account);
        liquidStakes[rewardToken].stakeSum[account] = liquidStakes[rewardToken].latestStakeSum; // protect against double function call

        //transfer reward token
        if (reward > 0) {
            liquidStakes[rewardToken].totalRewardsClaimed += reward;
            if (id == 0) {
                IERC20(rewardToken).transfer(account, reward);
            } else {
                IERC1155(rewardToken).safeTransferFrom(address(this), account, id, reward, "");
            }
            emit RewardTokensClaimed(rewardToken, id, account, reward);
        }
    }

    function _mintRewardToken(address rewardToken, address account, uint256 amount) internal {
        IRewardToken(rewardToken).mint(rewardTokens[rewardToken].idERC1155, account, amount);
        latestClaimTimestamp[rewardToken][account] = block.timestamp;
    }

    function _checkIfRewardToDistribute(address rewardToken) internal {
        // check if any new reward was transferred to distribute
        uint256 currentRewardsBalance;
        if (rewardTokens[rewardToken].idERC1155 != 0) {
            currentRewardsBalance = IERC1155(rewardToken).balanceOf(address(this), rewardTokens[rewardToken].idERC1155);
        } else {
            currentRewardsBalance = IERC20(rewardToken).balanceOf(address(this));
        }
        uint256 newRewardAmount = currentRewardsBalance +
            liquidStakes[rewardToken].totalRewardsClaimed -
            liquidStakes[rewardToken].latestRewardsSum;
        liquidStakes[rewardToken].latestRewardsSum += newRewardAmount;

        if (newRewardAmount > 0 && totalSupply() > 0) {
            liquidStakes[rewardToken].latestStakeSum += (newRewardAmount * _LIQUID_STAKE_DENOMINATOR) / totalSupply();
            emit RewardTokensDistributed(rewardToken, rewardTokens[rewardToken].idERC1155, newRewardAmount);
        }
    }

    function _calculateTimeStakeReward(address rewardToken, address account) internal view returns (uint256 reward) {
        uint256 stakedSeconds = block.timestamp - latestClaimTimestamp[rewardToken][account];

        uint128 _rewardDenominator = 10 ** 18;
        if (rewardTokens[rewardToken].idERC1155 != 0) {
            // ERC1155 - remove decimals
            _rewardDenominator = 10 ** 36;
        }
        reward =
            ((balanceOf(account) * rewardTokens[rewardToken].dailyReward * stakedSeconds) / 1 days) /
            _rewardDenominator;
    }

    function _calculateLiquidStakeReward(address rewardToken, address account) internal view returns (uint256 reward) {
        uint256 investorStake = balanceOf(account);

        // for ERC20 do not change decimals (USDC: 6 and others: 18)
        uint128 _rewardDenominator = 1;
        if (rewardTokens[rewardToken].idERC1155 != 0) {
            // ERC1155 - remove decimals
            _rewardDenominator = 10 ** 18;
        }
        reward = ((investorStake *
            (liquidStakes[rewardToken].latestStakeSum - liquidStakes[rewardToken].stakeSum[account])) /
            (_LIQUID_STAKE_DENOMINATOR * _rewardDenominator));
    }

    function _updateStakeSum(address account) internal {
        for (uint8 i = 0; i < rewardTokensList.length; i++) {
            liquidStakes[rewardTokensList[i]].stakeSum[account] = liquidStakes[rewardTokensList[i]].latestStakeSum;
        }
    }

    function _beforeStakeTokenTransfer(address account) internal {
        for (uint8 i = 0; i < rewardTokensList.length; i++) {
            _claimTimeStakeRewardFrom(rewardTokensList[i], account);
            _claimLiquidStakeReward(rewardTokensList[i], account);
        }
    }

    /**
     * @dev See {IERC165-supportsInterface}.
     * The following functions are overrides required by Solidity.
     */
    function supportsInterface(
        bytes4 interfaceId
    ) public view virtual override(AccessControlUpgradeable, ERC1155ReceiverUpgradeable) returns (bool) {
        return
            interfaceId == type(IAccessControlUpgradeable).interfaceId ||
            interfaceId == type(IERC1155ReceiverUpgradeable).interfaceId ||
            super.supportsInterface(interfaceId);
    }
}
"
    }
  },
  "settings": {
    "optimizer": {
      "enabled": true,
      "runs": 200
    },
    "outputSelection": {
      "*": {
        "*": [
          "evm.bytecode",
          "evm.deployedBytecode",
          "devdoc",
          "userdoc",
          "metadata",
          "abi"
        ]
      }
    },
    "metadata": {
      "useLiteralContent": true
    },
    "libraries": {}
  }
}}
