      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.19;

import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {Ownable} from "@openzeppelin/contracts/access/Ownable.sol";
import {IWETH} from "contracts/Diamond/interfaces/IWETH.sol";

/// @title A DAO treasury
/// @notice This contract is a simple treasury for the DAO.
contract Treasury is Ownable {
    IWETH public immutable WETH;
    /// @notice Emitted when a payment is released
    /// @param to The address receiving the payment
    /// @param token The token transfered
    /// @param amount The amount paid

    event Released(address to, address token, uint256 amount);

    constructor(IWETH _weth) {
        WETH = _weth;
    }

    /// @notice Release funds to a recipient
    /// @param _recipient The receiver
    /// @param _token The token to transfer
    /// @param _amount The amount to transfer
    function transfer(address _recipient, IERC20 _token, uint256 _amount) external onlyOwner {
        require(_recipient != address(0), "TRS: INVALID_ADDRESS");
        _token.transfer(_recipient, _amount);
        emit Released(_recipient, address(_token), _amount);
    }

    receive() external payable {
        WETH.deposit{value: msg.value}();
    }
}
