pragma solidity ^0.8.17;

import "@openzeppelin/contracts/token/ERC1155/ERC1155.sol";
import "@openzeppelin/contracts/token/common/ERC2981.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Strings.sol";
import "@openzeppelin/contracts/utils/Base64.sol";
import "operator-filter-registry/src/DefaultOperatorFilterer.sol";

contract NekrCardsIsERC1155 is ERC1155, ERC2981, Ownable, DefaultOperatorFilterer {

    using Strings for uint256;

    string public constant name = "0xNekr Cards";
    string public constant symbol = "NEKARD";

    uint idsCount = 0;

    struct Card {
        uint256 id;
        string name;
        string description;
        string uri;
        uint256 maxSupply;
        uint256 currentSupply;
        uint256 maxByUser;
    }

    mapping(uint256 => Card) public cardsById;
    mapping(address => mapping(uint256 => uint256)) public mintedByIdByUser;

    constructor() ERC1155("") {}

    /* ********************************** */
    /*               Modifiers            */
    /* ********************************** */

    modifier cardShouldExist(uint _cardId) {
        require(keccak256(abi.encodePacked(cardsById[_cardId].name)) != keccak256(abi.encodePacked("")), "Item does not exist");
        _;
    }

    /* ********************************** */
    /*               Card                 */
    /* ********************************** */

    function createCard(string memory _name, string memory _description, string memory _uri, uint256 _maxSupply, uint256 _maxByUser) external onlyOwner {
        uint256 newCardId = idsCount;
        cardsById[newCardId] = Card(newCardId, _name, _description, _uri, _maxSupply, 0, _maxByUser);
        idsCount += 1;
    }

    function updateCard(uint256 _cardId, string memory _name, string memory _description, string memory _uri, uint256 _maxSupply, uint256 _maxByUser) external onlyOwner cardShouldExist(_cardId) {
        require(_maxSupply > cardsById[_cardId].currentSupply, "Max supply must be greater than current supply");
        cardsById[_cardId].name = _name;
        cardsById[_cardId].description = _description;
        cardsById[_cardId].uri = _uri;
        cardsById[_cardId].maxSupply = _maxSupply;
        cardsById[_cardId].maxByUser = _maxByUser;
    }

    function updateCardSupply(uint256 _cardId, uint256 _amount) external onlyOwner cardShouldExist(_cardId) {
        cardsById[_cardId].maxSupply += _amount;
    }

    function mintCard(uint256 _cardId, address _to, uint256 _amount) external cardShouldExist(_cardId) {
        require(cardsById[_cardId].currentSupply + _amount <= cardsById[_cardId].maxSupply, "No items available");
        require(mintedByIdByUser[_to][_cardId] + _amount <= cardsById[_cardId].maxByUser, "Cannot mint more than max by user");

        cardsById[_cardId].currentSupply += _amount;
        mintedByIdByUser[_to][_cardId] += _amount;

        _mint(_to, _cardId, _amount, "");
    }

    function adminAirdrop(uint256 _cardId, address[] memory _to, uint256[] memory _amount) external onlyOwner cardShouldExist(_cardId) {
        require(_to.length == _amount.length, "Array length must be equal");
        for (uint i = 0; i < _to.length; i++) {
            _mint(_to[i], _cardId, _amount[i], "");
        }
    }

    function burn(address account, uint256 id, uint256 value) public virtual {
        require(
            account == _msgSender() || isApprovedForAll(account, _msgSender()),
            "ERC1155: caller is not token owner or approved"
        );

        _burn(account, id, value);
    }

    function uri(uint256 _cardId) override public view returns (string memory)  {
        require(keccak256(abi.encodePacked(cardsById[_cardId].name)) != keccak256(abi.encodePacked("")), "Item does not exist");
        string memory card_image = cardsById[_cardId].uri;
        string memory card_name = cardsById[_cardId].name;
        string memory card_description = cardsById[_cardId].description;
        string memory card_current_supply = cardsById[_cardId].currentSupply.toString();

        string memory json = Base64.encode(
            bytes(
                string(
                    abi.encodePacked(
                        '{"name": "', card_name, '", "image": "', card_image, '", "description": "', card_description ,'", "attributes": [{"trait_type": "Current supply", "value": "', card_current_supply, '"}]}'
                    )
                )
            )
        );

        return string(abi.encodePacked('data:application/json;base64,', json));
    }

    function totalSupply() external view returns (uint256) {
        uint256 total = 0;

        for (uint i = 0; i < idsCount; i++) {
            total += cardsById[i].currentSupply;
        }

        return total;
    }

    /**
     * @dev See {IERC1155-setApprovalForAll}.
     *      In this example the added modifier ensures that the operator is allowed by the OperatorFilterRegistry.
     */
    function setApprovalForAll(address operator, bool approved) public override onlyAllowedOperatorApproval(operator) {
        super.setApprovalForAll(operator, approved);
    }

    /**
     * @dev See {IERC1155-safeTransferFrom}.
     *      In this example the added modifier ensures that the operator is allowed by the OperatorFilterRegistry.
     */
    function safeTransferFrom(address from, address to, uint256 tokenId, uint256 amount, bytes memory data)
    public
    override
    onlyAllowedOperator(from)
    {
        super.safeTransferFrom(from, to, tokenId, amount, data);
    }

    /**
     * @dev See {IERC1155-safeBatchTransferFrom}.
     *      In this example the added modifier ensures that the operator is allowed by the OperatorFilterRegistry.
