library StringStorage {
    struct Index {
        uint256 id;
    }

    struct Set {
        mapping(string => Index) map;
        string[] strings;
    }


    function size(Set storage self) internal view returns (uint256) {
        return uint256(self.strings.length);
    }

    function getStrings(Set storage self) internal view returns (string[] memory) {
        return self.strings;
    }

    function mustAdd(Set storage self, string memory _string) internal {
        require(
            add(self, _string),
            "The string has already been added to the storage, "
            "failed to add the string to the string storage."
        );
    }

    function add(Set storage self, string memory _string) internal returns (bool) {
        if (contains(self, _string)) {
            return false;
        }
        _add(self, _string);
        return true;
    }

    function remove(Set storage self, string memory _string) internal returns (bool) {
        if (!contains(self, _string)) {
            return false;
        }
        _remove(self, _string);
        return true;
    }

    function clear(Set storage self) internal returns (bool) {
        for (uint256 i = 0; i < self.strings.length; i++) {
            delete self.map[self.strings[i]];
        }
        delete self.strings;
        return true;
    }

    function contains(Set storage self, string memory _string) internal view returns (bool) {
        return self.map[_string].id > 0;
    }

    function _add(Set storage self, string memory _string) private {
        self.strings.push(_string);
        self.map[_string].id = self.strings.length;

        _checkEntry(self, _string);
    }

    function _remove(Set storage self, string memory _string) private {
        Index memory index = self.map[_string];

        // Move an last element of array into the vacated key slot.
        uint256 _lastListID = self.strings.length - 1;
        string memory _lastListString = self.strings[_lastListID];
        if (_lastListID != index.id - 1) {
            self.map[_lastListString].id = index.id;
            self.strings[index.id - 1] = _lastListString;
        }
        self.strings.pop();
        delete self.map[_string];

        _checkEntry(self, _string);

        if (!_equals(_lastListString, _string)) _checkEntry(self, _lastListString);
    }

    function _checkEntry(Set storage self, string memory _string) private view {
        uint256 _id = self.map[_string].id;
        assert(_id <= self.strings.length);
        // "Map contains wrong id"
        if (contains(self, _string))
            assert(_id > 0);
        // "index start with 1, so included elements must have index > 0"
