abstract contract UsableErc721  {
    event Used(uint256 tokenId);
    event Unused(uint256 tokenId);

    mapping(uint256=>bool) used;

    function inUse(uint256 tokenId) public view virtual returns (bool) {
        return used[tokenId];
    }

    modifier whenNotInUse(uint256 tokenId) {
        require(!used[tokenId], "UsableErc721: token is used");
        _;
    }

    modifier whenInUse(uint256 tokenId) {
        require(used[tokenId], "UsableErc721: token is unused");
        _;
    }

    function _use(uint256 tokenId) internal virtual whenNotInUse(tokenId) {
        used[tokenId] = true;
        emit Used(tokenId);
    }

    function _unuse(uint256 tokenId) internal virtual whenInUse(tokenId) {
        used[tokenId] = false;
        emit Unused(tokenId);
    }
