contract ProxyAdminPin is  ERC2771Context, Ownable {
    constructor(address trustedForwarder) ERC2771Context(trustedForwarder) {}

    function _msgSender() internal view virtual override(Context, ERC2771Context) returns (address sender) {
        return ERC2771Context._msgSender();
    }

    function _msgData() internal view virtual override(Context, ERC2771Context) returns (bytes calldata) {
        return ERC2771Context._msgData();
    }

    function pin(ITokenProxy proxy) public virtual onlyOwner {
        proxy.pin();
    }

    function upgrade(ITokenProxy proxy) public virtual onlyOwner {
        proxy.upgradeTo();
    }

    function unpin(ITokenProxy proxy) public virtual onlyOwner {
        proxy.unpin();
    }

    function getProxyImplementation(ITokenProxy proxy) public view virtual returns (address) {
        // We need to manually run the static call since the getter cannot be flagged as view
        // bytes4(keccak256("implementation()")) == 0x5c60da1b
        (bool success, bytes memory returndata) = address(proxy).staticcall(hex"5c60da1b");
        require(success);
        return abi.decode(returndata, (address));
    }

    function getProxyAdmin(ITokenProxy proxy) public view virtual returns (address) {
        // We need to manually run the static call since the getter cannot be flagged as view
        // bytes4(keccak256("admin()")) == 0xf851a440
        (bool success, bytes memory returndata) = address(proxy).staticcall(hex"f851a440");
        require(success);
        return abi.decode(returndata, (address));
    }

    function changeProxyAdmin(ITokenProxy proxy, address newAdmin) public virtual onlyOwner {
        proxy.changeAdmin(newAdmin);
    }
}

contract ERC721ImplementationV1 is Initializable, ERC721Upgradeable, UsableErc721,
PausableUpgradeable, OwnableUpgradeable, ERC2771ContextUpgradeable, ERC2981 {
    using CountersUpgradeable for CountersUpgradeable.Counter;

    uint256  private capacity;
    IFactory private factory;

    CountersUpgradeable.Counter private _tokenIdCounter;

    constructor(address trustedForwarder) payable ERC2771ContextUpgradeable(trustedForwarder) {}

    function initialize(
        string calldata _name,
        string calldata _symbol,
        address _factory,
        uint256 _capacity,
        uint96 _royaltyFee,
        address _royaltyReceiver
    ) initializer public {
        __ERC721_init(_name, _symbol);
        __Pausable_init();
        __Ownable_init();

        factory = IFactory(_factory);
        capacity = _capacity;
        _setDefaultRoyalty(_royaltyReceiver, _royaltyFee);
    }

    modifier onlyFactoryOwner {
        require(msg.sender == factory.owner(), "sender must by factory owner");
        _;
    }

    modifier onlyTrustedSender {
        require(
            isTrustedForwarder(msg.sender) ||
            msg.sender == address(factory),
            "sender must be trusted"
        );
        _;
    }

    function use(uint256 tokenId) public {
        require(ownerOf(tokenId) == _msgSender(), "only token owner can use");
        _use(tokenId);
    }

    function unuse(uint256 tokenId) public {
        require(ownerOf(tokenId) == _msgSender(), "only token owner can unuse");
        _unuse(tokenId);
    }

    function pause() public onlyFactoryOwner {
        _pause();
    }

    function unpause() public onlyFactoryOwner {
        _unpause();
    }

    function safeMint(address to, uint256 tokenID) public onlyTrustedSender onlyOwner {
        _tokenIdCounter.increment();
        require(_tokenIdCounter.current() <= capacity, "capacity exceeded");

        _safeMint(to, tokenID);
    }

    function _safeMint(address to, uint256 tokenID) internal override {
        super._safeMint(to, tokenID);
    }

    function batchSafeMint(address to, uint256[] calldata tokenIDs) public onlyTrustedSender onlyOwner {
        uint256 _now = _tokenIdCounter.current();
        uint256 _after = _now + tokenIDs.length;
        require(_after <= capacity, "capacity exceeded");
        for (uint256 i = 0; i < tokenIDs.length; i++) {
            _safeMint(to, tokenIDs[i]);
        }
        _tokenIdCounter._value = _after;
    }

    function burn(uint256 tokenId) public {
        require(_isApprovedOrOwner(_msgSender(), tokenId), "ERC721Burnable: caller is not owner nor approved");
        _burn(tokenId);
    }

    function batchApprove(address to, uint256[] calldata tokenIds) external {
        require(to != address(0), "spender can't be zero address");
        require(tokenIds.length > 0, "zero len array illegal");
        for (uint256 i = 0; i < tokenIds.length; i++) {
            approve(to, tokenIds[i]);
        }
    }

    function batchRevoke(uint256[] calldata tokenIds) external {
        require(tokenIds.length > 0, "zero len array illegal");
        for (uint256 i = 0; i < tokenIds.length; i++) {
            approve(address(0), tokenIds[i]);
        }
    }

    function _beforeTokenTransfer(address from, address to, uint256 tokenId) internal whenNotPaused whenNotInUse(tokenId) override {
        super._beforeTokenTransfer(from, to, tokenId);
    }

    // The following functions are overrides required by Solidity.

    function _burn(uint256 tokenId) internal override(ERC721Upgradeable) {
        super._burn(tokenId);
    }

    function _msgSender() internal view override(ContextUpgradeable, ERC2771ContextUpgradeable) returns (address) {
        return super._msgSender();
    }

    function _msgData() internal view override(ContextUpgradeable, ERC2771ContextUpgradeable) returns (bytes calldata) {
        return super._msgData();
    }

    function tokenURI(uint256 tokenId) public view override(ERC721Upgradeable) returns (string memory)
    {
        return string(
            abi.encodePacked(
                factory.baseURI(),
                "/",
                Strings.toHexString(uint256(uint160(address(this))), 20),
                "/",
                Strings.toString(tokenId)
            )
        );
    }

    function contractURI() public view returns (string memory)
    {
        return string(
            abi.encodePacked(
                factory.baseURI(),
                "/",
                Strings.toHexString(uint256(uint160(address(this))), 20)
            )
        );
    }

    function totalSupply() public view returns (uint256) {
        return capacity;
    }

    function supportsInterface(bytes4 interfaceId) public view virtual override(ERC2981, ERC721Upgradeable) returns (bool)
    {
        return
        interfaceId == type(IERC721Upgradeable).interfaceId ||
        interfaceId == type(IERC721MetadataUpgradeable).interfaceId ||
        interfaceId == type(IERC2981).interfaceId ||
        super.supportsInterface(interfaceId);
    }
}

contract FactoryERC721 is IFactory, Ownable, EIP712 {
    using ECDSA for bytes32;
    using StringStorage for StringStorage.Set;

    string uri;
    address _implementation;

    event Deployed(address collection, address owner);

    bytes32 private constant TYPECREATEERC721 = keccak256(abi.encodePacked("createERC721"));

    mapping(address => address) public _proxyAdmins;
    mapping(address => StringStorage.Set) private  _onetimestrs;
    mapping(address => bool) private _managers;

    bytes32 private constant _TYPEHASH =
    keccak256("DeployRequest(string Tx_Type,string Description,address Owner,string Name,string Symbol,uint256 Max_Items_per_Collection,uint96 Percent_Royalty_in_Hundredths,address Royalties_to_this_Address,uint256[] Token_ID,string Onetimestr,bytes32 Salt)");

    using StringStorage for StringStorage.Set;

    struct DeployRequest {
        address owner;
        string description;
        string name;
        string symbol;
        uint256 capacity;
        uint96 royaltyP;
        address royaltyWallet;
        uint256[] tokenID;
        string onetimestr;
        bytes32 salt;
    }

    function verify(DeployRequest calldata req, bytes calldata signature) public view returns (bool) {
        if (_hashTypedDataV4(
            keccak256(bytes.concat(
                abi.encode(
                    _TYPEHASH,
                    TYPECREATEERC721,
                    keccak256(abi.encodePacked(req.description)),
                    req.owner,
                    keccak256(abi.encodePacked(req.name)),
                    keccak256(abi.encodePacked(req.symbol))
                ), abi.encode(
                    req.capacity,
                    req.royaltyP,
                    req.royaltyWallet,
                    keccak256(abi.encodePacked(req.tokenID)),
                    keccak256(abi.encodePacked(req.onetimestr)),
                    req.salt
                )
            ))).recover(signature) != req.owner) {
            return false;
        }
        return !_onetimestrs[req.owner].contains(req.onetimestr);
    }

    constructor (string memory _uri, address implementation) EIP712("Pixchange 721 Minting", "0.0.1") {
        uri = _uri;
        _implementation = implementation;
    }

    function owner() public override(Ownable, IFactory) view returns (address) {
        return Ownable.owner();
    }

    function baseURI() external override view returns (string memory) {
        return uri;
    }

    function setBaseURI(string memory _uri) external onlyOwner {
        uri = _uri;
    }

    function setImplementation(address implementation) external onlyOwner {
        _implementation = implementation;
    }

    function implementation() external view returns (address){
        return _implementation;
    }

    function addManager(address _manager) external onlyOwner {
        _managers[_manager] = true;
    }

    function deleteManager(address _manager) external onlyOwner {
        _managers[_manager] = false;
    }

    function isManager(address _manager) public view returns (bool){
        return _managers[_manager];
    }

    modifier onlyManagerOrOwner() {
        require(isManager(msg.sender) || msg.sender == owner(), "must be manager");
        _;
    }

    function deployWithMint(
        DeployRequest calldata req,
        bytes calldata signature
    ) external onlyManagerOrOwner {
        require(verify(req, signature), "failed to verify signature");
        _onetimestrs[req.owner].mustAdd(req.onetimestr);
        address tokenProxy = deployProxyForAddr(req);

        ERC721ImplementationV1 _impl = ERC721ImplementationV1(tokenProxy);
        _impl.batchSafeMint(req.owner, req.tokenID);
        _impl.transferOwnership(req.owner);
    }

    function deploy(
        DeployRequest calldata req,
        bytes calldata signature
    ) external onlyManagerOrOwner {
        require(verify(req, signature), "failed to verify signature");
        _onetimestrs[req.owner].mustAdd(req.onetimestr);
        address tokenProxy = deployProxyForAddr(req);
        ERC721ImplementationV1(address(tokenProxy)).transferOwnership(req.owner);
    }

    function deployProxyForAddr(
        DeployRequest calldata req
    ) internal returns (address){
        address _admin = getAdmin(req.owner, req.salt);
        address _tokenProxy = deployProxy(
            _admin,
            req.name,
            req.symbol,
            req.capacity,
            req.royaltyP,
            req.royaltyWallet,
            req.salt
        );
        return _tokenProxy;
    }

    function getAdmin(address owner, bytes32 _salt) internal returns (address){
        address _admin = _proxyAdmins[owner];
        if (_admin == address(0)) {
            _admin = address(new ProxyAdminPin{salt : _salt}(owner));
            _proxyAdmins[owner] = _admin;
        }
        return _admin;
    }

    function predictAddress(bytes32 salt, address owner, string calldata name, string calldata symbol, uint256 capacity, uint256 royaltyP, address royaltyWallet) external view returns (address) {
        address _admin = _proxyAdmins[owner];
        if (_admin == address(0)) {
            bytes32 hash = keccak256(
                abi.encodePacked(
                    bytes1(0xff), address(this), salt, keccak256(getProxyAdminBytecode(owner))
                )
            );

            _admin = address(uint160(uint(hash)));
        }

        bytes32 hash = keccak256(
            abi.encodePacked(
                bytes1(0xff), address(this), salt, keccak256(getERC721Bytecode(_admin, name, symbol, capacity, royaltyP, royaltyWallet))
            )
        );
        return address(uint160(uint(hash)));
    }

    function getERC721Bytecode(address _admin, string calldata name, string calldata symbol, uint256 capacity, uint256 royaltyP, address royaltyWallet) internal view returns (bytes memory) {
        bytes memory bytecode = type(TokenProxy).creationCode;
        return abi.encodePacked(bytecode, abi.encode(_admin, _implementation,
            abi.encodeWithSelector(
                bytes4(keccak256("initialize(string,string,address,uint256,uint96,address)")),
                name,
                symbol,
                address(this),
                capacity,
                royaltyP,
                royaltyWallet
            )));
    }

    function getProxyAdminBytecode(address _owner) internal view returns (bytes memory) {
        bytes memory bytecode = type(ProxyAdminPin).creationCode;
        return abi.encodePacked(bytecode, abi.encode(_owner));
    }

    function deployProxy(
        address admin,
        string calldata name,
        string calldata symbol,
        uint256 capacity,
        uint96 royaltyP,
        address royaltyWallet,
        bytes32 _salt
    ) internal returns (address) {
        require(capacity <= 1000, "high capacity");

        TokenProxy tokenProxy = new TokenProxy{salt : _salt}(
            admin,
            _implementation,
            abi.encodeWithSelector(
                bytes4(keccak256("initialize(string,string,address,uint256,uint96,address)")),
                name,
                symbol,
                address(this),
                capacity,
                royaltyP,
                royaltyWallet
            ));

        emit Deployed(address(tokenProxy), admin);
        return address(tokenProxy);
    }
}
