    "contracts/Marketplace.sol": {
      "content": "// SPDX-License-Identifier: MIT

pragma solidity 0.8.12;

import "./access/AccessControlEnumerable.sol";
import "./security/Pausable.sol";
import {IERC20, SafeERC20} from "./token/ERC20/utils/SafeERC20.sol";
import "./token/ERC1155/IERC1155.sol";
import "./utils/cryptography/draft-EIP712.sol";

import "./VRJAMCollection.sol";
import "./LibOrder.sol";

/**
 * @dev {Marketplace} The off-chain orders logic.
 */
contract Marketplace is Pausable, AccessControlEnumerable, EIP712 {
  using SafeERC20 for IERC20;

  /* EIP712 domain name */
  string public constant EIP712_DOMAIN_NAME = "VRJAM MARKETPLACE";
  /* EIP712 domain version */
  string public constant EIP712_DOMAIN_VERSION = "1";

  /* Admin marketplace role */
  bytes32 public constant ADMIN_ROLE = keccak256("ADMIN_ROLE");

  /* The denominator with which to interpret the fee set as a fraction of the sale price. */
  uint96 public constant FEE_DENOMINATOR = 10000;

  /* The marketplace fee numerator is expressed in basis points. Defaults to 2.5%. */
  uint96 private _fee = 250;

  /* The marketplace fee receiver */
  address private _beneficiary;

  /* Matketplace native collection */
  VRJAMCollection private _collection;

  /**
   * @dev Emitted when orders are matched.
   */
  event MatchOrders(
    address indexed seller,
    address indexed buyer,
    address indexed nft,
    uint256 nftId,        
    address token,
    uint256 price
  );

  /**
   * @dev Emitted when the beneficiary is setted.
   */
  event SetBeneficiary(address beneficiary);

  /**
   * @dev Emitted when a fee is setted.
   */
  event SetFee(uint96 fee);

  /**
   * @dev Initialize contract.
   */
  constructor(address collection_, address beneficiary_) EIP712(EIP712_DOMAIN_NAME, EIP712_DOMAIN_VERSION) {
    _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
    _grantRole(ADMIN_ROLE, msg.sender);

    require(collection_ != address(0), "Marketplace: invalid collection address");
    require(beneficiary_ != address(0), "Marketplace: invalid beneficiary address");

    _collection = VRJAMCollection(collection_);
    _beneficiary = beneficiary_;
  }

  /**
  * @dev Returns the marketplace version.
  */
  function version() public pure returns (string memory) {
    return "1.0.0";
  }

  /**
   * @dev Returns the marketplace fee receiver.
   */
  function beneficiary() public view returns (address) {
    return _beneficiary;
  }

  /**
   * @dev Returns the collection address.
   */
  function collection() public view returns (address) {
    return address(_collection);
  }

  /**
   * @dev Returns the fee numerator.
   */
  function fee() public view returns (uint96) {
    return _fee;
  }

  /**
   * @dev Sets a new marketplace fee receiver.
   */
  function setBeneficiary(address beneficiary_) public onlyRole(ADMIN_ROLE) {
    require(beneficiary_ != address(0), "Marketplace: invalid beneficiary address");
    _beneficiary = beneficiary_;

    emit SetBeneficiary(_beneficiary);
  }

  /**
   * @dev Sets a new marketplace fee numerator.
   */
  function setFee(uint96 fee_) public onlyRole(ADMIN_ROLE) {
    require(fee_ <= FEE_DENOMINATOR, "Marketplace: fee will exceed sale price");
    _fee = fee_;

    emit SetFee(_fee);
  }

  /**
   * @dev Pauses the creation of orders.
   *
   * Requirements:
   *
   * - the caller must have the `ADMIN_ROLE`.
   */
  function pause() public onlyRole(ADMIN_ROLE) {
    _pause();
  }

  /**
   * @dev Unpauses the creation of orders.
   *
   * Requirements:
   *
   * - the caller must have the `ADMIN_ROLE`.
   */
  function unpause() public onlyRole(ADMIN_ROLE) {
    _unpause();
  }

  /**
   * @dev Match orders.
   * @param ask order
   * @param bid order
   *
   * Emits a {MatchOrders} event.
   * 
   * @notice Tokenize only without fee in a lazy-mint case.
   */
  function matchOrders(LibOrder.Order memory ask, LibOrder.Order memory bid) external whenNotPaused {
    LibOrder.validate(ask);
    LibOrder.validate(bid);

    require(ask.seller != bid.buyer, "Marketplace: ask seller and bid buyer are the same");
    require(ask.nft == bid.nft, "Marketplace: collection address does not match");
    require(ask.nftId == bid.nftId, "Marketplace: asset ID does not match");
    require(ask.token == bid.token, "Marketplace: payment token does not match");
    require(ask.price <= bid.price, "Marketplace: price does not match");

    require(ask.signature.length == 65 || bid.signature.length == 65, "Marketplace: no signed order");

    if (ask.signature.length == 65) {
      address signer = _recover(ask);

      require(ask.seller == signer, "Marketplace: seller is not signer");
      require(bid.buyer == msg.sender, "Marketplace: sender is not a buyer");
    }

    if (bid.signature.length == 65) {
      address signer = _recover(bid);

      require(bid.buyer == signer, "Marketplace: buyer is not signer");
      require(ask.seller == msg.sender, "Marketplace: sender is not a seller");
    }

    if (ask.data.length > 0) {
      (bool success, ) = ask.target.call(ask.data);
      require(success, "Marketplace: ask call execution reverted");
    }

    if (bid.data.length > 0) {
      (bool success, ) = bid.target.call(bid.data);
      require(success, "Marketplace: bid call execution reverted");
    }

    uint256 feeAmount = (ask.price * _fee) / FEE_DENOMINATOR;

    // Transfer fee from buyer to fee receiver
    IERC20(ask.token).safeTransferFrom(bid.buyer, _beneficiary, feeAmount);

    // Transfer payment from buyer to seller
    IERC20(ask.token).safeTransferFrom(bid.buyer, ask.seller, ask.price - feeAmount);

    // Transfer asset from seller to buyer
    IERC1155(ask.nft).safeTransferFrom(ask.seller, bid.buyer, ask.nftId, 1, '');

    emit MatchOrders(
      ask.seller,
      bid.buyer,
      ask.nft,
      ask.nftId,
      ask.token,
      ask.price
    );
  }

  /**
   * @dev Returns the address that signed `order` with `signature` field.
   */  
  function recover(LibOrder.Order memory order) external view returns (address) {
    return _recover(order);
  }

  /**
   * @dev Returns the chain id of the current blockchain.
   */
  function getChainID() external view returns (uint256) {
    return block.chainid;
  }

  /**
   * @dev Returns the current timestamp.
   */
  function timestamp() external view returns (uint256) {
    return block.timestamp;
  }

  /**
   * @dev See {ECDSA-recover}.
   */
  function _recover(LibOrder.Order memory order) internal view returns (address) {
    bytes32 digest = _hashTypedDataV4(LibOrder.hash(order));
    return ECDSA.recover(digest, order.signature);
  }
}"
    },
