    "contracts/LibOrder.sol": {
      "content": "// SPDX-License-Identifier: MIT

pragma solidity 0.8.12;

library LibOrder {
    /* Order type hash */
    bytes32 public constant ORDER_TYPEHASH = keccak256(
        "Order(address seller,address buyer,address nft,uint256 nftId,address token,uint256 price,uint256 start,uint256 end,uint256 salt,address target,bytes data)"
    );

    /* An ask/bid order on the marketplace. */
    struct Order {
        /* Asset seller address, or the zero-address for bid orders. */
        address seller;
        /* Asset bueyer address, or the zero-address for ask orders. */
        address buyer;
        /* Collection address */
        address nft;
        /* NFT ID */
        uint256 nftId;        
        /* Token used to pay for the order, or the zero-address as a sentinel value for Ether. */
        address token;
        /* Price of the order (in payment tokens). */
        uint256 price;
        /* Listing timestamp. */
        uint256 start;
        /* Expiration timestamp - 0 for no expiry. */
        uint256 end;
        /* Order salt to prevent duplicate hashes. */
        uint256 salt;

        /* Target to call */
        address target;

        /* Calldata for the target call */
        bytes data;

        /* The EIP-712 signature of all other fields in the Order struct. */
        bytes signature;
    }

    /**
     * @dev Returns the EIP-712 order hash.
     */
    function hash(Order memory order) internal pure returns (bytes32) {
        return keccak256(abi.encode(
                    ORDER_TYPEHASH,
                    order.seller,
                    order.buyer,
                    order.nft,
                    order.nftId,
                    order.token,
                    order.price,
                    order.start,
                    order.end,
                    order.salt,
                    order.target,
                    keccak256(order.data)
                ));
    }

    /**
     * @dev Returns true if order time is valid.
     */
    function validate(LibOrder.Order memory order) internal view {
        require(order.start == 0 || order.start < block.timestamp, "LibOrder: the order start validation failed");
        require(order.end == 0 || order.end > block.timestamp, "LibOrder: the order end validation failed");
    }
}"
    },
    "contracts/VRJAMCollection.sol": {
      "content": "// SPDX-License-Identifier: MIT

pragma solidity 0.8.12;

import "./access/AccessControlEnumerable.sol";
import "./security/Pausable.sol";
import "./token/ERC1155/extensions/ERC1155Burnable.sol";
import "./token/ERC1155/extensions/ERC1155Supply.sol";
import "./token/ERC1155/extensions/ERC1155URIStorage.sol";

/**
 * @dev {VRJAMCollection} including:
 *
 *  - ability for holders to burn (destroy) their tokens
 *  - a minter role that allows for token minting (creation)
 *  - a pauser role that allows to stop all token transfers
 *  - a uri setter role that allows to update uri
 *
 * This contract uses {AccessControlEnumerable} to lock permissioned functions using the
 * different roles - head to its documentation for details.
 *
 * The account that deploys the contract will be granted the minter and pauser
 * roles, as well as the default admin role, which will let it grant both minter
 * and pauser roles to other accounts.
 */
contract VRJAMCollection is ERC1155URIStorage, AccessControlEnumerable, Pausable, ERC1155Burnable, ERC1155Supply {
    bytes32 public constant PAUSER_ROLE = keccak256("PAUSER_ROLE");
    bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");
    bytes32 public constant URI_SETTER_ROLE = keccak256("URI_SETTER_ROLE");

    // Mapping from token ID to props
    mapping(uint256 => bytes) private _props;

    /**
     * @dev Grants `DEFAULT_ADMIN_ROLE`, `URI_SETTER_ROLE`, `MINTER_ROLE`, and `PAUSER_ROLE` to the account that
     * deploys the contract.
     */
    constructor(string memory uri_) ERC1155(uri_) {
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(PAUSER_ROLE, msg.sender);
        _grantRole(MINTER_ROLE, msg.sender);
        _grantRole(URI_SETTER_ROLE, msg.sender);

        _setBaseURI(uri_);
    }

    /**
     * @dev Collection name.
     */
    function name() public pure returns (string memory) {
        return "VRJAM Collections";
    }

    /**
     * @dev Collection symbol.
     */
    function symbol() public pure returns (string memory) {
        return "VRJAMC";
    }

    /**
     * @dev Collection version.
     */
    function version() public pure returns (string memory) {
        return "1.0.0";
    }

    /**
     * @dev See {IERC1155MetadataURI-uri}.
     */
    function uri(uint256 tokenId) public view virtual override(ERC1155URIStorage, ERC1155) returns (string memory) {
        return ERC1155URIStorage.uri(tokenId);
    }

    /**
     * @dev See {ERC1155-uri}.
     */
    function baseURI() public view returns (string memory) {
        return ERC1155URIStorage._baseURI;
    }

    /**
     * @dev Returns the props for token type `id`.
     */
    function props(uint256 id) public view returns (bytes memory) {
        return _props[id];
    }

    /**
     * @dev See {IERC165-supportsInterface}.
     */
    function supportsInterface(bytes4 interfaceId) public view override(ERC1155, AccessControlEnumerable) returns (bool)
    {
        return super.supportsInterface(interfaceId);
    }

    /**
     * @dev Set uri by `newuri`.
     *
     * Requirements:
     *
     * - the caller must have the `URI_SETTER_ROLE`.
     */
    function setBaseURI(string memory baseURI_) public onlyRole(URI_SETTER_ROLE) {
        _setBaseURI(baseURI_);
        _setURI(baseURI_);
    }

    /**
     * @dev Pauses all token transfers.
     *
     * See {ERC1155Pausable} and {Pausable-_pause}.
     *
     * Requirements:
     *
     * - the caller must have the `PAUSER_ROLE`.
     */
    function pause() public onlyRole(PAUSER_ROLE) {
        _pause();
    }

    /**
     * @dev Unpauses all token transfers.
     *
     * See {ERC1155Pausable} and {Pausable-_unpause}.
     *
     * Requirements:
     *
     * - the caller must have the `PAUSER_ROLE`.
     */
    function unpause() public onlyRole(PAUSER_ROLE) {
        _unpause();
    }

    /**
     * @dev Creates `amount` new tokens for `to`, of token type `id`.
     *
     * See {ERC1155-_mint}.
     *
     * Requirements:
     *
     * - the caller must have the `MINTER_ROLE`.
     */
    function mint(address account, uint256 id, uint256 amount, string memory tokenURI, bytes memory data, bytes memory property)
        public
        onlyRole(MINTER_ROLE)
    {
        _mint(account, id, amount, data);
        _setURI(id, tokenURI);

        if (property.length > 0) {
            _props[id] = property;
        }
    }

    /**
     * @dev xref:ROOT:erc1155.adoc#batch-operations[Batched] variant of {mint}.
     */
    function mintBatch(address to, uint256[] memory ids, uint256[] memory amounts, string[] memory uris, bytes memory data, bytes[] memory properties)
        public
        onlyRole(MINTER_ROLE)
    {
        _mintBatch(to, ids, amounts, data);

        require(ids.length == uris.length, "ERC1155: ids and URIs length mismatch");
        require(ids.length == properties.length, "ERC1155: ids and props length mismatch");

        for (uint256 i = 0; i < ids.length; i++) {
            _tokenURIs[ids[i]] = uris[i];

            if (properties[i].length > 0) {
                _props[ids[i]] = properties[i];
            }
        }
    }

    /**
     * @dev See {ERC1155Burnable-burn}.
     */
    function burn(
        address account,
        uint256 id,
        uint256 value
    ) public override(ERC1155Burnable) {
        super.burn(account, id, value);

        if (!exists(id)) {
            delete _tokenURIs[id];
            delete _props[id];
        }
    }

    /**
     * @dev See {ERC1155Burnable-burnBatch}.
     */
    function burnBatch(
        address account,
        uint256[] memory ids,
        uint256[] memory values
    ) public override(ERC1155Burnable) {
        super.burnBatch(account, ids, values);

        for (uint256 i = 0; i < ids.length; i++) {
            if (!exists(ids[i])) {
                delete _tokenURIs[ids[i]];
                delete _props[ids[i]];
            }
        }
    }      

    /**
     * @dev See {ERC1155Supply-_beforeTokenTransfer}.
     */
    function _beforeTokenTransfer(address operator, address from, address to, uint256[] memory ids, uint256[] memory amounts, bytes memory data)
        internal
        whenNotPaused
        override(ERC1155, ERC1155Supply)
    {
        super._beforeTokenTransfer(operator, from, to, ids, amounts, data);
    }
}"
    },
