    bytes32 public constant ADMIN_ROLE = keccak256("ADMIN_ROLE");

    // Info of each user.
    struct UserInfo {
        uint256 amount; // How many LP tokens the user has provided.
        uint256 rewardDebt; // Reward debt. See explanation below.
        uint256 offset;
    }

    


        // Info of each pool.
    struct PoolInfo {
        uint256 allocPoint; // How many allocation points assigned to this pool. tokens to distribute per block.
        uint256 lastRewardBlock; // Last block number that tokens distribution occurs.
        uint256 accTokenPerShare; // Accumulated tokens per share, times 1e12. See below.
    }


    IERC721 public tokenLP;
   

    // Info of each pool.
    PoolInfo public poolInfo;
    // Info of each user that stakes LP tokens.
    mapping(address => UserInfo) internal _userInfo;


    mapping(address => mapping(uint256 => uint256)) ownerNftIDs;

    uint32 public max_withdraw;
    uint32 public max_deposit;

    uint256 constant  totalAllocPoint = 10000;
    uint256 public startBlock;
    uint256 public bonusEndBlock;
    uint256 public accRewardNextPeriod;
    uint256 public accRewardNextTrigger;
    uint256 public blocksScroll;
    uint256 public totalStakedAmount;
    uint256 public totRewDelivered;
    uint256 public rewardPerBlock;
    

    event Deposit(address indexed user, uint256 amount, uint256 pending);
    event Withdraw(address indexed user, uint256 amount, uint256 pending);
    event EmergencyWithdraw(address indexed user, uint256 amount);
    event StolenFundRecovered(address indexed user, uint256 amount);
    event Moved(uint256 startBlock, uint256 endBlock,uint256 accreward, uint256 rewardxblock);


    constructor(
        IERC721 _tokenLP,
        uint256 _blocksScroll,
        uint256 _triggerScrollValue

    )  {
        require(_blocksScroll > 1, "Stk: invalid _blocksScroll!");

         _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(ADMIN_ROLE, msg.sender);


        tokenLP = _tokenLP;
        startBlock = block.number;
        bonusEndBlock = block.number;
        // staking pool
        poolInfo=  PoolInfo({
                allocPoint: totalAllocPoint,
                lastRewardBlock: startBlock,
                accTokenPerShare: 0
            });
        //1 ether;
        accRewardNextTrigger=_triggerScrollValue;


        blocksScroll = _blocksScroll;

        rewardPerBlock = accRewardNextTrigger / blocksScroll;

        max_withdraw=100;
        max_deposit=100;

    }



    // This function is called for all messages sent to
    // this contract, except plain Ether transfers
    // (there is no other function except the receive function).
    // Any call with non-empty calldata to this contract will execute
    // the fallback function (even if Ether is sent along with the call).
    fallback() external payable {}

    // This function is called for plain Ether transfers, i.e.
    // for every call with empty calldata.
    receive() external payable {
        accRewardNextPeriod += msg.value;
    }

    function receiveFromContract() public payable {

        accRewardNextPeriod += msg.value;

        if(  accRewardNextPeriod >= accRewardNextTrigger ){
            moveToNextPeriod();
        }


    }


    function userInfo(address account) external view returns(UserInfo memory usr){

        usr= _userInfo[account];

        if(usr.amount>0){
            usr.amount = usr.amount / 1e18;
        }
    }

    // Return reward multiplier over the given _from to _to block.
    function getMultiplier(uint256 _from, uint256 _to)
        public
        view
        returns (uint256)
    {
        if (_to <= bonusEndBlock) {
            return _to - _from;
        } else if (_from >= bonusEndBlock) {
            return 0;
        } else {
            return bonusEndBlock - _from;
        }
    }

    // View function to see pending Reward on frontend.
    function pendingReward(address _user) external view returns (uint256) {

        UserInfo storage user = _userInfo[_user];
        uint256 accTokenPerShare = poolInfo.accTokenPerShare;

        if (block.number > poolInfo.lastRewardBlock && totalStakedAmount != 0) {
            uint256 multiplier = getMultiplier(poolInfo.lastRewardBlock, block.number);
            uint256 wtkReward = ( multiplier * rewardPerBlock * poolInfo.allocPoint ) / totalAllocPoint ;
            accTokenPerShare += (wtkReward * 1e12) / totalStakedAmount ;
        }
        return ((user.amount * accTokenPerShare) / 1e12) - user.rewardDebt;
    }

    // Update reward variables of the given pool to be up-to-date.
    function updatePool() public {

        if (block.number <= poolInfo.lastRewardBlock) {
            return;
        }

        if (totalStakedAmount == 0) {
            poolInfo.lastRewardBlock = block.number;
            return;
        }
        uint256 multiplier = getMultiplier(poolInfo.lastRewardBlock, block.number);
        uint256 wtkReward =  (multiplier * rewardPerBlock * poolInfo.allocPoint) / totalAllocPoint ;

        poolInfo.accTokenPerShare += (wtkReward * 1e12) / totalStakedAmount;
        poolInfo.lastRewardBlock = block.number;
    }






    function deposit(uint32 qty) external whenNotPaused{

        UserInfo storage user = _userInfo[msg.sender];

        uint256 max = qty;

        uint256 pending = 0;

        updatePool();
        if (user.amount > 0) {

            pending = ((user.amount * poolInfo.accTokenPerShare) / 1e12) -  user.rewardDebt;

            if (pending > 0) {

                totRewDelivered += pending;

                payable(msg.sender).transfer(pending);

            }
        }


        if (qty > 0) {


            require( tokenLP.isApprovedForAll(msg.sender, address(this)) == true,
                    "Stk:Not Approved!"  
                    );

            require( tokenLP.balanceOf(msg.sender) >= qty,
                    "Stk:insufficient balance!"  
                    );

            uint256[] memory nftIDs=tokenLP.tokensOfOwner(msg.sender) ;

            require(nftIDs.length>0,"Stk: holder has 0 NFTs"); 

            uint256  usr_amount= user.amount/  1e18;
            uint256 offset =user.offset;
            max = qty > nftIDs.length ? nftIDs.length : qty;
            max = max > max_deposit ? max_deposit : max;

            user.amount +=  max * 1e18;
            totalStakedAmount += max * 1e18;

            unchecked {
                for (uint256 i; i < max;i++) {
                    ownerNftIDs[msg.sender][offset+(usr_amount++) ]=nftIDs[i];
                    tokenLP.safeTransferFrom(
                        msg.sender,
                        address(this),
                        nftIDs[i]
                        ) ;
                }                
            }


        }
        user.rewardDebt = (user.amount * poolInfo.accTokenPerShare) / 1e12;

        emit Deposit(msg.sender, max, pending);
    }




    function withdraw(uint32 qty) external whenNotPaused{

        UserInfo storage user = _userInfo[msg.sender];
        require(user.amount >= qty, "Stk: withdraw: not good");
        updatePool();
        uint256 pending = ((user.amount * poolInfo.accTokenPerShare) / 1e12) - user.rewardDebt ;        

        uint256 qty_withdraw =qty;

        if (pending > 0) {

            totRewDelivered += pending;

            payable(msg.sender).transfer(pending);
            
        }
        if (qty > 0) {

            uint256 balanceOf= user.amount /  1e18;
            uint256 offset=user.offset;
            qty_withdraw =qty > balanceOf ? balanceOf : qty; 
            qty_withdraw =qty_withdraw > max_withdraw ? max_withdraw : qty_withdraw; 

            user.amount -= qty_withdraw * 1e18;
            totalStakedAmount -= qty_withdraw * 1e18;
            user.offset+=qty_withdraw;

            unchecked {
                for (uint i=offset; i < (offset + qty_withdraw ); i++ ){

                    tokenLP.safeTransferFrom(
                        address(this),
                        msg.sender,
                        ownerNftIDs[msg.sender][i]
                        ) ;
                }
            }

        }
       
        user.rewardDebt = (user.amount * poolInfo.accTokenPerShare) / 1e12;
        emit Withdraw(msg.sender, qty_withdraw, pending);
    }









    function emergencyWithdraw(uint32 qty) external {

        UserInfo storage user = _userInfo[msg.sender];

        uint256 balanceOf= user.amount /  1e18;
        uint256 offset=user.offset;
        uint256 qty_withdraw =qty > balanceOf ? balanceOf : qty; 
        qty_withdraw =qty_withdraw > max_withdraw ? max_withdraw : qty_withdraw; 

        user.amount -= qty_withdraw * 1e18;
        user.rewardDebt = 0;
        totalStakedAmount -= qty_withdraw * 1e18;
        user.offset+=qty_withdraw;

        unchecked {
            for (uint i=offset; i < (offset + qty_withdraw ); i++ ){

                tokenLP.safeTransferFrom(
                    address(this),
                    msg.sender,
                    ownerNftIDs[msg.sender][i]
                    ) ;
            }
        }

        emit EmergencyWithdraw(msg.sender, qty_withdraw);
    }



    function emergencyRewardWithdraw(uint256 _amount) external onlyRole(ADMIN_ROLE) {
        require(
            _amount <= address(this).balance,
            "Stk: not enough eth"
        );

        payable(msg.sender).transfer(_amount);
    }

    

    // Update the given pool's WDEFI allocation point and deposit fee. Can only be called by the owner.
    function set(uint256 _allocPoint, bool _withUpdate) external onlyRole(ADMIN_ROLE) {

        require(_allocPoint > 0 && _allocPoint <= totalAllocPoint,"Stk: Invalid value!");

        if (_withUpdate) {
            updatePool();
        }


        poolInfo.allocPoint = _allocPoint;
    }


    function recoverStolenFund(address _user) external onlyRole(ADMIN_ROLE) {
       
        UserInfo storage user = _userInfo[_user];

        totalStakedAmount -= user.amount;
        uint256 oldAmount = user.amount / 1e18;
        user.amount = 0;
        user.rewardDebt = 0;

        tokenLP.safeTransferFrom(
            address(this),
            address(msg.sender),
            oldAmount,
            ""
        );

        emit StolenFundRecovered(msg.sender, oldAmount);
    }


    function getRewardBalance() external view returns(uint256){

        return address(this).balance;
    }


//********************************************************************************************************************************
    function getLPbalanceOf(address account) external view returns(uint256  qty){
        UserInfo storage user = _userInfo[account];

        qty= user.amount /  1e18;
    }

    function getNFT(address account,uint32 qty) external view  returns(uint256[] memory nfts){

        UserInfo storage user = _userInfo[account];

        uint256 balanceOf= user.amount /  1e18;

        if(balanceOf==0){
            return new uint256[](0);    
        }
        uint256 offset=user.offset;

        uint256 _qty =qty > balanceOf ? balanceOf : qty; 

        nfts = new uint256[](_qty);

        unchecked {
            for (uint i=offset; i < (offset + _qty ); i++ ){

                nfts[i-offset]= ownerNftIDs[account][i] ;
            }
        }

    }


    function setMaxWithdraw(uint32 newMax) external onlyRole(ADMIN_ROLE){
        require(newMax>0,"Stk: invalid newMax");
        max_withdraw=newMax;
    } 

    function setMaxDeposit(uint32 newMax) external onlyRole(ADMIN_ROLE){
        require(newMax>0,"Stk: invalid newMax");
        max_deposit=newMax;
    } 


    function stopReward() external onlyRole(ADMIN_ROLE) {
        bonusEndBlock = block.number -1 ;
    }



    function extendRange(uint256 newBonusEndNlock) external onlyRole(ADMIN_ROLE) {
        require(newBonusEndNlock > poolInfo.lastRewardBlock,"Stk: Invalid block number" );

        bonusEndBlock = newBonusEndNlock;
    }


    function setNewTriggerValue(uint256 newValue) external onlyRole(ADMIN_ROLE){

        require(newValue>1e12,"Stk: Invalid value" );

        accRewardNextTrigger=newValue;

    }

    function setNewBlocksScrollValue(uint256 newValue) external onlyRole(ADMIN_ROLE){

        require(newValue>1,"Stk: Invalid block number" );

        blocksScroll=newValue;

    }



    function moveToNextPeriod() internal {

        updatePool();
        
        uint256 pend_bl_qty=0;

        if(bonusEndBlock > poolInfo.lastRewardBlock){
            pend_bl_qty = bonusEndBlock - poolInfo.lastRewardBlock;
        }

        uint256 tot_pend_reward= accRewardNextPeriod + ( rewardPerBlock * pend_bl_qty  )  ;


        rewardPerBlock = tot_pend_reward / blocksScroll;

        bonusEndBlock = poolInfo.lastRewardBlock + blocksScroll;

       emit Moved(poolInfo.lastRewardBlock, bonusEndBlock,accRewardNextPeriod, rewardPerBlock); 

       accRewardNextPeriod = 0;


    }


    function pause() public onlyRole(ADMIN_ROLE) {
        _pause();
    }

    function unpause() public onlyRole(ADMIN_ROLE) {
        _unpause();
    }

    function onERC721Received(address, address, uint256, bytes calldata) external pure returns (bytes4) {
        return IERC721Receiver.onERC721Received.selector;
    }


}
