// File: contracts/InviteAirDrop.sol



pragma solidity >=0.7.0 <0.9.0;





contract InviteAirDrop is OwnableUpgradeable, PausableUpgradeable {
    using SafeERC20 for IERC20;

    mapping(address => uint256) private nftCount;
    mapping(address => uint256) private nftPayCount;
    mapping(address => uint256) private inviters;

    address private projectOwner;

    bool _initialized;

    IManorNFT public manorNFT;
    IERC20 public usdToken;
    IERC20 public wofToken;

    uint256 private mintPrice;
    event FullPayMint(address inviter, address owner, uint256 amount, uint256 profit);

    constructor() {}

    function initialize(
        address _manorNFT,
        address _usdToken,
        address _wofToken,
        address _projectOwner
    ) public initializer {
        __Ownable_init();

        _initialized = true;

        manorNFT = IManorNFT(_manorNFT);
        usdToken = IERC20(_usdToken);
        wofToken = IERC20(_wofToken);
        projectOwner = _projectOwner;
    }

    modifier onlyInitialized() {
        require(_initialized, "contract is not initialized");
        _;
    }

    function setCount(
        address[] calldata targets,
        uint256 count
    )
    onlyInitialized
    onlyOwner
    external
    {
        for (uint i = 0; i < targets.length; i++) {
            nftCount[targets[i]] = count;
        }
    }

    function setMintPrice(
        uint256 price
    )
    onlyInitialized
    onlyOwner
    external
    {
        mintPrice = price;
    }

    function setPayCount(
        address[] calldata targets,
        uint256 count
    )
    onlyInitialized
    onlyOwner
    external
    {
        for (uint i = 0; i < targets.length; i++) {
            nftPayCount[targets[i]] = count;
        }
    }

    function isInviter(
        address account
    )
    external
    view
    returns (bool)
    {
        return inviters[account] > 0;
    }

    function getCount(
        address owner
    )
    external
    view
    returns (uint256, uint256)
    {
        return (nftCount[owner], nftPayCount[owner]);
    }

    function InviterDeposit()
    onlyInitialized
    external
    {
        require(inviters[_msgSender()] == 0, "this address is an inviter");
        wofToken.transferFrom(_msgSender(), projectOwner, 50 * (10 ** 18));
        inviters[_msgSender()] = 1;
    }

    function InviterWithdraw()
    onlyInitialized
    external
    {
        require(inviters[_msgSender()] > 0, "this address is not an inviter");
        wofToken.transferFrom(projectOwner, _msgSender(), 50 * (10 ** 18));
        delete(inviters[_msgSender()]);
    }

    function mint()
    onlyInitialized
    external
    {
        require(nftCount[_msgSender()] > 0, "nft count error");
        usdToken.safeTransferFrom(_msgSender(), projectOwner, mintPrice * (10 ** 6));
        manorNFT.safeMint(_msgSender());
        nftCount[_msgSender()]--;
    }

    function fullPayMint(address inviter)
    onlyInitialized
    external
    {
        require(nftPayCount[_msgSender()] > 0, "nft count error");

        uint256 amount = 100;
        uint256 profit = 0;

        if (inviter != projectOwner) {
            require(inviters[inviter] > 0, "the address is not an inviter.");
            usdToken.safeTransferFrom(_msgSender(), inviter, 20 * (10 ** 6));
            amount -= 20;
            profit = 20;
        }

        usdToken.safeTransferFrom(_msgSender(), projectOwner, amount * (10 ** 6));
        manorNFT.safeMint(_msgSender());
        nftPayCount[_msgSender()]--;

        emit FullPayMint(inviter, _msgSender(), amount, profit);
    }
}
