abstract contract CSConfigurable {
    event ConfigChanged(address newConfig);

    bytes32 public constant CONFIGURATOR_ROLE = keccak256("CONFIGURATOR_ROLE");

    ICSConfig public config;

    constructor(address _config) {
        _setConfig(_config);
    }

    function _setConfig(address _config) internal {
        config = ICSConfig(_config);
        emit ConfigChanged(_config);
    }

    modifier onlyRole(bytes32 role) {
        config.checkRole(role, msg.sender);
        _;
    }

    function setConfig(address _config) public onlyRole(CONFIGURATOR_ROLE) {
        _setConfig(_config);
    }
}


// File contracts/core/TokenRescue.sol


pragma solidity ^0.8.15;



abstract contract TokenRescue is CSConfigurable {
    bytes32 public constant RESCUE_ROLE = keccak256("RESCUE_ROLE");

    /*** Make sure currency or NFT doesn't get stranded in this contract ***/

    // If this contract gets a balance in some ERC20 contract after it's finished, then we can rescue it.
    function rescueToken(IERC20 _foreignToken, address _to)
        external
        onlyRole(RESCUE_ROLE)
    {
        _foreignToken.transfer(_to, _foreignToken.balanceOf(address(this)));
    }

    // If this contract gets a balance in some ERC721 contract after it's finished, then we can rescue it.
    function approveNFTrescue(IERC721 _foreignNFT, address _to)
        external
        onlyRole(RESCUE_ROLE)
    {
        _foreignNFT.setApprovalForAll(_to, true);
    }
}


// File contracts/cryptostamp/CS4Base.sol


pragma solidity ^0.8.15;






contract CS4Base is ERC721Enumerable, CSConfigurable, TokenRescue {
    uint256 constant COLOR_COUNT = 5;

    enum SColors {
        Sleeping,
        Black,
        Green,
        Blue,
        Yellow,
        Red
    }

    mapping(address => uint256) public signedTransferNonce;
    mapping(uint256 => SColors) public colors;

    constructor(
        string memory _name,
        string memory _symbol,
        address _config
    ) ERC721(_name, _symbol) CSConfigurable(_config) {}

    function tokenURI(uint256 id)
        public
        view
        virtual
        override
        returns (string memory)
    {
        require(_exists(id));
        return config.tokenURIFor(symbol(), id);
    }

    function signedTransfer(address _to, uint256 _tokenId, bytes memory transferData, bytes memory _signature)
    public
    {
        address currentOwner = ownerOf(_tokenId);
        bytes32 data = keccak256(abi.encode(address(this), block.chainid, _to, _tokenId, signedTransferNonce[currentOwner], transferData));
        require(ECDSA.recover(ECDSA.toEthSignedMessageHash(data), _signature) == currentOwner, "invalid signature");
        signedTransferNonce[currentOwner]++;
        _safeTransfer(currentOwner, _to, _tokenId, transferData);
    }
}


// File contracts/cryptostamp/CS4.sol


pragma solidity ^0.8.15;





contract CS4 is Ownable, CS4Base {
    bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");
    bytes32 public constant REVEAL_ROLE = keccak256("REVEAL_ROLE");

    event ColorRevealStarted(uint256 id);
    event ColorRevealFinished(uint256 id, SColors color);

    uint256 immutable finalSupply;
    uint32[COLOR_COUNT] public totalColorSupply;
    uint32[COLOR_COUNT] public unrevealedColorSupply;
    mapping(uint256 => uint256) public revealBlockByStamp;

    constructor(
        string memory _name,
        string memory _symbol,
        address _config,
        uint32[COLOR_COUNT] memory _totalColorSupply
    ) CS4Base(_name, _symbol, _config) {
        totalColorSupply = _totalColorSupply;
        unrevealedColorSupply = _totalColorSupply;

        uint256 _finalSupply = 0;
        for (uint256 i = 0; i < _totalColorSupply.length; i++) {
            _finalSupply += _totalColorSupply[i];
        }
        finalSupply = _finalSupply;
    }

    function mintMany(address[] memory targets, uint256[] memory ids) external onlyRole(MINTER_ROLE) {
        require(targets.length == ids.length);
        for(uint256 i = 0; i < targets.length; i++) {
            uint256 id = ids[i];
            require(id < finalSupply, "invalid stamp id");
            _mint(targets[i], id);
        }
    }

    function mintRange(address target, uint256 startIndex, uint256 count) external onlyRole(MINTER_ROLE) {
        for(uint256 i = 0; i < count; i++) {
            uint256 id = startIndex + i;
            require(id < finalSupply, "invalid stamp id");
            _mint(target, id);
        }
    }

    function _startColorReveal(uint256 id) internal {
        require(_exists(id), "stamp does not exist");
        require(
            revealBlockByStamp[id] == 0 ||
            block.number >= revealBlockByStamp[id] + 256,
            "reveal already started"
        );
        revealBlockByStamp[id] = block.number;
        emit ColorRevealStarted(id);
    }

    function startColorReveal(uint256 id) external onlyRole(REVEAL_ROLE) {
        _startColorReveal(id);
    }

    function _beforeTokenTransfer(address _from, address _to, uint256 _tokenId)
    internal override
    {
        if (_from != address(0) && colors[_tokenId] == SColors.Sleeping && revealBlockByStamp[_tokenId] == 0) {
            _startColorReveal(_tokenId);
        }
        super._beforeTokenTransfer(_from, _to, _tokenId);
    }

    function accumulatedUnrevealedColorsCount()
        internal
        view
        returns (uint256[COLOR_COUNT] memory)
    {
        uint256[COLOR_COUNT] memory count;
        uint256 total = 0;
        for (uint256 i = 0; i < COLOR_COUNT; i++) {
            total += unrevealedColorSupply[i];
            count[i] = total;
        }
        return count;
    }

    function finishColorReveal(uint256 id) external {
        uint256 revealBlock = revealBlockByStamp[id];
        require(revealBlock != 0, "reveal not started");
        require(revealBlock < block.number, "reveal too soon");
        require(revealBlock + 256 > block.number, "reveal too late");

        uint256[COLOR_COUNT]
            memory unrevealedStampCount = accumulatedUnrevealedColorsCount();

        uint256 colorIndex = uint256(
            keccak256(abi.encodePacked(blockhash(revealBlock), id))
        ) % unrevealedStampCount[COLOR_COUNT - 1];

        uint256 selectedColorIndex = 0;
        for (; selectedColorIndex < COLOR_COUNT; selectedColorIndex++) {
            if (colorIndex < unrevealedStampCount[selectedColorIndex]) {
                break;
            }
        }

        SColors selectedColor = SColors(selectedColorIndex + 1);
        colors[id] = selectedColor;
        revealBlockByStamp[id] = 0;
        unrevealedColorSupply[selectedColorIndex]--;
        emit ColorRevealFinished(id, selectedColor);
    }

    // polygon integration

    event WithdrawnBatch(address indexed user, uint256[] tokenIds);

    bytes32 public constant DEPOSITOR_ROLE = keccak256("DEPOSITOR_ROLE"); // for polygon bridge
    bool public polygonEnabled;
    mapping (uint256 => bool) public withdrawnTokens;

    function deposit(address user, bytes calldata depositData)
        external onlyRole(DEPOSITOR_ROLE)
    {
        require(polygonEnabled, "polygon support not enabled");
        // deposit single
        if (depositData.length == 32) {
            uint256 tokenId = abi.decode(depositData, (uint256));
            withdrawnTokens[tokenId] = false;
            _mint(user, tokenId);

        // deposit batch
        } else {
            uint256[] memory tokenIds = abi.decode(depositData, (uint256[]));
            uint256 length = tokenIds.length;
            for (uint256 i; i < length; i++) {
                withdrawnTokens[tokenIds[i]] = false;
                _mint(user, tokenIds[i]);
            }
        }
    }

    function withdraw(uint256 tokenId) external {
        require(polygonEnabled, "polygon support not enabled");
        require(msg.sender == ownerOf(tokenId), "ChildMintableERC721: INVALID_TOKEN_OWNER");
        withdrawnTokens[tokenId] = true;
        _burn(tokenId);
    }

    function withdrawBatch(uint256[] calldata tokenIds) external {
        require(polygonEnabled, "polygon support not enabled");
        uint256 length = tokenIds.length;

        // Iteratively burn ERC721 tokens, for performing
        // batch withdraw
        for (uint256 i; i < length; i++) {
            uint256 tokenId = tokenIds[i];
            require(msg.sender == ownerOf(tokenId), string(abi.encodePacked("ChildMintableERC721: INVALID_TOKEN_OWNER ", tokenId)));
            withdrawnTokens[tokenId] = true;
            _burn(tokenId);
        }

        // At last emit this event, which will be used
        // in MintableERC721 predicate contract on L1
        // while verifying burn proof
        emit WithdrawnBatch(_msgSender(), tokenIds);
    }

    function setPolygonEnabled(bool _polygonEnabled) public onlyRole(CONFIGURATOR_ROLE) {
        polygonEnabled = _polygonEnabled;
    }
}
