{{
  "language": "Solidity",
  "sources": {
    "/contracts/TheZeneMarketplace.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity =0.8.13;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "@openzeppelin/contracts/utils/cryptography/draft-EIP712.sol";
import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/token/ERC1155/utils/ERC1155Holder.sol";
import "@openzeppelin/contracts/interfaces/IERC2981.sol";
import "./TheZeneNFT.sol";
import "./metatx/EIP712MetaTransaction.sol";

/**
 * @title Zene Marketplace Contract
 * @author Kuldeep K Srivastava
 * @notice Users able to sell and buy NFTs. NFT Creators can create tokens off-chain which will be minted when bought by a buyer.
 * @dev Buy and Sell features are using lazy minting technique.
 *      This contract is using EIP-712 for hashing and signing type structured data.
 *      This is also RBAC enabled using Access Control.
 *
 *      Visit https://docs.openzeppelin.com/contracts/4.x/api/utils#EIP712 for more details
 */
contract TheZeneMarketplace is
    ERC1155Holder,
    AccessControl,
    Pausable,
    EIP712,
    ReentrancyGuard,
    EIP712MetaTransaction
{
    using EnumerableSet for EnumerableSet.UintSet;
    /**
     * @notice This data structure ('voucher') contains all the informations necessary to reconstruct the current state of a sale
     * @dev The last parameter ('signature') is generated by NFT Creators following the EIP-712 (Typed Structured data hashing and signing)
     */
    struct NFTVoucher {
        // Token ID generated using UUIDv4
        uint256 tokenId;
        // Price of the NFT
        uint256 price;
        // Token URI of the token
        string uri;
        // ERC20 Token in which payment to be made, 0x0 in case of MATIC
        address paymentToken;
        // NFT creator & Royalty receiver
        address creator;
        // Royalty percentage in basis points
        uint64 royaltyPercentage;
        // NFT Creator's signature
        bytes signature;
        bytes validatorSignature;
    }

    /**
     * @notice This data structure ('listing') contains all the informations necessary to sell an NFT in secondary sales
     */
    struct Listing {
        // Address of the seller
        address seller;
        // Price of the NFT
        uint256 price;
    }

    bytes32 public constant ADMIN_ROLE = keccak256("ADMIN_ROLE");
    bytes32 public constant VALIDATOR_ROLE = keccak256("VALIDATOR_ROLE");
    string private constant SIGNING_DOMAIN = "TheZeneMarketplace";
    string private constant SIGNATURE_VERSION = "4";
    // Platform fee percent, uint256(100) == 1%
    uint16 private platformFeePercent;
    // vendor share (in %) for platform fee in every primary sale
    uint16 private vendorPrimarySalePlatformFeePercent;
    // vendor share (in %) for platform fee in every secondary sale
    uint16 private vendorSecondarySalePlatformFeePercent;
    // zene platform fee share withdrawal address
    address private platformFeeWithdrawalAddress;
    // vendor platform fee share withdrawal address
    address private vendorPlatformFeeWithdrawalAddress;
    // Zene NFT Contract
    TheZeneNFT public nftContract;
    // Map hashing of signatures in voucher to bool. This is to invalidate more than once use of vouchers.
    mapping(bytes => bool) private voucherRedeemed;
    // Map between tokenIds to Listing data listed for secondary sales
    mapping(uint256 => Listing) private listings;
    // Listed Token Ids set
    EnumerableSet.UintSet private listedTokenIds;

    // Events
    event DirectBuy(
        uint256 indexed tokenId,
        address indexed seller,
        address indexed buyer,
        uint256 price
    );
    event DirectBuyUsingLazyMint(
        uint256 indexed tokenId,
        address indexed seller,
        address indexed buyer,
        uint256 price,
        NFTVoucher voucher
    );
    event AdminUpdatedPlatformFeePercent(
        address admin,
        uint16 newPlatformFeePercent
    );
    event AdminUpdatedVendorPlatformFeePercent(
        address admin,
        uint16 newPlatformFeePercent
    );
    event AdminUpdatedPlatformFeeWithdrawalAddress(
        address admin,
        address newPlatformFeeWithdrawalAddress
    );
    event AdminUpdatedVendorPlatformFeeWithdrawalAddress(
        address admin,
        address newVendorPlatformFeeWithdrawalAddress
    );
    event AddedListing(
        uint256 indexed tokenId,
        address indexed seller,
        uint256 price
    );
    event RemovedListing(uint256 indexed tokenId, address indexed seller);

    function _tokenIsNotListed(uint256 tokenId) private view {
        require(!listedTokenIds.contains(tokenId), "Token already listed");
    }

    modifier tokenIsNotListed(uint256 tokenId) {
        _tokenIsNotListed(tokenId);
        _;
    }

    function _tokenIsListed(uint256 tokenId) private view {
        require(listedTokenIds.contains(tokenId), "Token not listed");
    }

    modifier tokenIsListed(uint256 tokenId) {
        _tokenIsListed(tokenId);
        _;
    }

    constructor(
        address _admin,
        address _validator,
        address _nftContract,
        uint16 _platformFeePercent,
        uint16 _vendorPrimarySalePlatformFeePercent,
        uint16 _vendorSecondarySalePlatformFeePercent,
        address _vendorPlatformFeeWithdrawalAddress
    )
        EIP712(SIGNING_DOMAIN, SIGNATURE_VERSION)
        EIP712MetaTransaction("TheZeneMarketplaceMetaTx", "1")
    {
        nftContract = TheZeneNFT(_nftContract);
        platformFeePercent = _platformFeePercent;
        platformFeeWithdrawalAddress = _admin;
        vendorPrimarySalePlatformFeePercent = _vendorPrimarySalePlatformFeePercent;
        vendorSecondarySalePlatformFeePercent = _vendorSecondarySalePlatformFeePercent;
        vendorPlatformFeeWithdrawalAddress = _vendorPlatformFeeWithdrawalAddress;
        // set _admin with ADMIN role
        _setupRole(ADMIN_ROLE, _admin);
        _setupRole(VALIDATOR_ROLE, _validator);
        // WARNING: The every role is also its own admin: it has permission to
        // grant and revoke this role. Extra precautions should be taken to secure
        // accounts that have been granted it.
        _setRoleAdmin(ADMIN_ROLE, ADMIN_ROLE);
        _setRoleAdmin(VALIDATOR_ROLE, ADMIN_ROLE);
    }

    /**
     * @dev See {IERC165-supportsInterface}.
     * @param interfaceId the interfaceId you want to know if that contracts supports (in bytes4 format)
     * @return bool true/false statement if the Zene Marketplace smart-contract supports a given interface
     * (e.g. IERC1155Receiver,IAccessControl or any of their respective parent classes)
     */
    function supportsInterface(bytes4 interfaceId)
        public
        view
        override(ERC1155Receiver, AccessControl)
        returns (bool)
    {
        return
            ERC1155Receiver.supportsInterface(interfaceId) ||
            AccessControl.supportsInterface(interfaceId);
    }

    /**
     * @notice To let admin pause contracts methods in case of emergency
     *
     * WARNING: This method is to be used only for emergency case.
     */
    function pause() external onlyRole(ADMIN_ROLE) {
        _pause();
    }

    /**
     * @notice To let admin unpause contracts methods
     *
     * WARNING: This method is to be used only for emergency case.
     */
    function unpause() external onlyRole(ADMIN_ROLE) {
        _unpause();
    }

    /**
     * @notice To let admin transfer all listed NFTs to their seller. This can be called only when contract is paused.
     * @return true if operation is successful
     *
     * WARNING: This method is to be used only for emergency case.
     */
    function removeListingAll()
        external
        onlyRole(ADMIN_ROLE)
        whenPaused
        returns (bool)
    {
        for (uint256 i = getTotalNumberOfListedNFTs(); i >= 1; i--) {
            uint256 tokenId = listedTokenIds.at(i - 1);
            address seller = listings[tokenId].seller;
            listedTokenIds.remove(tokenId);
            delete listings[tokenId];
            nftContract.safeTransferFrom(
                address(this),
                seller,
                tokenId,
                1,
                "0x"
            );
        }
        return true;
    }


    /**
     * @notice To let admin transfer listed NFTs to their seller
     * @param tokenIds list of token ids to be removed from listing
     * @return true if operation is successful
     *
     * WARNING: This method is to be used only for emergency case.
     */
    function removeListingByIds(uint256[] memory tokenIds)
        external
        onlyRole(ADMIN_ROLE)
        returns (bool)
    {
        uint256 len = tokenIds.length;

        for (uint256 i = 0; i < len; i++) {
            if (listedTokenIds.contains(tokenIds[i])) {
                uint256 tokenId = tokenIds[i];
                address seller = listings[tokenId].seller;

                listedTokenIds.remove(tokenId);
                delete listings[tokenId];
                nftContract.safeTransferFrom(
                    address(this),
                    seller,
                    tokenId,
                    1,
                    "0x"
                );
            }
        }
        return true;
    }

    /**
     * @notice To let admin update platform fee percent (uint256(100) == 1%)
     * @param newPlatformFeePercent the new platform fee percent.
     */
    function setPlatformFeePercent(uint16 newPlatformFeePercent)
        external
        onlyRole(ADMIN_ROLE)
    {
        require(newPlatformFeePercent <= 10000, "Platform Fee Percent > 100%");
        platformFeePercent = newPlatformFeePercent;
        emit AdminUpdatedPlatformFeePercent(msgSender(), newPlatformFeePercent);
    }

    /**
     * @notice To get the platform fee set in percentage. Values is set as basis points.
     */
    function getPlatformFeePercent() external view returns (uint16) {
        return platformFeePercent;
    }

    /**
     * @notice To let admin update vendor primary sale platform fee share in percent (uint16(100) == 1%)
     * @param newVendorPrimarySalePlatformFeePercent the new vendor platform fee share in percent.
     */
    function setVendorPrimarySalePlatformFeePercent(
        uint16 newVendorPrimarySalePlatformFeePercent
    ) external onlyRole(ADMIN_ROLE) {
        require(
            newVendorPrimarySalePlatformFeePercent <= 10000,
            "Platform Fee Percent > 100%"
        );
        vendorPrimarySalePlatformFeePercent = newVendorPrimarySalePlatformFeePercent;
        emit AdminUpdatedVendorPlatformFeePercent(
            msgSender(),
            newVendorPrimarySalePlatformFeePercent
        );
    }

    /**
     * @notice To get the vendor primary sale platform fee share set in percentage. Values is set as basis points.
     */
    function getVendorPrimarySalePlatformFeePercent()
        external
        view
        returns (uint16)
    {
        return vendorPrimarySalePlatformFeePercent;
    }

    /**
     * @notice To let admin update vendor secondary sale platform fee share in percent (uint16(100) == 1%)
     * @param newVendorSecondarySalePlatformFeePercent the new vendor platform fee share in percent.
     */
    function setVendorSecondarySalePlatformFeePercent(
        uint16 newVendorSecondarySalePlatformFeePercent
    ) external onlyRole(ADMIN_ROLE) {
        require(
            newVendorSecondarySalePlatformFeePercent <= 10000,
            "Platform Fee Percent > 100%"
        );
        vendorSecondarySalePlatformFeePercent = newVendorSecondarySalePlatformFeePercent;
        emit AdminUpdatedVendorPlatformFeePercent(
            msgSender(),
            newVendorSecondarySalePlatformFeePercent
        );
    }

    /**
     * @notice To get the vendor secondary sale platform fee share set in percentage. Values is set as basis points.
     */
    function getVendorSecondarySalePlatformFeePercent()
        external
        view
        returns (uint16)
    {
        return vendorSecondarySalePlatformFeePercent;
    }

    /**
     * @notice To let admin update platform fee withdrawal address
     * @param newPlatformFeeWithdrawalAddress the new platform fee withdrawal address
     */
    function setPlatformFeeWithdrawalAddress(
        address newPlatformFeeWithdrawalAddress
    ) external onlyRole(ADMIN_ROLE) {
        require(
            newPlatformFeeWithdrawalAddress != address(0),
            "Address 0x0 not allowed"
        );
        platformFeeWithdrawalAddress = newPlatformFeeWithdrawalAddress;
        emit AdminUpdatedPlatformFeeWithdrawalAddress(
            msgSender(),
            newPlatformFeeWithdrawalAddress
        );
    }

    /**
     * @notice To get the platform fee withdrawal address
     */
    function getPlatformFeeWithdrawalAddress() external view returns (address) {
        return platformFeeWithdrawalAddress;
    }

    /**
     * @notice To let admin update vendor platform fee withdrawal address
     * @param newVendorPlatformFeeWithdrawalAddress the new vendor platform fee withdrawal address
     */
    function setVendorPlatformFeeWithdrawalAddress(
        address newVendorPlatformFeeWithdrawalAddress
    ) external onlyRole(ADMIN_ROLE) {
        require(
            newVendorPlatformFeeWithdrawalAddress != address(0),
            "Address 0x0 not allowed"
        );
        vendorPlatformFeeWithdrawalAddress = newVendorPlatformFeeWithdrawalAddress;
        emit AdminUpdatedVendorPlatformFeeWithdrawalAddress(
            msgSender(),
            newVendorPlatformFeeWithdrawalAddress
        );
    }

    /**
     * @notice To get the vendor platform fee withdrawal address
     */
    function getVendorPlatformFeeWithdrawalAddress()
        external
        view
        returns (address)
    {
        return vendorPlatformFeeWithdrawalAddress;
    }

    /**
     * @notice To let user to list their NFTs for secondary sales
     * @param tokenId Token Id to be listed.
     * @param price Price to be set for the listed NFT.
     */
    function addListing(uint256 tokenId, uint256 price)
        public
        tokenIsNotListed(tokenId)
        whenNotPaused
    {
        require(
            nftContract.balanceOf(msgSender(), tokenId) == 1,
            "User does not have NFT"
        );
        require(
            nftContract.isApprovedForAll(msgSender(), address(this)),
            "Not given approval to transfer"
        );
        require(price > 0, "Set price should be more than 0");

        // transfer the NFT from msgSender() to this contract
        nftContract.safeTransferFrom(
            msgSender(),
            address(this),
            tokenId,
            1,
            ""
        );
        // add the listing
        Listing storage list = listings[tokenId];
        list.seller = msgSender();
        list.price = price;
        // add the token id
        listedTokenIds.add(tokenId);

        emit AddedListing(tokenId, msgSender(), price);
    }

    /**
     * @notice To let user to remove their NFTs for secondary sales
     * @param tokenId Token Id to be removed from listing.
     */
    function removeListing(uint256 tokenId)
        public
        tokenIsListed(tokenId)
        whenNotPaused
    {
        require(
            listings[tokenId].seller == msgSender(),
            "Only NFT Owner allowed to remove"
        );
        listedTokenIds.remove(tokenId);
        delete listings[tokenId];

        nftContract.safeTransferFrom(
            address(this),
            msgSender(),
            tokenId,
            1,
            "0x"
        );

        emit RemovedListing(tokenId, msgSender());
    }

    /**
     * @notice To get the total number of NFTs listed
     * @return total number of listed NFTs
     */
    function getTotalNumberOfListedNFTs() public view returns (uint256) {
        return listedTokenIds.length();
    }

    /**
     * @notice To get list of all listed NFTs token ids.
     * @param limit maximum no. of token Ids to be returned
     * @param pageNumber page number for pagination
     * @return list of token ids
     */
    function getListedNFTs(uint256 limit, uint256 pageNumber)
        public
        view
        returns (uint256[] memory)
    {
        require(limit >= 10, "Limit too low");
        require(pageNumber >= 1, "Page number should be +ve number");

        uint256 len = getTotalNumberOfListedNFTs();
        uint256 start = limit * (pageNumber - 1);
        uint256 end = (limit * pageNumber) - 1;
        require(len >= start, "Page size exceeded");

        uint256 tokensSize = len >= end
            ? limit
            : len - (limit * (pageNumber - 1));
        uint256[] memory tokenIds = new uint256[](tokensSize);

        uint8 tokenIterator = 0;
        for (uint256 i = start; i <= end && i < len; i++) {
            tokenIds[tokenIterator] = listedTokenIds.at(i);
            tokenIterator++;
        }
        return tokenIds;
    }

    /**
     * @notice To get details (seller, price) of the listed NFT
     * @return a tuple of (seller, price)
     */
    function getListedNFTDetails(uint256 tokenId)
        public
        view
        tokenIsListed(tokenId)
        returns (Listing memory)
    {
        return listings[tokenId];
    }

    /**
     * @notice To get price of the listed NFT
     * @return price of the listed NFT
     */
    function getListedNFTPrice(uint256 tokenId)
        public
        view
        tokenIsListed(tokenId)
        returns (uint256)
    {
        return listings[tokenId].price;
    }

    /**
     * @notice To get seller of the listed NFT
     * @return seller of the listed NFT
     */
    function getListedNFTSeller(uint256 tokenId)
        public
        view
        tokenIsListed(tokenId)
        returns (address)
    {
        return listings[tokenId].seller;
    }

    /**
     * @notice To get list of all listed NFTs token ids by a seller
     * @return list of token ids
     */
    function getListedNFTsBySeller(address seller)
        public
        view
        returns (uint256[] memory)
    {
        uint256 len = getTotalNumberOfListedNFTs();
        uint256 count;

        for (uint256 i = 0; i < len; i++) {
            if (listings[listedTokenIds.at(i)].seller == seller) {
                count++;
            }
        }
        uint256[] memory tokenIds = new uint256[](count);

        uint256 index = 0;
        for (uint256 i = 0; i < len; i++) {
            uint256 id = listedTokenIds.at(i);
            if (listings[id].seller == seller) {
                tokenIds[index++] = id;
            }
        }

        return tokenIds;
    }

    /**
     * @notice To let users buy an NFT from secondary sales with MATIC
     * @param receiver Address which will receive the NFT
     * @param tokenId NFT token Id for the sale
     * @return Returns the NFT Token Id
     */
    function directBuy(address receiver, uint256 tokenId)
        external
        payable
        tokenIsListed(tokenId)
        whenNotPaused
        nonReentrant
        returns (uint256)
    {
        require(
            nftContract.supportsInterface(type(IERC2981).interfaceId),
            "ERC2981 Incompatible"
        );
        uint256 price = listings[tokenId].price;
        require(msg.value >= price, "Insufficient MATIC to purchase");
        address seller = listings[tokenId].seller;

        // calculate and transfer royalties
        uint256 royaltyAmount = _calculateAndTransferRoyalties(tokenId, price);
        // calculate and transfer platform fees, vendor fees
        _calculateAndTransferFees(false, seller, price, royaltyAmount);

        // update the listing
        listedTokenIds.remove(tokenId);
        delete listings[tokenId];

        // transfer NFT to receiver
        nftContract.safeTransferFrom(address(this), receiver, tokenId, 1, "0x");

        emit DirectBuy(tokenId, seller, receiver, price);
        return tokenId;
    }

    /**
     * @notice To let users buy an NFT if they provide valid vouchers using lazy mint
     * @param receiver Address which will receive the minted NFT
     * @param voucher NFT Voucher containing all the information that receiver wants to buy.
     * @return Return the Token ID of the minted NFT
     */
    function directBuyUsingLazyMint(
        address receiver,
        NFTVoucher calldata voucher
    ) external payable nonReentrant whenNotPaused returns (uint256) {
        uint256 price = voucher.price;
        require(price > 0, "Set price should be more than 0");
        require(msg.value >= price, "Insufficient MATIC to purchase");
        require(
            !voucherRedeemed[voucher.signature],
            "Voucher already redeemed"
        );
        // verifying signer and getting signer address
        address signer = verify(voucher);
        // invalidating voucher
        voucherRedeemed[voucher.signature] = true;

        // calculate and transfer platform fees, vendor fees, gas fees
        _calculateAndTransferFees(true, signer, price, 0);

        // mint NFT to signer and then transfer to receiver
        // minting to signer first is to prove provenance on-chain
        require(
            nftContract.mintAndTransferNFT(
                signer,
                receiver,
                voucher.tokenId,
                voucher.uri,
                voucher.creator,
                voucher.royaltyPercentage
            ),
            "Minting and Transfer NFT failed"
        );

        emit DirectBuyUsingLazyMint(
            voucher.tokenId,
            signer,
            receiver,
            price,
            voucher
        );

        return voucher.tokenId;
    }

    /**
     * @dev internal _calculateAndTransferFees method; used only post-checks
     * @param primarySale To check if fees transferred for primary sale or secondary sales
     * @param seller NFT Creator or NFT Owner address
     * @param price NFT price set for sale
     */
    function _calculateAndTransferFees(
        bool primarySale,
        address seller,
        uint256 price,
        uint256 royaltyAmount
    ) internal {
        // calculating platform fee
        uint256 platformFee = (price / 10000) * platformFeePercent;

        // calculating vendor platform fee share
        uint256 vendorPlatformFeePercent = primarySale
            ? vendorPrimarySalePlatformFeePercent
            : vendorSecondarySalePlatformFeePercent;
        uint256 vendorSaleFee = (platformFee / 10000) *
            vendorPlatformFeePercent;

        // calculating price seller will get
        uint256 sellingPrice = price - platformFee - royaltyAmount;

        // calculating amount zene will get as platform fee
        platformFee = platformFee - vendorSaleFee;

        // sending extra matic back to buyer
        (bool status, ) = msgSender().call{value: msg.value - price}("");
        require(status, "Transfer to receiver failed!");

        // transferring to seller
        (status, ) = seller.call{value: sellingPrice}("");
        require(status, "Transfer to seller failed!");

        // transferring platform fee to zene
        (status, ) = platformFeeWithdrawalAddress.call{value: platformFee}("");
        require(status, "Transfer to zene failed!");

        // transferring vendor sale platform fee share to vendor
        (status, ) = vendorPlatformFeeWithdrawalAddress.call{
            value: vendorSaleFee
        }("");
        require(status, "Transfer to vendor failed!");
    }

    /**
     * @dev internal _calculateAndTransferRoyalties method; used only post-checks
     * @param tokenId NFT token Id for which to calculate and transfer royalties
     * @param price NFT price set for sale
     */
    function _calculateAndTransferRoyalties(uint256 tokenId, uint256 price)
        internal
        returns (uint256)
    {
        (address royaltyReceiver, uint256 royaltyAmount) = nftContract
            .royaltyInfo(tokenId, price);
        if (royaltyReceiver != address(0) && royaltyAmount != 0) {
            // transferring royalties amount to royalty receiver
            (bool status, ) = royaltyReceiver.call{value: royaltyAmount}("");
            require(status, "Transfer of royalty failed!");
        }

        return royaltyAmount;
    }

    /**
     * @notice Returns a hash of the given NFTVoucher, prepared using EIP712 typed data hashing rules.
     * @dev This function is used by 'verify()' in order to retrieve the address of the voucher's signer.
     * @param voucher An NFTVoucher to hash.
     * @return the hash of the NFTVoucher in bytes32 format.
     */
    function _hash(NFTVoucher calldata voucher)
        internal
        view
        returns (bytes32)
    {
        return
            _hashTypedDataV4(
                keccak256(
                    abi.encode(
                        keccak256(
                            "NFTVoucher(uint256 tokenId,uint256 price,string uri,address paymentToken,address creator,uint64 royaltyPercentage)"
                        ),
                        voucher.tokenId,
                        voucher.price,
                        keccak256(bytes(voucher.uri)),
                        voucher.paymentToken,
                        voucher.creator,
                        voucher.royaltyPercentage
                    )
                )
            );
    }

    /**
     * @notice Verifies the signature for a given NFTVoucher, returning the address of the signer. Signature is based on the EIP712 standard.
     * @dev Will revert if the signature is invalid. Does not verify that the signer is authorized to mint NFTs.
     * @param voucher An NFTVoucher describing the sale buyer wants to buy.
     * @return the address of the user account which signed the NFT.
     */
    function verify(NFTVoucher calldata voucher) public view returns (address) {
        bytes32 digest = _hash(voucher);
        address signer = ECDSA.recover(digest, voucher.signature);
        require(signer == voucher.creator, "Invalid creator signature");
        address validator = ECDSA.recover(digest, voucher.validatorSignature);
        require(hasRole(VALIDATOR_ROLE, validator) == true, "Invalid validator signature");
        return signer;
    }

    /**
     * @notice fallback function; always reverts (prevents funds from being sent here inadvertently)
     * @dev doesn't prevents an external smart-contract to send funds through a self-destruct mechanism
     */
    fallback() external payable {
        revert("fallback: reverts");
    }

    /**
     * @notice receive function; always reverts (prevents funds from being sent here inadvertently)
     */
    receive() external payable {
        revert("receive matic: reverts");
    }
}
"
    },
    "/contracts/metatx/lib/EIP712Base.sol": {
      "content": "//SPDX-License-Identifier: MIT
pragma solidity =0.8.13;

contract EIP712Base {
    struct EIP712Domain {
        string name;
        string version;
        address verifyingContract;
        bytes32 salt;
    }

    bytes32 internal constant EIP712_DOMAIN_TYPEHASH =
        keccak256(
            bytes(
                "EIP712Domain(string name,string version,address verifyingContract,bytes32 salt)"
            )
        );

    bytes32 internal domainSeparator;

    constructor(string memory name, string memory version) {
        domainSeparator = keccak256(
            abi.encode(
                EIP712_DOMAIN_TYPEHASH,
                keccak256(bytes(name)),
                keccak256(bytes(version)),
                address(this),
                bytes32(getChainID())
            )
        );
    }

    function getChainID() public view returns (uint256 id) {
        assembly {
            id := chainid()
        }
    }

    function getDomainSeparator() private view returns (bytes32) {
        return domainSeparator;
